--
-- 表的结构 `academic_work_summary`
--

CREATE TABLE IF NOT EXISTS `academic_work_summary` (
  `NewsID` int(11) NOT NULL,
  `Book` varchar(255) DEFAULT NULL,
  `Author` varchar(255) DEFAULT NULL,
  `Publish` varchar(255) DEFAULT NULL,
  `Time` varchar(255) DEFAULT NULL,
  `Content` text,
  `BrowseNumber` int(11) DEFAULT '0',
  `Picture` varchar(200) DEFAULT NULL,
  `Level` int(11) DEFAULT '0'
) ENGINE=MyISAM AUTO_INCREMENT=17 DEFAULT CHARSET=utf8;

--
-- 转存表中的数据 `academic_work_summary`
--

INSERT INTO `academic_work_summary` (`NewsID`, `Book`, `Author`, `Publish`, `Time`, `Content`, `BrowseNumber`, `Picture`, `Level`) VALUES
(1, '大运河两岸的历史记忆——楚州、高邮考古报告集', '南京博物院 编著', '科学出版社', '2010-5', '本书是南水北调东线工程江苏段一期控制性项目考古发掘成果的汇总。南水北调东线江苏段一期控制性项目主要集中在运河沿岸的历史文化名城楚州和高邮。各发掘报告的内容有史前化石地点、唐宋元明清时代的遗址和墓地，对研究里运河一带的居民迁徙、贸易发展、文化交流、市井生活和社会风俗提供了新的材料，是大运河两岸的历史印记。\r\n本书可供考古工作者，高等院校考古专业、遗产保护专业师生，对中国大运河特殊线形文化遗产研究、中国漕运史的专家学者阅读、参考。 \r\n', 94, 'C:\\xampp\\htdocs\\chinese_canal_images\\academic_work\\大运河两岸的历史记忆——楚州、高邮考古报告集.jpg', 0),
(2, '中国大运河史', '《中国大运河史》编篡委员会 ', '中华书局', '2001-9', '	本书搜集和运用系统的中国大运河史料，以大运河经济带为重心，按时间顺序，分门别类地就运河流经地域的政治、经济、军事、文化、水利以及社会风俗等加以全面描述，深刻揭示敢中国大运河数千年中对人类社会进步所起的巨大作用。史著提出了自己独到的见解：大运河兴衰的时代使命论。并且展示了大运河未来可持续发展的方向。此外，本书在史意义上，不仅开拓了大运河史研究的新领域，而且创新了编撰运河史的新体例。全书征引丰富，描述细腻，文笔流畅，并配有插图30余幅，对大中学校师生、水利航运人员以及研究工作都有一定的阅读参考价值，是一部较好的爱国主义教育读物。', 97, 'C:\\xampp\\htdocs\\chinese_canal_images\\academic_work\\中国大运河史.jpg', 0),
(3, '实现整体保护与可持续利用的大运河遗产廊道构建概念.途径与设想', '奚雪松', '电子工业出版社', '2012-12', '本书旨在从理论和方法上解决大运河这样一类自然与文化资产类型多样、数量众多的跨区域型遗产保护与可持续利用问题。强调通过大运河遗产廊道的构建对沿运河区域丰富多样的自然与文化资产进行整体化保护，实现大运河区域遗产保护、生态环境保护、文化身份认同、教育游憩机会增加、经济社会良性发展等多重目标。以期为当前中国正在开展的大运河遗产保护规划编制和申请世界遗产行动提供理论与方法的借鉴，并为今后古蜀道、茶马古道等中国五千年文化蕴涵丰富的跨区域型遗产保护与可持续利用提供参考。', 120, 'C:\\xampp\\htdocs\\chinese_canal_images\\academic_work\\实现整体保护与可持续利用的大运河遗产廊道构建概念.途径与设想.jpg', 1),
(4, '景观设计学—大运河保护与再生', '北京大学景观设计学研究院', '中国林业出版社', '2012-6', '《景观设计学(第23辑):大运河保护与再生》讲述京杭大运河在当代的存在状态。通过全程实际考察京杭大运河并收集相关图像和文字，实测100多个不同的河道断面来解剖定格于2004年的运河生存状态，我们制定了未来研究京杭大运河的一个基本物理参照。同时，收录汇总了沿运文化遗产点，并部分考察验证。我们发现，京杭大运河这样一条对国土生态安全和民族文化认同具有关键意义的遗产与生态廊道，目前面临着严重的威胁，如不尽快统一规划、保护、管理和建设，必将成为难以挽回的遗憾。', 93, 'C:\\xampp\\htdocs\\chinese_canal_images\\academic_work\\景观设计学—大运河保护与再生.jpg', 0),
(5, '京杭大运河国家遗产与生态廊道', '俞孔坚，李迪华，李海龙，张蕾 等\r\n', '北京大学出版社', '2012-4\r\n', '这本《京杭大运河国家遗产与生态廊道(附光盘)(精)》由俞孔坚等人著，本书以丰富的田野调查记录，以及对京杭大运河历史、自然地理特征和遗产资源的深入整理成果，为这一项举世瞩目的文化遗产保护工程提供了直接依据和建设性的方案，是对中国文化遗产研究和保护的重要贡献，也是世界文化遗产研究的成功案例。', 106, 'C:\\xampp\\htdocs\\chinese_canal_images\\academic_work\\京杭大运河国家遗产与生态廊道.jpg', 0),
(6, '京杭大运河沿线生态环境变迁', '张金池，毛锋，林杰等著', '科学出版社', '2012-3', '本书是《空间信息技术与文化遗产保护丛书》之一。本书内容是“十一五”国家科技支撑计划课题“空间信息技术在大遗址保护中的应用研究（以京杭大运河为例）”（课题编号：2006BAK30B01）的研究成果。本书介绍运河沿线1794km的古生态环境和生态演变规律，包括水系、气候、土壤、生物多样性、湿地、水土保持、森林植被等3000多年的生态环境变迁过程，分析了京杭大运河沿线1794km的生态环境现状，填补了京杭大运河沿线生态环境演变研究的空白。\r\n本书可供生态环境研究人员、文化遗产保护与研究人员、区域和城市规划人员参考，也可为高等院校生态环境、文化遗产保护、城乡规划等专业的师生阅读使用。\r\n', 103, 'C:\\xampp\\htdocs\\chinese_canal_images\\academic_work\\京杭大运河沿线生态环境变迁.jpg', 0),
(7, '中国脐带：大运河城市群叙事', '刘士林，耿波，李正爱 等著', '辽宁人民出版社', '2008-1', '大运河是世界上开凿最早、规模最大、里程取最长的运河。从时间上讲，自公元前486年吴王夫差开凿邗沟起始，中经历代王朝不断的疏浚与整修，至清代末年的漕粮改折为终点，以古运河、隋唐大运河、京杭大运河为主要形态的大运河在中国大地上先后驰骋奔流了2500年之久；从空间上看，无论是以洛阳为中心，北起北京南至杭州，贯通海、黄、淮、江、浙五大水系全长2700余公里的隋唐大运河，还是元代经过截弯取直之后形成的纵横北京、天津、河北、山东、江苏、浙江六省市全长1794公里的京杭大运河，其规模与里程在世界上都是绝无仅有的。如此巨大的时间跨度与空间规模，使大运河与万里长城一起成为中华文明最伟大的标志性工程。但两者的现代命运却是很不相同的，当巍巍万里长城以中华文明的雄伟标志被世界逐渐接受的时候，同样对中华文明作出巨大历史贡献的大运河，却如韩愈所说“骈死于槽枥之间”，在20世纪日益式微，甚至在人们的记忆中走向了彻底的遗忘。值得庆幸的是，在21世纪曙色初开的时候，借助于世界文化遗产申报与保护的时代风云，在现实中已严重边缘化、在大众记忆中早已沉寂的大运河开始于深渊般的沉默中滚动起激荡四海的风雷声，使人们重新注意到它辉煌的历史与充满生机的未来。关于大运河，人们似乎突然发现，有太多的时间、地点、人物、活动、历史、记忆、话语与愿望需要寻觅、钩沉、捕捞和重建。所谓十年河东，十年河西，由于一些偶然的原因，我们也被卷入到这个人员越来越多、声势越来越浩大、节奏越来越快速的人流、思潮与社会进程中。', 101, 'C:\\xampp\\htdocs\\chinese_canal_images\\academic_work\\中国脐带：大运河城市群叙事.jpg', 0),
(8, '中国大运河遗产构成及价值评估', '谭徐明　等著', '水利水电出版社', '2012-12', '大运河沟通中国东部平原海河、黄河、淮河、长江、太湖、钱塘江，甬江是以都城为目的地的，以粮食运输为主的水利工程。大运河是实证两千多年中国历史进程的标志性工程。本书基于大运河兴建与运用的自然与社会背景，对大运河历史沿革、运河与大江大河关系进行厂系统的梳理。依据运河与江河关系，分河段研究了运河遗产构成，阐释了蕴含其中的自然与文化价值。为读者提供了从水利学、历史学认知大运河的不同视角。\r\n本书适用于从事工程类遗产保护、水利专业人员，以及对工程史、历史、地理、考古等有兴趣的人群\r\n', 83, 'C:\\xampp\\htdocs\\chinese_canal_images\\academic_work\\中国大运河遗产构成及价值评估.jpg', 0),
(9, '中国大运河文化遗产保护技术基础', '谭徐明、朱云枫、张念强、王英华、唐剑波、李云鹏、王力、万金红、马建明、邓俊、刘建刚', '科学出版社', '2013-7', '本书是《空间信息技术与文化遗产保护丛书》之一。本书内容是基于国家科技支撑计划课题“空间信息技术在大遗址保护中的应用研究（以京杭大运河为例）”（编号：2006BAK30B01）的研究成果。内容包括运河遗产价值认知、遗产构成、保护与管理策略、古代闸坝复原、运河遗产保护工程图例设计、数据采集技术。附录部分收录了经过注释的二十五史运河篇（元明清部分）。本书选择大运河山东德州段保护规划作为案例，以阐释运河遗产评价和保护技术的应用。\r\n本书适用于从事工程类遗产保护、规划编制、水利专业人员，以及工程史、历史、地理、考古等领域的科研人员阅读使用。\r\n', 84, 'C:\\xampp\\htdocs\\chinese_canal_images\\academic_work\\中国大运河文化遗产保护技术基础.jpg', 0),
(11, '明清山东运河区域社会变迁 ', '王云 ', '人民出版社', '2006-6', '《明清山东运河区域社会变迁》在继承前人研究成果并掌握丰富的文献资料和社会调查资料的基础上，运用历史学、历史地理学、社会学、民俗学等多种学科的理论和综合研究、个案研究、比较研究相结合的方法。对明清时期极具典型意义的山东运河区域社会变迁的历史动因，社会变迁的起伏过程和社会变迁的历史趋势及特点，进行了较为全面深入的考察和研究。认为明清山东运河区域社会变迁的动力主要是交通环境改善、漕运政策和对其他区域多种文化的吸纳融合，指出运河沿岸城镇与运河腹地社会发展的不平衡性，并总结出该地区在社会变革过程中所表现的大起大落类似马鞍型的发展趋势及经验教训', 107, 'C:\\xampp\\htdocs\\chinese_canal_images\\academic_work\\明清山东运河区域社会变迁 .jpg', 0),
(12, '京杭大运河的历史与未来', '董文虎　等著', '社会科学文献出版社', '2008-2', '京杭大运河流经京、津、冀、鲁、苏、浙二市四省，沟通海河、黄河、淮河、长江、钱塘江五大河流，是一条贯通我国南北水运的大动脉。它自开凿以来经历了2500多年的沧桑变迁，迄今仍奔流在华夏大地上，依然发挥着航运、水利、南水北调、生态保护等功能，作为“活着的、流动着的人类遗产”，堪称中华文明的瑰宝、世界级古代水运工程。本书以深厚悠久的历史底蕴，多角度地向读者述说大运河的过去与现在，畅想更加美好的未来。 ', 94, 'C:\\xampp\\htdocs\\chinese_canal_images\\academic_work\\京杭大运河的历史与未来.jpg', 0),
(13, '杭州运河史', ' 孙忠焕 ', '中国社会科学出版社', '2011-6', '由孙忠焕主编的《杭州运河史》对京杭大运河(杭州段)的历史演变及其社会、经济、生态等进行了系统、完整的梳理，是国内迄今为止最全面系统阐述杭州运河历史的著作。本书共9章计30余万字，绪论重点论述了杭州运河的由来、对城市的影响及杭州运河的地位，结语叙述了新中国成立后杭州运河的变迁。 ', 97, 'C:\\xampp\\htdocs\\chinese_canal_images\\academic_work\\杭州运河史.jpg', 0),
(14, '中国运河文化史', '安作璋', '山东教育出版社', '2006-6', '这是一部大运河变迁史，从早期运河一直说到民国时期的运河。全书按时间的不同，共分六编四十九章，涉及早期、隋唐时期、宋元时期、明代、清代和民国等时期的运河文化以及当时运河为沿岸所带来的诸方面的变动。', 230, 'C:\\xampp\\htdocs\\chinese_canal_images\\academic_work\\中国运河文化史.jpg', 0),
(15, '杭州运河历史研究', '陈述 ', '杭州出版社', '2006-4', '《杭州运河丛书》分为《杭州运河历史研究》、《杭州运河文献》(上下册)、《杭州运河风俗》、《京杭大运河图说》、《杭州运河古诗词选评》、《杭州运河桥船码头》、《杭州运河遗韵》共八本，从多方面、多角度充分展示了杭州大运河的丰富历史文化内涵，提示大运河对杭州经济、社会、文化和城市发展的意义，为研究、保护、治理和开发杭州大运河，提供了一份具有参考、借鉴价值的珍贵资料。本书为其中的一本。全书分上下两编，上编为论著部分，叙述大运河的形成和历史、功能和地位、运河与杭州城市发展、运河整治历史与管理制度、运河文化风貌等，下编为译著部分。', 101, 'C:\\xampp\\htdocs\\chinese_canal_images\\academic_work\\杭州运河历史研究.jpg', 0),
(16, '中国运河开发史', '陈桥驿', '中华书局', '2008-9', '《中国运河开发史》是由我国著名历史地理学家陈桥驿先生领衔主编，邹逸麟、王守春、朱士光等著名学者分别担任相关部分的编撰。本书详尽论述了我国各个历史时期主要运河的起源、发展、演变、现状和历史作用，全书分八篇，分别论述了黄河以北运河、山东运河、里运河、关中豫东与皖北皖中、江南运河、杭州段运河、浙东运河、灵渠等各段运河，是目前关于运河研究的集大成之作。', 93, 'C:\\xampp\\htdocs\\chinese_canal_images\\academic_work\\中国运河开发史.jpg', 0);

-- --------------------------------------------------------

