CREATE TABLE IF NOT EXISTS `academic_xuezhe` (
  `NewsID` int(11) NOT NULL,
  `Name` varchar(255) DEFAULT NULL,
  `Content` text,
  `Picture` varchar(255) DEFAULT NULL,
  `BrowseNumber` int(11) DEFAULT '0',
  `Level` int(11) DEFAULT '0'
) ENGINE=MyISAM AUTO_INCREMENT=49 DEFAULT CHARSET=utf8;

--
-- 转存表中的数据 `academic_xuezhe`
--

INSERT INTO `academic_xuezhe` (`NewsID`, `Name`, `Content`, `Picture`, `BrowseNumber`, `Level`) VALUES
(26, '史念海', '史念海（1912-2001），历史地理学家，山西平陆人，1948年后，历任西北大学、西安师范学院、陕西师范大学教授、历史系主任，陕西师范大学历史地理研究所及唐史研究所所长、副校长，陕西省历史学会第一届会长，民进中央委员、陕西省委主任委员。1956年获全国先进生产者称号。是第五、六届全国政协委员。着有《中国的运河》、《河山集》（一、二、三集），与顾颉刚合著《中国疆域沿革史》。', 'C:\\xampp\\htdocs\\chinese_canal_images\\canal_people_images\\史念海.jpg', 768, 0),
(27, '傅崇兰', '傅崇兰，男，1940年12月23日生，山东东平人，中国著名城市规划学家，1964年8月毕业于曲阜师范学院，1964年8月进入中国社会科学院工作，历任中国社科院城市发展与环境研究中心主任、中国社会科学院研究生院城市发展系主任、中国社科院社会学系教授、中国城市规划学会委员、理事、北京市专家顾问团顾问等。主要学术专长是城市史、城市规划、建设，现从事城市规划、区域规划、人口预测研究。著有《中国运河城市发展史》 。', 'C:\\xampp\\htdocs\\chinese_canal_images\\canal_people_images\\傅崇兰.jpg', 610, 0),
(28, '安作璋', '安作璋，1927年生于山东省曹县。1951年毕业于齐鲁大学文学院历史系。山东师范大学、山东大学教授、博士生导师。历任山东师范大学古籍整理研究所所长、中国秦汉史研究会副会长、山东历史学会副会长和山东地方志学会副会长等职务。他在秦汉史、中国古代史、历史文献学、山东地方史，齐鲁文化史、中国运河文化史等领域均有独特贡献。安作璋主编的《中国运河文化史》(山东教育出版社2001年)是一部研究中国运河文化的通史之作.', 'C:\\xampp\\htdocs\\chinese_canal_images\\canal_people_images\\安作璋.jpg', 687, 0),
(29, '刘士林', '刘士林（1966-）,河北曲阳人，博士，上海交通大学城市科学研究院院长、首席专家，媒体与设计学院副院长，教授，博导，上海高校都市文化E-研究院特聘研究员，主要从事美学、文化学、城市科学研究。兼任教育部《中国都市化进程年度报告》负责人、社会科学文献出版社《城市群蓝皮书》主编、《中国城市科学》集刊主编，中国行政体制改革研究会行政文化委员会理事、中华美学学会理事、上海市民俗文化学会副会长等。主持国家社科基金项目、教育部重大项目、国家发改委、上海市政府及国内省市委托项目多项。先后获第12届中国图书奖、教育部第三届中国高校人文社会科学研究优秀成果奖、上海市曙光学教育部新世纪优秀人才支持计划等奖励与荣誉称号。', 'C:\\xampp\\htdocs\\chinese_canal_images\\canal_people_images\\刘士林.jpg', 590, 0),
(30, '王云', '王云(1958-),女，山东阳谷人，历史学博士、教授、硕士生导师。研究方向为中国古代社会史、明清经济史。自1982年大学毕业后从事中国古代史、中国社会史的教学和研究工作。1994年晋升为副教授，1996年任历史系总支副书记兼副主任，1997年破格晋升为教授，同时被评为中青年学术骨干和学科带头人培养对象。1998年起任聊城大学图书馆馆长，1999年被评为校级第二层次“跨世纪人才”。2001年考入南京大学历史系攻读“明清经济史”专业博士学位。2004年7月毕业并获得博士学位。主要学术成果：《明清山东运河社会变迁》，人民出版社。2006年6月出版。《山东运河文化研究》（合著），齐鲁书社，2006年11月出版', 'C:\\xampp\\htdocs\\chinese_canal_images\\canal_people_images\\王云.jpg', 580, 1),
(31, '姚汉源', '姚汉源(1913-2009) 山东巨野人。中国水利水电科学研究院教授。\r\n1937年毕业于清华大学土木工程系。先后任清华大学助教，重庆大学助教，四川大学讲师。1949年任江西南昌农业专科学校校务委员会主任、教授兼水利科主任。1953～1963年任武汉水利电力学院教授兼副教务长。1963年起任北京水利水电学院教授兼副院长。1982年起任水利水电科学研究院教授级高工。1982～1990年任中国水利学会水利史研究会会长。曾先后参与《中国农业百科全书》、《中国大百科全书》及《中国水利百科全书》的撰写，约50余万字。著有《京杭运河史》(120万字，1998年出版)等。\r\n', 'C:\\xampp\\htdocs\\chinese_canal_images\\canal_people_images\\姚汉源.jpg', 578, 0),
(32, '王健', '王健　男、籍贯安徽泾县，1959年8月生于南京市，华东师范大学历史系中国古代史专业，博士。现任江苏省社会科学院历史研究所副所长、研究员。中国先秦史学会理事、江苏省郑和研究会理事兼副秘书长、《郑和研究》主编，南京历史学会副会长。研究方向：中国古代先秦史、历史文献、历史地理。近期重点研究：先秦史、区域历史文化（吴文化、江苏历史文化）、大运河航运史等。', 'C:\\xampp\\htdocs\\chinese_canal_images\\canal_people_images\\王健.jpg', 630, 0),
(33, '鲍彦邦', '鲍彦邦（1937-2010），广东珠海市人，1962年毕业于武汉大学历史系本科，1965年毕业于中山大学历史系硕士，曾在中山大学历史系、广东省委理论组等单位工作。1978年以后，一直在暨南大学历史系工作，1996年晋升教授，长期承担中国古代史本科教学任务，并负责指导中国社会经济史硕士研究生一，参与筹建中国古代史博士点。早年师从我国社会经济史学派的创始者之一梁方仲先生，潜心研究明清社会经济史，后致力于明代漕运和广东社会经济史研究，其出版的学术专著《明代漕运研究》，曾获广东省人文社科优秀成果奖，在海内外学术界产生较大影响。', 'C:\\xampp\\htdocs\\chinese_canal_images\\canal_people_images\\鲍彦邦.jpg', 703, 0),
(34, '李治亭', '李治亭：汉族，1942年1月13日生，山东莒南人。1965年辽宁大学历史系毕业。曾任辽宁大学历史研究所副所长、所长等职。主要研究方向为：清史兼及明史。主要作品：《吴三桂大传》、《中国漕运史》、《清康乾盛世》等。', 'C:\\xampp\\htdocs\\chinese_canal_images\\canal_people_images\\李治亭.jpg', 711, 0),
(35, '朱光亚', '东南大学建筑学院教授、教育部城市与建筑遗产保护实验室学术委员会副主任、国家文物局大运河遗产保护规划专家委员会成员、大运河遗产保护规划江苏省段负责人 。主要从事建筑遗产保护及相关理论、技术体系的研究，包括中国建筑遗产资源评估；传统建筑工艺抢救；传统建筑结构与构造机制，历史地带、名胜风景及遗址保护中的规划、设计及再利用研究。', 'C:\\xampp\\htdocs\\chinese_canal_images\\canal_people_images\\朱光亚.jpg', 632, 0),
(36, '吴琦', '吴琦，　男，1962年9月生，江西吉安人。1984年本科毕业于江西师范大学历史系，1987、1997年于华中师范大学历史文化学院分别获历史学硕士、博士学位。现为华中师范大学历史文化学院教授，中国古代史学科带头人，国家精品课程“中国古代史”负责人，明清史及中国古代社会史的博士生导师，中国古代史博士、硕士培养点负责人。主要研究成果：先后承担了“漕运与中国社会”、“清代漕粮征派与地方社会秩序”、“群体与社会变迁——多学科视野下的前近代社会群体研究”、“《荆楚全书》编纂（史部）”等教育部人文社科基金项目、国家社科基金项目、中央高校基本科研业务费专项资金项目、教育部重大攻关项目等的研究。出版了《漕运与中国社会》（华中师范大学出版社）、《漕运•群体•社会——明清史论集》（湖北人民出版社）、《明清社会群体研究》（中国社会科学出版社）、《明清地方力量与地方社会》（中国社会科学出版社）等多部著作.', 'C:\\xampp\\htdocs\\chinese_canal_images\\canal_people_images\\吴琦.jpg', 647, 0),
(37, '吴欣', '吴欣，女，山东陵县人，1972年7月生，博士、教授、硕士生导师，山东大学历史文化学院博士后，研究方向为中国古代社会史、运河文化史、法律史。1996年大学毕业后，在聊城师范学院历史系从事中国古代史、中国古代社会史的教学和研究工作。1999年考入南开大学攻读硕士学位，2002年继续在南开大学师从常建华教授攻读博士学位， 2005年7月毕业并获得博士学位。2006年晋升为副教授，2007年5月被聘为聊城大学历史文化学院硕士研究生导师。', 'C:\\xampp\\htdocs\\chinese_canal_images\\canal_people_images\\吴欣.jpg', 587, 0),
(38, '谭徐明', '谭徐明，女，汉族，四川成都人，教授级高工。现为水利史研究所所长，博士生导师，主要方向为水利史、灾害与减灾。\r\n谭徐明教授长期从事水利史与水灾害方面的科研工作，在灌溉、防洪、运河技术史、流域水环境演变、灾害与减灾管理研究方向具有突出成绩，在水景观规划、水利遗产保护等方法研究与实践方面具有专长。多次负责参与流域水环境演变、洪水风险管理、水利史等方面的国家攻关项目、国家自然科学基金项目、国际合作项目、国内重大研究项目以及国家社会公益专题项目、京杭运河保护方法与规划编制等专项科研项目等。近些年来，主持和参与各种课题研究30余项，包括国家科技支撑、科技攻关项目、国家自然科学基金项目及948国际先进技术引进项目。在科研方面水利史研究中以古代水利典籍河工档案的校注与整编，都江堰、京杭运河、海塘工程等典型古代水利工程技术史及其科学价值，以及江河防洪史、区域灌溉史方面有多年的研究积累。由于水利工程与水利史的知识背景和科研实践，近些年在灾害规律研究、多学科宏观综合研究方面多有开创性拓展。有《都江堰史》、《中国灌溉与防洪史》、《人为的灾害》等专著或译著。至2011年以前主持再研的科研项目主要有：水利部项目“在用古代水利工程与水利遗产保护规划”（至2011年止）；国家文物局委托项目“大运河关键工程与遗产特点”（至2009年12月止）\r\n\r\n', 'C:\\xampp\\htdocs\\chinese_canal_images\\canal_people_images\\谭徐明.jpg', 700, 0),
(39, '俞孔坚', '俞孔坚，1963年出生于浙江金华 教授，哈佛大学设计学博士,主持创办了北京大学景观设计学研究院和北京大学建筑与景观设计学院。获得多项国际重要奖项，曾9度获得美国景观设计师协会颁发的年度设计奖，其中2次获得年度杰出奖；3次蝉联世界建筑节全球最佳景观奖；3个设计获国际建筑奖发表《京杭大运河的完全价值观》《发生学视角下的大运河遗产廊道构成》《论大运河区域生态基础设施战略和实施途径》等文,著有《京杭大运河国家遗产与生态廊道》。', 'C:\\xampp\\htdocs\\chinese_canal_images\\canal_people_images\\俞孔坚.jpg', 566, 0),
(40, '阮仪三', '1934年11月出生，苏州市人。1956年考入同济大学，1961年毕业留校。现任建设部同济大学国家历史文化名城研究中心主任，同济大学建筑城规学院教授、博士生导师；中国历史文化名城保护专家委员会委员。上个世纪80年代以来，努力促成平遥、周庄、丽江等众多古城古镇的保护，因而享有“古城卫士”、“古城保护神”等美誉。曾获联合国教科文组织遗产保护委员会颁发的2003年亚太地区文化遗产保护杰出成就奖。主要著作有《护城纪实》《护城踪录》《江南古镇》《历史文化名城保护理论与规划》等。', 'C:\\xampp\\htdocs\\chinese_canal_images\\canal_people_images\\阮仪三.jpg', 741, 0),
(41, '齐欣', '毕业于中国政法大学。承担多项国家与专项相关课题研究，研究方向为文化遗产传播与公众参与体系。现为人民日报海外版主任编辑、硕士生导师、特邀研究员。自1985年开始参加工作，齐欣就在人民日报海外版，至今没有换过部门。曾获全国好新闻特别奖、中国记协全国首届现场短新闻奖、第三届全国法制好新闻奖。文化遗产传播理论的研究者和实践推广者。齐欣率先在国内提出“文化遗产传播”的理念和定义，并且正在努力搭建文化遗产传播的理论架构。齐欣是“遗产小道”这一公众体验方式的创建者，并由此成为文化遗产体验线路的规划者。撰写了《18城市力推大运河“申遗”》《大运河流过2006》《2006：活着的大运河》《大运河申遗：如何影响我们》《文化遗产保护将进入公众传播时代》 ，为大运河申遗作出了重要贡献。', 'C:\\xampp\\htdocs\\chinese_canal_images\\canal_people_images\\齐欣.jpg', 685, 0),
(42, '罗哲文', '罗哲文（公元1924—2012年），四川宜宾人，中共党员，中国古建筑学家，国家文物局古建筑专家组组长，原中国文物研究所所长，中国人民政治协商会议第六、七、八届全国委员会委员。一直从事中国古代建筑的维修保护和调查研究工作。2012年5月14日在北京逝世，享年88岁。\r\n作为一个从事长城保护与研究的工作者，85岁高龄的罗哲文一直密切关心着大运河的荣誉和命运。他曾说“长城与大运河这两项古代最伟大的工程，都开始修筑、开凿于2000多年前的春秋战国时期；是多民族国家统一与发展的必然结果，见证着中国古代历史上这一阶段发展兴衰的历程，随着近年来对文化与自然遗产保护意识的不断加强，关心大运河、保护大运河，已经成为社会共识。在专家学者提议和全国政协委员提案的有力推动下，一个大运河保护与申遗的热潮已经形成。2006年5月，国务院已公布大运河为全国重点文物保护单位。随后，国家文物局也把它列入申报世界遗产的预备名单。现在社会各界都为大运河贡献才智，很受鼓舞。大运河的兴衰与历史地位，大运河的辉煌未来，尤其是大运河申遗过程中所呈现的中国特色，这些都是我们现在和今后应该逐步梳理并加以详细研究的工作。通过我们的深入研究，将能够为大运河的前途和世界遗产保护的理论与形式都找到更好的发展之路。”\r\n作为“运河三老”之一，2006年全国“两会”上，罗哲文等58位政协委员提交了关于大运河保护和申遗的提案。而大运河申遗，也是他最后的牵挂。此后，国家文物局启动了大运河保护与申报世界遗产的工作。\r\n', 'C:\\xampp\\htdocs\\chinese_canal_images\\canal_people_images\\罗哲文.jpg', 610, 0),
(43, '郑孝燮', '郑孝燮，1916年2月2日出生于沈阳，国家历史文化名城保护专家委员会副主任，城市规划专家，设置中国历史文化名城主要倡议人之一。\r\n郑孝燮，今年91岁了，他曾参与过平遥古城、卢沟桥的保护，与罗哲文一起花了21年的时间，为它争取申报世界遗产的资格。为此他们多次到大运河去考察，郑孝燮曾说“政治上、经济上、文化上，运河都是生命线啊。”曾在采访中谈到了对运河申遗的看法“大运河申报世界遗产这项工作已经由全国政协，前不久在大会上由58位委员联名做一个提案正式提出来了。明天开始，从北京到杭州全过程的考察就要开始，这次考察活动我觉得意义非常重大。我们保护并申报大运河为世界遗产，其重要意义跟我们申报“万里长城”意义是同等重要的。它不只是人类的奇迹、世界的奇迹。也是对人类文明的一个巨大贡献，世界没有一条运河比我们的京杭大运河建得更早，没有一条运河比我们京杭大运河建得长，因而它是世界的一个奇迹。我们也不能不说，这些年来在保护方面虽然取得了一些成绩，但是也有很多问题需要我们在今后，在申报过程中下大力气把它解决掉。一个主要问题是生态环境。运河的水质问题，这是最主要问题。其次，就是运河沿途历史文化遗产要进一步按照世界文化遗产的标准来加强调查、加强检查、加强保护和整治。”\r\n', 'C:\\xampp\\htdocs\\chinese_canal_images\\canal_people_images\\郑孝燮.jpg', 618, 0),
(44, '朱炳仁', '朱炳仁，1944年11月生，研究员职称，清同治绍兴“朱府铜艺”（被国家内贸部认定为“中华老字号”）四代传人，被中国文联及中国民协命名为中国民间文化杰出传承人（已公示），铜雕技艺国家级非物质文化遗产唯一传承人（非遗名录902号）。朱炳仁被誉为“中国当代铜建筑之父”。 现为中国工艺美术大师，和著名熔铜艺术家，西泠印社社员。\r\n朱炳仁与88岁的古文物专家罗哲文、91岁的古建筑专家郑孝燮先生，怀着对祖国传统文化遗产的深厚感情共同提出了运河保护与申遗的创意。得到全国政协58位政协委员的提案支持，催生了全国政协对大运河的保护和申遗的考察，从而激发了全国人民对祖国文化遗产的强烈责任心，在2006年与2007年朱炳仁随全国政协考察团二次考察大运河全程，总结研究运河保护经验，尤其是不遗余力推广宣传杭州市在运河综合保护上取得的成就.。他还提出了“运河品质保护”、“零保护”等新理念，在全国政协的专家论坛上，产生了强烈的反响，同时他还亲自策划并创办了“京杭大运河”网站，成为目前国内唯一一个点击率最高，影响最大的大运河专业性网站。成为了人们尊敬的“运河三老”之一。\r\n2013年朱炳仁曾接受《中国文化报•大运河专刊》采访，对联合申遗和“零保护”做了详细的解说。\r\n', 'C:\\xampp\\htdocs\\chinese_canal_images\\canal_people_images\\朱炳仁.jpg', 697, 0),
(45, '徐匡迪', '徐匡迪（1937年12月－ ），中国钢铁冶金专家，浙江桐乡崇德人；北京钢铁工业学院冶金系毕业，中国工程院院士。1983年6月加入中国共产党，是中共第十四届中央候补委员，第十五、十六届中央委员。曾任第十届全国政协副主席，中国工程院院长，上海市市长等职。现任中国经济工业联合会会长，上海大学教授、博士生导师。2010年6月7日在中国工程院第十次院士大会正式卸任工程院院长一职。\r\n徐匡迪多次考察运河申遗情况，曾在会议中指出，大运河是中华民族文明进化中的一个光辉篇章。保护好大运河，就是保护好中华民族共有的精神家园。运河文化是经历了千百年的历史积淀而成的，有着深厚的底蕴和深刻的内涵。如果再不加强保护，大运河的历史文化遗存、风光景物和自然生态环境就会不可避免地遭到破坏，运河文化的真实性和完整性就会不复存在，这将是中华民族不可挽回的巨大损失。要以对历史负责、对民族负责的态度继承和发展运河文化，坚决防止将运河文化简单化、粗糙化、功利化、商品化的倾向，坚决防止任何形式的建设性破坏。徐匡迪强调，大运河的保护涉及众多领域，要综合利用相关学科参与大运河的保护和研究工作。对大运河及其沿线的文物资源和非物质文化遗产开展科学全面的调查，认真做好历史研究、考古、野外勘查、测绘、文献档案、汇总分析报告等基础性工作。要重视高科技手段在考古、勘查中的运用，提高大运河基础研究的效率。要切实组织多学科的合作，从历史、文物、民俗、水利、建筑、旅游、环保等不同领域的视角进一步挖掘、研究、宣传大运河的文化遗产价值，探索推动大运河保护和申遗的新方法、新思路。\r\n', 'C:\\xampp\\htdocs\\chinese_canal_images\\canal_people_images\\徐匡迪.jpg', 691, 0),
(46, '冯骥才', '冯骥才，1942年出生于天津，祖籍浙江宁波慈溪县（今宁波市江北区慈城镇），当代著名作家、文学家、艺术家，民间艺术工作者，民间文艺家，画家。早年在天津从事绘画工作，后专职从事文学创作和民间文化研究，大力推动民间文化保护宣传工作。他创作了大量优秀散文、小说和绘画作品，有多篇文章入选中小学、大学课本，如散文《珍珠鸟》。他曾经担任天津市文联主席、国际笔会中国中心会员。现任中国文学艺术界联合会执行副主席，中国小说学会会长，中国民间文艺家协会主席，国际民间艺术组织（IOV）副主席，中国民主促进会中央副主席，全国政协常委等职。是“文革”后崛起的“伤痕文学运动”代表作家，一九八五年后以“文化反思小说”对文坛产生深远影响。\r\n    冯骥才还兼任《民间文化》刊名誉总编辑，他满怀热情京杭大运河“申遗”鼓与呼，积极承担起京杭大运河的保护和申遗工作。一是在《民间文化》杂志开辟专题栏目，对大运河申遗进行全方位、立体化的跟踪报道。二是开展京杭大运河“万众一心”特别策划的倡导行动。三是与政府全面配合，从社会民间力量来组织和协调运河两岸相关部门，建立相应的组织机构开展工作。四是组织专家进行实地考察进行科学论证，制定审慎周密的实施方案。五是动员社会上有识之士，牵手民间资本运作融汇到京杭大运河申遗中来。六是支持和帮助运河两岸人民做好保护物质文化和非物质文化的遗产的宣传工作。七是协助政府做好加快京杭大运河遗产保护和“申遗”的各项准备工作。为申遗提供了坚实的基础。\r\n', 'C:\\xampp\\htdocs\\chinese_canal_images\\canal_people_images\\冯骥才.jpg', 585, 0),
(47, '刘绍棠', '刘绍棠，中国著名乡土文学作家，“荷花淀派”的代表作家之一，“大运河乡土文学体系”创立者。13岁时就开始发表作品，加入作协时是当时最年轻的作协会员。受作家孙犁和肖洛霍夫的影响，走上乡土文学之路，作品题材多以京东运河(北运河)一带农村生活为题材，格调清新淳朴，乡土色彩浓郁。他的作品曾多次获国内奖项并受国家嘉奖；又有多部作品被翻译成外文，在国际上产生了积极影响。\r\n1950年春，因被诗人晏明称为“神童”，“神童作家”称号逐渐流传并被大众认可， 又因家乡儒林村临近北运河，又被称为“大运河之子”。青少年时代作品主要有短篇小说集《青枝绿叶》和长篇小说《运河的桨声》等。这一时期的作品清新、淡雅，把土改后的农村写得嫩苗遍野，生机盎然，受孙犁作品深刻影响，有社会主义“田园牧歌”之美誉。后遭波折，益增其华，出版长篇小说12部：《地火》、《春草》等，中篇小说《蒲柳人家》《小荷才露尖尖角》等四十余部。另有《刘绍棠文集•大运河乡土文学体系》(10卷)出版。\r\n　运河两岸的风土民情，陶冶了绍棠先生宽广的胸襟。“我的生身之地，京东大运河……”他总是以这样的称谓开场叫板，“京东大运河”，仿佛那是一个很了不起的品牌。是的，在绍棠先生的心目中，他的生身之地京东大运河边的儒林村，就是一个价值连城、永不贬值的黄金品牌。他一生都在为这个品牌而不遗余力地做着赞美的广告。早在他13岁时，就开始奏响了自己生命的芦笛。他戴着故乡原野上盛开的《红花》，他走过运河上下人来人往的《摆渡口》，他骑着故乡高大健壮的《大青骡子》，就这样迎着新中国初升的朝阳，迈着矫健的步伐开始了他艰辛漫长的文学之旅。\r\n刘绍棠的每一部(篇)小说都是美文，从他的作品中，人们能明显地感到伟大祖国脉搏的跳动，感到祖国的美好，人民的善良。它们给人以鼓舞，给人以美的享受。刘绍棠的作品之所以深受国内外广大读者的欢迎，就是因为他在从事创作时，刻意追求美，把美溶化在作品里，产生良好的社会效果。\r\n', 'C:\\xampp\\htdocs\\chinese_canal_images\\canal_people_images\\刘绍棠.jpg', 724, 0),
(48, '舒乙', '舒乙，1935年生于青岛，北京人，满族，中国著名文学家舒庆春（老舍）之子。曾4次调研京杭大运河，对运河从头到尾进行了考察，曾写过《京杭大运河，残缺的辉煌》、《隋唐大运河，地下的辉煌》、《江南运河，水乡的辉煌》等三篇考察实录。多次在各地演讲对大运河保护的意义，为大运河的申遗保护做了不懈努力。\r\n   舒乙对大运河有着浓浓的感情，曾在全国政协十届五次会议举行的集体采访中指出，大运河的现状，是两个“大”：意义之大和问题之大。两个“大”。意义之大，一说就明白，它的知名度特别大，历史上的作用与现代的作用特别大。但是这头大，问题也特别大，现状的复杂程度也特别大。并提出了保护运河的重要性和关键性。\r\n    在2009年春节前夕，舒乙所著的《疼爱与思考—一个政协委员的大运河四次考察亲历记》一书的研讨座谈会在全国政协专委楼第一会议室举行，据悉，该书是全国政协主席贾庆林同志亲自批示拨专款，于2008年10月由中国文史出版社出版。书中提到了很多对运河申遗保护的观点，这些保护运河的建议，是舒乙对于重塑大运河“昨日辉煌”的一种寄托与探索，也是他在保护大运河过程中那“一串串脚印”中的一部分。他的建议得到了国务院和相关部门的肯定，并直接推动了由国家权威部门主导的“大运河保护与申报世界文化遗产”专项工作组的成立。他也成为专家顾问组的重要成员之一。\r\n虽然已经年逾古稀，舒乙对于新的“任务”依旧充满期待。在他眼里，大运河如同一位坚强的劳动女性，奔放而美丽，吸引着他继续为之“陶醉”着。舒乙期盼申遗成功后的大运河，将重现往日的辉煌。\r\n', 'C:\\xampp\\htdocs\\chinese_canal_images\\canal_people_images\\舒乙.jpg', 701, 0);

