CREATE TABLE IF NOT EXISTS `classic_market` (
  `ID` int(11) NOT NULL,
  `Author` varchar(255) DEFAULT NULL,
  `Title` varchar(255) DEFAULT NULL,
  `Content` text,
  `BrowseNumber` int(11) DEFAULT '0',
  `PubTime` datetime DEFAULT NULL,
  `ImgPath` varchar(255) DEFAULT NULL,
  `WorksPath` text,
  `InsertTime` int(11) NOT NULL,
  `Abstract` text
) ENGINE=MyISAM AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;

--
-- 转存表中的数据 `classic_market`
--

INSERT INTO `classic_market` (`ID`, `Author`, `Title`, `Content`, `BrowseNumber`, `PubTime`, `ImgPath`, `WorksPath`, `InsertTime`, `Abstract`) VALUES
(1, '钱丽媛', '骨子里流淌着运河脉动的血液', '宜兴紫砂工艺，运河城市一奇葩。它始于北宋，盛于明清，繁荣于当今。集书画、诗文、篆刻、雕塑于一体，成为独步世界的艺术品。在这块灿烂锦绣的五色之土上，造就了紫砂壶艺之美，也造就了高级工艺美术师钱丽媛。\r\n钱丽媛1958年进宜兴紫砂工艺厂，先后跟随“七大老艺人”之中的“紫砂泰斗”顾景舟、蒋蓉、王寅春学艺，深得大师厚爱，并得到众大师真传，还在工艺美术大师汪寅仙身边研习半年。所制紫砂壶不仅深得几位艺术大师的教诲，更形成自己的风格，以其制壶功力和艺术含量享誉壶界。作品多次被邀参加港澳名壶精品展，并在国内外展评中多次获得大奖。\r\n值得一提的是，1979年钱丽媛应河南省宝丰县政府邀请，领衔创办宝丰紫砂工艺厂。10年困苦，玉汝于成；10年耕耘，先后培养200多位紫砂艺人，作品远销20多个国家和地区，被河南省政府评为“女技术能手”和“三八红旗手”。1989年，经顾景舟介绍，钱丽媛与知名艺术家韩美林结识，合作达两年之久，而两人合作的《美林提梁壶》，壶身丰盈饱满，两枝竹竿从壶体上部伸出，搭接成提梁，具有强烈的艺术个性，亦分外雅致清新。韩美林以美学观念设计，钱丽媛以高深的技艺制作，可谓相得益彰，堪称紫砂陶艺的佳话。\r\n半个多世纪的探索和追求，钱丽媛在紫砂艺术领域中注入了新的艺术元素，闯出了自己独特的紫砂艺术之路。钱丽媛做的壶，古风新韵，扑面而来。她讲究精巧的结构，精湛的技艺和精良的功能。同时又十分注意整体艺术效果，寻求典雅的意趣。她特地为山东省设计制作的《荷花仙子壶》，高达1000毫米，直径600毫米左右，共两件，其中一件在展览期间赠送给山东省政府，作为全运会的纪念收藏。《荷花仙子壶》将荷花、荷叶、荷梗的自然姿色描绘得生动、灵秀。尤其是俯卧在壶盖上的“荷花仙子”双手托腮，神态可掬，充溢着稚雅天真的艺术形象。\r\n长期受教于顾景舟、蒋蓉、王寅春以及汪寅仙4位大师的钱丽媛，传统的理念早已根植于创作的思维中，但钱丽媛并不固守传统，退休后，她仍在不断寻找突破。专利产品“丽媛牌“磁化紫砂壶、磁化紫砂杯便是紫砂艺苑中的一朵奇葩，是钱丽媛创作上的一个突破，在国际上获得了3项发明金奖。她在运河畔的南禅寺设专营店出售此壶时，以其神奇的疗效被众多治愈者誉称为“丽媛神壶”、“丽媛神杯”。特别是“丽媛牌”磁化紫砂容器饮水机投放市场后，受到广泛关注与好评。可谓掬一捧运河之水，冲一杯淡淡香茶，岂不畅已。\r\n做壶如做人，作品如人品。年已古稀的钱丽媛做了一辈子的壶，却始终保持着一颗平常心，执着于做壶的乐趣，执着和淡泊使得她的作品也因此而变得更纯粹、更无暇。钱丽媛骨子里流淌着的是紫砂血液，紫砂壶是她的生命、她的魂，她把自己的那分执着、那分情愫，注入五色土，用悟性和灵性编织绚丽的紫砂艺术世界。钱丽媛对紫砂艺术发自内心的领悟，更是刻骨铭心的记忆，她所制之壶，流淌着运河脉动的魅力。\r\n', 315, '2014-07-02 15:03:47', 'Home/Images/dgmuseum/市场/八方和顺.jpg', '', 0, '宜兴紫砂工艺，运河城市一奇葩。它始于北宋，盛于明清，繁荣于当今。集书画、诗文、篆刻、雕塑于一体，成为独步世界的艺术品。'),
(2, '钱丽媛', '八方和顺', '八方器型是紫砂方器中较为难见的一种器型，现在多见的八方器型多为不规则的八方，八方壶造型方中藏圆，线面挺括平正，轮廓线条分明，给予人们干净利落，明快挺秀之感。历来有很多出色的方器造型。亦达到方器造型规则要求：“线条流畅，轮廓分明，平稳庄重”，以直线、横线为主，曲线、细线为辅，器型的中轴线、平衡线要正确、匀挺、富于变化。方器除口、盖、的、把、嘴应与壶体相对称外，还要求做到“方中寓圆，方中求变，口盖划一，刚柔相称”。', 0, NULL, 'Home/Images/dgmuseum/市场/八方和顺.jpg', NULL, 0, NULL),
(3, '钱丽媛', '荊溪提梁', '壶呈六方提梁，神兽伏于壶盖，龙首琢成壶嘴，寄寓祥瑞之意。壶身刻上荆溪春光，勾画了宜兴的青山丽水，迷人景色，流露了作者对“荆溪山水甲江南”的赞美与自豪。', 0, NULL, 'Home/Images/dgmuseum/市场/荊溪提梁.jpg', NULL, 0, NULL),
(4, '钱丽媛', '梅桩壶', '梅桩壶紫砂泥胎，呈深栗色。壶身、流、把、盖全部是用极富生态的残梅桩、树皮及缠枝组成，作品是一件强而有力的雕塑，壶上的梅花是用堆花手法，将有色的泥浆堆积塑造成型，栩栩如生。梅桩壶高10.8厘米，宽14.6厘米。', 0, NULL, 'Home/Images/dgmuseum/市场/梅桩壶.jpg', NULL, 0, NULL);

