  `BrowseNumber` int(11) DEFAULT '0',
  `InsertTime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `ImgPath` varchar(255) DEFAULT NULL
) ENGINE=MyISAM AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;

