--

INSERT INTO `heritage_institution` (`ID`, `Title`, `Content`, `BrowseNumber`, `InsertTime`, `ImgPath`) VALUES
(1, '中国文化遗产研究院', '中国文化遗产研究院是国家文物局直属的文化遗产保护科学技术研究机构。其前身可追溯至成立于1935年的“旧都文物整理委员会”；1949年更名为“北京文物整理委员会”，是新中国第一个由中央政府主办并管理的文物保护专业机构；1973年更名为“文物保护科学技术研究所”；1990年与文化部古文献研究室合并为中国文物研究所；2007年8月更名为中国文化遗产研究院。\r\n    根据国家文物局的要求，中国文化遗产研究院目前的主要职责是：开展国家文化遗产资源的调查、登录工作；承担国家水下文化遗产保护相关工作；承担文化遗产科学的基础研究、专项研究，开展文化遗产保护应用技术研究，推广科学技术研究成果；承担国家重要文化遗产保护规划编制、维修及展示方案设计；开展文化遗产保护科学技术的国际合作、学术交流和教育培训工作等。\r\n', 668, '2015-07-21 07:55:06', 'Home/Images/heritage/研究机构/中国文化遗产研究院.jpg'),
(2, '中国文物学会大运河专业委员会', '中国文物学会大运河专业委员会是由志愿保护中国大运河文化遗产的个人、群体和团体组成的专业性、学术性公益组织，为中国文物学会分支机构，接受中国文物学会的领导、业务指导和监督。其宗旨为：加强对中国大运河文化遗产的研究，动员各种社会力量积极参与对中国大运河文化遗产的保护工作，促进人类珍贵遗产价值的永续传承和合理利用。', 715, '2015-07-21 07:55:06', 'Home/Images/heritage/研究机构/中国文物学会大运河专业委员会.jpg'),
(3, '世界运河历史文化城市合作组织', '成立宗旨：以运河为纽带，促进运河城市间经济文化交流，共享发展经验，推动互利合作，促进运河城市共 同发展和繁荣。业务范围：文化交流、国际合作、咨询服务。成立的动议缘于中国扬州世界运河名城博览会，在运博会举办期间，参会的中国太平洋经济合作全国委员会、埃及伊斯梅利来省、意大利里米尼市、瑞典特罗尔海坦市等建议在运博会基础上，建立的一个便于运河城市沟通和交流的常设机构，弘扬运河文化，传承历史文明，促进永续发展，是世界运河城市在2008中国扬州世界运河名城博览会达成的共识，也是 “合作组织”的责任所在。', 762, '2015-07-21 07:55:06', 'Home/Images/heritage/研究机构/世界运河历史文化城市合作组织.jpg'),
(4, '中国文化传媒集团大运河文化研究院', '中国文化传媒集团大运河文化研究院2012年创立，隶属于中国文化传媒集团的文化研究机构。\r\n大运河文化研究院以运河城市文化项目研究、开发与管理为主导，充分发挥大运河文化研究院学术团队的创造力，探索运河城市在文化产业经济发展过程中的解决之道。促进研究院与运河城市政府、企业的广泛合作，扩大中国运河城市与世界运河城市的经济文化交流，将国家媒体的社会影响力、品牌整合力与民间资本、公共学术资源高度聚合、协同发展，以此服务于社会，造福于民生，贡献于国家。\r\n在运河城市历史、运河城市文化、运河城市产业经济、运河城市管理、运河城市企业品牌管理、运河城市战略规划六个领域，发挥大运河文化研究院在中国运河城市文化产业发展中的作用。\r\n', 667, '2015-07-21 07:55:06', 'Home/Images/heritage/研究机构/中国文化传媒集团大运河文化研究院.jpg');

-- --------------------------------------------------------

--
-- 表的结构 `heritage_media`
--

CREATE TABLE IF NOT EXISTS `heritage_media` (
  `ID` int(11) NOT NULL,
  `Title` varchar(255) DEFAULT NULL,
  `Content` text,
  `BrowseNumber` int(11) DEFAULT '0',
  `InsertTime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `ImgPath` varchar(255) DEFAULT NULL
) ENGINE=MyISAM AUTO_INCREMENT=6 DEFAULT CHARSET=utf8;

--
-- 转存表中的数据 `heritage_media`
--

INSERT INTO `heritage_media` (`ID`, `Title`, `Content`, `BrowseNumber`, `InsertTime`, `ImgPath`) VALUES
(1, '中国文化报', '《中国文化报》是中华人民共和国文化部主管的权威性文化艺术类报纸。《中国文化报》以继承、弘扬中华民族文化传统，繁荣、发展文化事业和文化产业，促进中外文化交流为宗旨，权威发布国家文化政策，快速报道全球文化动态，大力推介优秀文化作品，理性评说社会文化热点，客观反映和科学探究文化领域的管理之策、经营之道和消费之势，是文化管理者适读、文化经营者适读、文化消费者适读的报纸。', 568, '2015-07-21 07:56:55', 'Home/Images/heritage/主要媒体/中国文化报.jpg'),
(2, '中国文物报', '中国文物报社是国家文物局主管的文博考古全行业全国性权威媒体。《中国文物报》的前身，是河南省文物局于1985年8月创办的《文物报》。1987年10月1日，改由国家文物委员会主办，更名《中国文物报》。1990年 1月，改为国家文物局机关报。宣传国家文物法律、法规和文物工。\r\n承担编辑出版《中国文物报》、《文物天地》杂志、《中国文化遗产》杂志以及主办中国文物信息网的任务，承办国家文物局交办的有关文物保护、文物知识普及宣传活动及其他相关业务活动，同时有计划地编辑出版文物普及宣传及文博资料性系列图书和宣传材料，开展公益性的与文博事业有关的调研、传播、策划、咨询等活动。介绍中华民族丰富多彩的文物，进行爱国主义和革命传统教育；报道国内外考古新发现和文物维修、保护、研究的新成果；传播文物知识，开展学术讨论；交流国内外文物工作信息和经验。\r\n', 718, '2015-07-21 07:56:55', 'Home/Images/heritage/主要媒体/中国文物报.jpg'),
(3, '大运河文化专刊', '《中国文化报•大运河文化专刊》创刊于2009年。《大运河文化专刊》的问世，是历史使命的重托，文化理想的召唤。以国家的文化战略、文化政策为基调，客观理性地循着运河开凿、变迁的历史轨迹，探索大运河文化建设的现实路径。\r\n    尊重历史，保护遗存；顺应民意、造福民生；创建平台，云集智慧是《大运河文化专刊》的基本宗旨。寻遗追宗，确立民族文化精神，再现大运河作为“中华动脉、民族魂魄”的灵性之光，营建民众生活福祉。以开放的心胸为所有光大大运河文化的优秀成果创设展示自己的舞台。\r\n    在波澜壮阔、革故鼎新、蔚为壮观的当代中国文化建设大繁荣大发展的时代，《大运河文化专刊》将为推动地方文化建设，丰富民众文化生活，宣传中华文化精华，提升国家文化形象，为人类文化遗产流芳永续做出不懈的努力。\r\n', 721, '2015-07-21 07:56:55', 'Home/Images/heritage/主要媒体/文化专刊.png');

-- --------------------------------------------------------

--
-- 表的结构 `heritage_organization`
--

CREATE TABLE IF NOT EXISTS `heritage_organization` (
  `ID` int(11) NOT NULL,
  `Title` varchar(255) DEFAULT NULL,
  `Content` text,
  `ImgPath` varchar(255) DEFAULT NULL,
  `BrowseNumber` int(11) DEFAULT '0',
  `PubTime` datetime NOT NULL,
  `InsertTime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `Abstract` text
) ENGINE=MyISAM AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;

--
-- 转存表中的数据 `heritage_organization`
--

INSERT INTO `heritage_organization` (`ID`, `Title`, `Content`, `ImgPath`, `BrowseNumber`, `PubTime`, `InsertTime`, `Abstract`) VALUES
(1, '联合国教科文组织', '联合国教科文组织的全称为联合国教育、科学及文化组织。1945年11月联合国在英国伦敦会议上通过了教科文组织的组织法，1946年11月4日正式生效，同年12月教科文组织成为联合国专门机构，总部设在法国巴黎。截止2008年，成员有188个国家和地区。\r\n    我国是联合国创始国之一。1972年当选为执行局委员，并一直连任这一职务。1978年2月，中国联合国教科文组织全国委员会正式成立，是由教育部牵头领导的跨部门政府机构，具体负责中国与联合国教科文组织之间的合作事务。\r\n', 'Home/Images/heritage/联合国教科文组织.jpg', 781, '0000-00-00 00:00:00', '2015-07-21 06:38:03', NULL),
(2, '联合国教科文组织世界遗产委员会', '联合国教科文组织世界遗产委员会是政府间组织。为保证《保护世界文化和自然遗产公约》有效实施，1976年11月联合国教科文组织世界遗产委员会成立，负责公约的实施，并建立了《世界遗产名录》。世界遗产委员会每年召开一次会议，主要决定哪些遗产可以录入《世界遗产名录》，对已列入名录的世界遗产的保护工作进行监督指导。委员会由21名成员组成，每届任期6年，每两年改选成员的1/3。另外，由7名成员组成世界遗产委员会主席团，主席团每年举行两次会议，筹备委员会的工作。', 'Home/Images/heritage/联合国教科文组织世界遗产委员会.jpg', 754, '0000-00-00 00:00:00', '2015-07-21 06:38:03', NULL),
(3, '世界自然保护联盟', '世界自然保护联盟是在教科文组织的支持下于1948年成立，总部设在日内瓦。世界自然保护联盟是委员会在自然遗产事务方面的合作机构。主要有三方面职能：1.负责对所有自然遗产、双重遗产和文化景观类别的预备项目进行评估。2.负责就世界自然遗产项目的现状向委员会进行报告。3.负责开展有关培训工作并努力提高自然遗产保护和管理方面的积极性，特别是对拥有世界自然遗产的地区。', 'Home/Images/heritage/世界自然保护联盟.jpg', 645, '0000-00-00 00:00:00', '2015-07-21 06:38:03', NULL),
(4, '国际古迹遗址理事会', '国际古迹遗址理事会总部设在法国巴黎，是唯一从事文化遗产保护理论、方法、科学技术的研究、运用和推广工作的非政府国际组织。它主要负责对各国《世界遗产预备清单》中的文化遗产项目进行认定和评估；同时还和世界自然保护联盟联合评估其中的双重遗产项目。国际古迹遗址理事会拥有来自世界上100多个国家的6000多名来自建筑学、考古学、艺术史、规划学以及遗产保护和管理等学科领域的专家成员。', 'Home/Images/heritage/国际古迹遗址理事会.jpg', 559, '0000-00-00 00:00:00', '2015-07-21 06:38:03', '国际古迹遗址理事会总部设在法国巴黎，是唯一从事文化遗产保护理论、方法、科学技术的研究、运用和推广工作的非政府国际组织。它主要负责对各国《世界遗产预备清单》中的文化遗产项目进行认定和评估；同时还和世界自然保护联盟联合评估其中的双重遗产项目。');

-- --------------------------------------------------------

--
-- 表的结构 `heritage_project`
--

CREATE TABLE IF NOT EXISTS `heritage_project` (
  `ID` int(11) NOT NULL,
  `Address` varchar(255) DEFAULT NULL,
  `ProjTime` varchar(11) DEFAULT NULL,
  `Class` varchar(255) DEFAULT NULL,
  `Content` text,
  `ImgPath` varchar(255) DEFAULT NULL,
  `BrowseNumber` int(11) DEFAULT '0',
  `InsertTime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `Abstract` text
) ENGINE=MyISAM AUTO_INCREMENT=49 DEFAULT CHARSET=utf8;

--
-- 转存表中的数据 `heritage_project`
--

INSERT INTO `heritage_project` (`ID`, `Address`, `ProjTime`, `Class`, `Content`, `ImgPath`, `BrowseNumber`, `InsertTime`, `Abstract`) VALUES
(2, '周口店北京人遗址', '1987-12', '文化遗产', '周口店北京人遗址位于北京市房山区周口店龙骨山。因上世纪20年代出土了较为完整的北京猿人化石而闻名于世，1929年发现了第一件北京人头盖骨，从而为北京人的存在提供了坚实的基础，成为古人类研究史上的里程碑。到目前为止，出土的人类化石包括6件头盖骨、15件下颌骨、157枚牙齿及大量骨骼碎块，代表约40个北京猿人个体。为研究人类早期的生物学演化及早期文化的发展提供了实物依据。', 'Home/Images/heritage/中国世遗项目/周口店北京人遗址.jpg', 601, '2015-07-21 06:42:57', NULL),
(3, '长城', '1987-12', '文化遗产', '长城与中国大运河并称为中国古代的两项伟大工程。长城始建于2000多年前的春秋战国时期，秦朝统一中国之后连成万里长城，汉、明两代又曾大规模修筑。其工程之浩繁，气势之雄伟，堪称世界奇迹。2002年11月，中国唯一的水上长城辽宁九门口长城通过联合国教科文组织的验收，作为长城的一部分正式挂牌成为世界文化遗产。', 'Home/Images/heritage/中国世遗项目/长城.jpg', 648, '2015-07-21 06:42:57', NULL),
(4, '故宫', '1987-12', '文化遗产', '北京故宫又称紫禁城，1987年12月被公布为世界文化遗产，位于北京市区中心，为明、清两代的皇宫，有24位皇帝相继在此登基执政。始建于1406年，至今已近600年。故宫是世界上现存规模最大、最完整的古代木构建筑群，占地面积为72万平方米，建筑面积约15万平方米，拥有殿宇9000多间。2004年7月，沈阳故宫作为明清皇宫文化遗产扩展项目被列入《世界遗产名录》。', 'Home/Images/heritage/中国世遗项目/故宫.jpg', 708, '2015-07-21 06:42:57', NULL),
(5, '山东泰山', '1987-12', '文化与自然双重遗产', '泰山，古名岱山，又称岱宗。自然景观雄伟绝奇，有数千年精神文化的渗透渲染和人文景观的烘托，被誉为中华民族精神文化的缩影。1987年，被联合国教科文组织公布为世界自然与文化遗产。', 'Home/Images/heritage/中国世遗项目/泰山.jpg', 652, '2015-07-21 06:42:57', NULL),
(6, '安徽黄山', '1990-12', '文化与自然双重遗产', '黄山雄踞风景秀丽的安徽南部，是我国最著名的山岳风景区之一。黄山美在奇松、怪石、云海、温泉“四绝”。自古以来，历游名山者多以黄山之美不亚于五岳,“五岳归来不看山，黄山归来不看岳”。', 'Home/Images/heritage/中国世遗项目/5.jpg', 722, '2015-07-21 06:42:57', NULL),
(7, ' 河北承德避暑山庄及周围寺庙', '1994-12', '文化遗产', '避暑山庄又名承德离宫或热河行宫，位于河北省承德市中心北部，是清代皇帝夏天避暑和处理政务的场所。避暑山庄位于承德市中心区以北，武烈河西岸一带狭长的谷地上，距离北京230公里。它始建于1703年，历经清朝三代皇帝：康熙、雍正、乾隆，耗时约90年建成，是中国现存占地最大的古代帝王宫苑。', 'Home/Images/heritage/中国世遗项目/6.jpg', 538, '2015-07-21 06:42:57', NULL),
(8, '苏州古典园林', '1997-12', '文化遗产', '苏州古典园林历史绵延2000余年，在世界造园史上有其独特的历史地位和价值，她以写意山水的高超艺术手法，蕴含浓厚的传统思想文化内涵，展示东方文明的造园艺术典范，实为中华民族的艺术瑰宝。1997年苏州拙政园、环秀山庄、留园、网师园作为苏州园林的代表被批准列入《世界遗产名录》。2000年11月，苏州艺圃、耦园、沧浪亭、狮子林和退思园5座园林作为苏州古典园林的扩展项目被批准列入《世界遗产名录》。苏州山塘街、平江路、东北街作为苏州古典园林的扩展项目被列入《世界遗产预备名录》。', 'Home/Images/heritage/中国世遗项目/7.jpg', 582, '2015-07-21 06:42:57', NULL),
(9, '北京天坛', '1998-11', '文化遗产', '天坛位于北京的南端，是明、清两代皇帝每年祭天和祈祷五谷丰收的地方。它严谨的建筑布局、奇特的建筑结构、瑰丽的建筑装饰，被认为是我国现存的一组最精致、最美丽的古建筑群，在世界上享有极大的声誉。天坛建于明永乐十八年（1420年），与故宫同时修建，面积约270万平方米，分为内坛和外坛两部分，主要建筑物都在内坛。', 'Home/Images/heritage/中国世遗项目/8.jpg', 578, '2015-07-21 06:42:57', NULL),
(10, '北京颐和园', '1998-11', '文化遗产', '北京西郊的西山脚下海淀一带，泉泽遍野，群峰叠翠，山光水色，风景如画。从公元11世纪起，这里就开始营建皇家园林，到800年后清朝结束时，园林总面积达到了1000多公顷。', 'Home/Images/heritage/中国世遗项目/9.jpg', 566, '2015-07-21 06:42:57', NULL),
(11, '明清皇家陵寝', '2000-11', '文化遗产', '明显陵：位于湖北省钟祥市的明显陵，是明世宗嘉靖皇帝的父亲恭睿皇帝和母亲章圣皇太后的合葬墓，始建于明正德十四年（1519年），其“一陵两冢”的陵寝结构，为历代帝王陵墓中绝无仅有。清东陵：位于河北省遵化市的马兰峪。陵园大小建筑580座，是葬有顺治、康熙、乾隆、咸丰和同治5个清朝皇帝等 161人的大陵园。清西陵：位于河北省易县的永宁山下，在北京西南方120公里，是清代帝王陵寝之一，与河北省遵化县东陵东西相对而称西陵。这里埋葬着雍正、嘉庆、道光、光绪4位皇帝等76人。2000年11月，明显陵、清东陵、清西陵被列为世界文化遗产。2003年7月，十三陵和明孝陵作为明清皇家陵寝的一部分被收入《世界遗产名录》。2004年7月，盛京三陵作为明清皇家陵寝扩展项目被列入《世界遗产名录》。', 'Home/Images/heritage/中国世遗项目/10.jpg', 712, '2015-07-21 06:42:57', NULL),
(12, '河南洛阳龙门石窟', '2000-11', '文化遗产', '河南洛阳龙门石窟位于洛阳市东南，分布于伊水两岸的崖壁上，南北长达1公里。龙门石窟始凿于北魏年间，先后营造400多年。现存窟龛2300多个，雕像10万余尊，是我国古代雕刻艺术的典范之作。', 'Home/Images/heritage/中国世遗项目/11.jpg', 637, '2015-07-21 06:42:57', NULL),
(13, '中国安阳殷墟', '2006-07', '文化遗产', '中国安阳商代遗址又名殷墟，占地约24平方公里，位于河南省安阳市区西北小屯村一带，距今已有3300多年历史。殷墟是闻名中外的中国商代晚期都城遗址，是中国历史上有文献可考、并为甲骨文和考古发掘所证实的最早的古代都城遗址。', 'Home/Images/heritage/中国世遗项目/12.jpg', 606, '2015-07-21 06:42:57', NULL),
(14, '河南天地之中古建筑群', '2010-08', '文化遗产', '河南嵩山，被认为是具有神圣意义的中岳。在海拔1500米的嵩山脚下，距河南省登封市不远，有8座占地共40平方公里的建筑群，其中包括三座汉代古阙，以及中国最古老的道教建筑遗址：中岳庙、周公测景台与登封观星台等。这些建筑物历经9个朝代修建而成，登封历史建筑群是古代建筑中用于祭祀、科学、技术及教育活动的最佳典范之一。其历经汉、魏、唐、宋、元、明、清，构成了一部中国中原地区上下两千年形象直观的建筑史，是中国时代跨度最长、建筑种类最多、文化内涵最丰富的古代建筑群。', 'Home/Images/heritage/中国世遗项目/13.jpg', 589, '2015-07-21 06:42:57', NULL),
(15, '杭州西湖', '2011-06', '文化景观', '杭州西湖文化景观肇始于9世纪、成型于13世纪、兴盛于18世纪，是中国历代文化精英秉承“天人合一”哲理，在深厚的中国古典文学、绘画美学、造园艺术和技巧传统背景下，创造“中国山水美学”景观设计最经典作品。', 'Home/Images/heritage/中国世遗项目/14.jpg', 625, '2015-07-21 06:42:57', NULL),
(16, '甘肃敦煌莫高窟', '1987-12', '文化遗产', '甘肃敦煌莫高窟俗称千佛洞。位于甘肃敦煌市东南25公里的鸣沙山东麓崖壁上，上下5层，南北长约1600米。始凿于366年，后经十六国至元十几个朝代的开凿，形成一座内容丰富、规模宏大的石窟群。现存洞窟492个，壁画 4.5万平方米，彩塑2400余身，飞天4000余身，唐宋木结构建筑5座，莲花柱石和铺地花砖数千块，是一处由建筑、绘画、雕塑组成的博大精深的综合艺术殿堂，是世界上现存规模最宏大、保存最完好的佛教艺术宝库，被誉为“东方艺术明珠”。', 'Home/Images/home/p1.jpg', 639, '2015-07-21 06:42:57', '甘肃敦煌莫高窟俗称千佛洞。位于甘肃省敦煌市东南25公里的鸣沙山东麓崖壁上，上下共5层，南北长约1600米。'),
(17, '丝绸之路', '2014-06', '世界文化遗产', '2014年6月22日， 在卡塔尔首都多哈举行的第38届世界遗产大会上，由中国、哈萨克斯坦和吉尔吉斯斯坦联合申报的“丝绸之路：起始段和天山廊道的路网”被成功列入世界遗产名录。“丝绸之路：起始段和天山廊道的路网”是跨国系列文化遗产，线路跨度近5000公里，沿线包括中心城镇遗迹、商贸城市、交通遗迹、宗教遗迹和关联遗迹等５类代表性遗迹共33处，申报遗产区总面积42680公顷。中国境内有22处考古遗址、古建筑等遗迹，哈萨克斯坦、吉尔吉斯斯坦境内各有8处和3处遗迹。', 'Home/Images/heritage/中国世遗项目/丝绸之路.jpg', 564, '2015-07-21 06:42:57', NULL),
(18, '中国大运河', '2014-06', '世界文化遗产', '2014年6月22日，在卡塔尔多哈进行的第38届世界遗产大会宣布，中国大运河项目成功入选世界文化遗产名录。大运河是世界建造时间最早、使用最久、空间跨度最大的人工运河。开凿至今1600多年，是中华民族留给世界的宝贵遗产。大运河是中国东部平原上的伟大工程，是中国古代劳动人民创造的一项伟大的水利建筑，作为世界上最长的运河，也是世界上开凿最早、规模最大的运河。此次大运河申报的系列遗产分别选取了各个河段的典型河道段落和重要遗产点，共包括中国大运河河道遗产27段，以及运河水工遗存、运河附属遗存、运河相关遗产共计58处遗产。这条由隋唐大运河、京杭大运河和浙东运河组成，河道总长度1011公里的大运河，在经历了8年的申遗准备之后，终于进入《世界遗产名录》。', 'Home/Images/heritage/中国世遗项目/中国大运河.jpg', 628, '2015-07-21 06:42:57', NULL),
(19, '云南红河哈尼梯田', '2013-06', '世界文化遗产', '红河哈尼梯田位于云南南部，遍布于红河州元阳、红河、金平、绿春四县，总面积约100万亩，是哈尼族人1300多年来生生不息地“雕刻”的山水田园风光画。该梯田于2007年成为国家湿地公园；2010年，被联合国粮农组织正式列入全球重要农业文化遗产保护试点；2013年，成为中国唯一申报世界文化遗产项目。', 'Home/Images/heritage/中国世遗项目/云南红河哈尼梯田.jpg', 720, '2015-07-21 06:42:57', NULL),
(20, '中国新疆天山', '2013-06', '世界自然遗产', '2013年6月21日，在柬埔寨金边举行的第37届世界遗产大会通过审议，将中国新疆天山列入世界遗产名录。新疆天山申请的是自然遗产，申遗组成地包括：博格达峰提名地（含天山天池国家级风景名胜区等区域）、中天山提名地、托木尔峰提名地等。加上新入选的新疆天山，中国已有44处遗产列入世界遗产名录。', 'Home/Images/heritage/中国世遗项目/中国新疆天山.jpg', 538, '2015-07-21 06:42:57', NULL),
(21, '澄江帽天山化石地', '2012-07', '自然遗产', '帽天山化石地地处云南省澄江县，自1984年7月1日发现距今5.3亿年前的寒武纪动物化石以来，已累计发现200余种早寒武纪珍稀动植物化石和遗迹化石，是迄今世界上发现古生物门类最多的区域。', 'Home/Images/heritage/中国世遗项目/澄江帽天山化石地.jpg', 663, '2015-07-21 06:42:57', NULL),
(22, '元上都遗址', '2012-06', '文化遗产', '元上都遗址属全国重点文物保护单位，是中国元代都城遗址，位于内蒙古自治区锡林郭勒盟正蓝旗旗政府所在地东北约20公里处、闪电河北岸。由我国北方骑马民族创建的这座草原都城，被认定为中原农耕文化与草原游牧文化奇妙结合的产物。', 'Home/Images/heritage/中国世遗项目/元上都遗址.jpg', 543, '2015-07-21 06:42:57', NULL),
(23, '中国丹霞', '2010-08', '自然遗产', '2010年8月1日，联合国世界遗产委员会（WHC）在第34届世界遗产大会上一致通过湖南崀山、广东丹霞山、福建泰宁、贵州赤水、江西龙虎山、浙江江郎山“捆绑”申报的中国“丹霞”列为世界自然遗产。至此，中国的世界遗产地数量已增加到40个。', 'Home/Images/heritage/中国世遗项目/中国丹霞.jpg', 605, '2015-07-21 06:42:57', NULL),
(24, '山西五台山', '2009-06', '文化景观', '五台山是世界五大佛教圣地之一，位居中国四大佛教名山之首，是国务院首批公布的国家级重点风景名胜区、国家森林公园、国家对外推出的35张旅游王牌产品之一，也是中华十大名山之一、国家首批5A级旅游区。五台山自东汉永平十一年（公元68年）开始建庙，至今历时近2000年，形成了国内唯一的一处由青庙（汉传佛教）、黄庙（藏传佛教）并居一山共同讲经说法的道场，被誉为中国佛教的缩影、世界著名的佛教圣地。五台山迄今仍保存着北魏、唐、宋、元、明、清等7个朝代的寺庙建筑47处，荟萃了7个朝代的彩塑、5个朝代的壁画，以及堪称典范的古建艺术。南禅寺是世界上现存最古老的木结构建筑，被誉为中华瑰宝。佛光寺被世人誉为东方古建明珠、亚洲佛光。五台山的标志建筑塔院寺大白塔，为我国现存元代覆钵式塔最高建筑。', 'Home/Images/heritage/中国世遗项目/山西五台山.jpg', 692, '2015-07-21 06:42:57', NULL),
(25, '陕西秦始皇陵及兵马俑', '1987-12', '文化遗产', '位于陕西临潼县城东5公里，距西安36公里，是秦始皇嬴政的皇陵。陵区分陵园区和从葬区两部分。陵园占地近8平方公里，建外、内城两重，封土呈四方锥形，顶部略平，高55米，不仅是中国历史上第一座皇帝陵，也是最大的皇帝陵。1974年以来，在陵园东1.5公里处发现从葬兵马俑坑3处，出土陶俑8000件、战车百乘以及数万件实物兵器等文物；1980年又在陵园西侧出土青铜铸大型车马2乘。引起全世界的震惊和关注，被誉为“世界第八奇迹”。', 'Home/Images/heritage/中国世遗项目/陕西秦始皇陵及兵马俑.jpg', 531, '2015-07-21 06:42:57', NULL),
(26, '四川黄龙国家级名胜区', '1992-12', '自然遗产', '黄龙风景名胜区位于四川省阿坝藏族羌族自治州松潘县境内。面积700平方公里。主要景观集中于长约3.6公里的黄龙沟，沟内遍布碳酸钙华沉积，并呈梯田状排列，仿佛是一条金色巨龙，并伴有雪山、瀑布、原始森林、峡谷等景观。黄龙风景名胜区既以独特的岩溶景观著称于世，也以丰富的动植物资源享誉人间。从黄龙沟底部（海拔2000米）到山顶（海拔3800米）依次出现亚热带常绿与落叶阔叶混交林、针叶阔叶混交林、亚高山针叶林、高山灌丛草甸等。包括大熊猫、金丝猴在内10余种珍贵动物。', 'Home/Images/heritage/中国世遗项目/四川黄龙国家级名胜区.jpg', 650, '2015-07-21 06:42:57', NULL),
(27, '湖南武陵源国家级名胜', '1992-12', '自然遗产', '武陵源风景名胜区位于湖南省张家界市。总面积264平方公里，由张家界国家森林公园、索溪峪和天子山三大景区组成。主要景观为石英砂岩峰林地貌，共有3103座奇峰，姿态万千，蔚为壮观。武凌源以奇峰、怪石、幽谷、秀水、溶洞“五绝”而闻名于世。', 'Home/Images/heritage/中国世遗项目/湖南武陵源国家级名胜.jpg', 525, '2015-07-21 06:42:57', NULL),
(28, '四川九寨沟国家级名胜', '1992-12', '自然遗产', '九寨沟位于四川省西北部阿坝州九寨沟县境内，地处青藏高原东南边缘的尕尔纳山峰北麓，海拔 在 2000米至3000米之间，距四川省省会成都市435公里。九寨沟以3沟118海为代表，包括5滩12瀑，十流数十泉等水景为主景，与九寨沟十二峰联合组成高山河谷自然景观。九寨沟以原始的生态环境、一尘不染的清新空气和雪山、森林、湖泊组合成神妙、奇幻、幽美的自然风光，显现“自然的美，美的自然”，被誉为“童话世界”“人间仙境”。九寨沟的高峰、彩林、翠海、叠瀑和藏族风情被称为“五绝”。', 'Home/Images/heritage/中国世遗项目/四川九寨沟国家级名胜.jpg', 568, '2015-07-21 06:42:57', NULL),
(29, '湖北武当山古建筑群', '1994-12', '文化遗产', '武当山，雄峰峻岭，耸立于中国西部山区城市十堰市境内，东经1100度，北纬320度附近。景区“绵亘八百里”。古代武当山以“亘古无双胜境，天下第一仙山”的显赫地位，为人们顶礼膜拜的“神峰宝地”；在当代，武当山古建筑群与自然环境巧妙结合，为我国著名的游览胜地和宗教活动场所。', 'Home/Images/heritage/中国世遗项目/湖北武当山古建筑群.jpg', 706, '2015-07-21 06:42:57', NULL),
(30, '山东曲阜的孔庙、孔府及孔林', '1994-12', '文化遗产', '曲阜孔庙、孔府、孔林位于山东省曲阜市，是中国历代纪念孔子、推崇儒学的表征，以丰厚的文化积淀、悠久历史、宏大规模、丰富文物珍藏，以及科学艺术价值而著称。曲阜孔庙是祭祀孔子的本庙，是分布在中国、朝鲜、日本、越南、印度尼西亚、新加坡、美国等国家2000多座孔子庙的先河和范本，始建于公元前478年，历经2400多年而从未放弃祭祀，是中国使用时间最长的庙宇，也是中国现存最为著名的古建筑群之一；孔林延续使用2400多年，不仅是中国，也是世界上延用时间最长的氏族墓地；孔子嫡孙保有世袭罔替的爵号，历时2100多年，是中国最古老的贵族世家，其府第孔府是中国现存规模最大、保存最好、最为典型的官衙与宅第合一的建筑群。', 'Home/Images/heritage/中国世遗项目/山东曲阜的孔庙、孔府及孔林.jpg', 531, '2015-07-21 06:42:57', NULL),
(31, '西藏布达拉宫', '1994-12', '文化遗产', '在拉萨西北的玛布日山上，是著名的宫堡式建筑群，藏族古建筑艺术的精华。始建于公元7世纪，是藏王松赞干布为远嫁西藏的唐朝文成公主而建。布达拉宫分为两大部分：红宫和白宫。居中央的是红宫，主要用于宗教事务；两翼刷白粉的是白宫，是达赖喇嘛生活起居和政治活动的场所。布达拉宫于1994年12月入选《世界遗产名录》。2000年11月又加入了拉萨的大昭寺。2001年12月，拉萨的罗布林卡也被补充加入此项世界文化遗产。 ', 'Home/Images/heritage/中国世遗项目/西藏布达拉宫.jpg', 621, '2015-07-21 06:42:57', NULL),
(32, '四川峨眉山—乐山风景名胜区', '1996-12', '文化与自然双重遗产', '峨眉山，位于中国四川省峨眉山市境内，景区面积154平方公里，最高峰万佛顶海拔3099米，是著名的旅游胜地和佛教名山；集自然风光与佛教文化为一体的中国国家级山岳型风景名胜区。“大江东去，佛祖西来”，大自然以它那鬼斧神功的创造力，雕刻出乐山睡佛的凝重和峨眉山水的清秀。佛教文化又以其数代人的努力，创造了乐山大佛、冼象池、万年寺等浓郁的艺术氛围。峨眉山—乐山1996年被联合国教科文组织列入《世界自然与文化遗产名录》。', 'Home/Images/heritage/中国世遗项目/四川峨眉山—乐山风景名胜区.jpg', 581, '2015-07-21 06:42:57', NULL),
(33, '江西庐山风景名胜区', '1996-12', '文化景观', '庐山位于中国第一大河长江中游南岸、中国第一大淡水湖鄱阳湖滨，是座地垒式断块山。大山、大江、大湖浑然一体，险峻与柔丽相济，素以“雄、奇、险、秀”闻名于世。富有独特的庐山文化，具有重要的科学价值与美学价值。庐山风景名胜区面积302公里，外围保护地带为500公里。有独特的第四纪冰川遗迹，有河流、湖泊、坡地、山峰等多种地貌，有地质公园之称。', 'Home/Images/heritage/中国世遗项目/江西庐山风景名胜区.jpg', 528, '2015-07-21 06:42:57', NULL),
(34, '山西平遥古城', '1997-12', '文化遗产', '古城始建于公元前827年至公元前782年间的周宣王时期，为西周大将尹吉甫驻军于此而建。自公元前221年，秦朝实行“郡县制”以来，平遥城一直是县治所在地，延续至今。平遥古城历尽沧桑、几经变迁，成为国内现存最完整的一座明清时期中国古代县城的原型。', 'Home/Images/heritage/中国世遗项目/山西平遥古城.jpg', 558, '2015-07-21 06:42:57', NULL),
(35, '云南丽江古城', '1997-12', '文化遗产', '丽江古城位于云南省西北部，纳西族自治县的中心城镇，古城位于县境中部，海拔2400余米。是一座风景秀丽、历史悠久和文化灿烂的名城，是罕见的保存相当完好的少数民族古城。', 'Home/Images/heritage/中国世遗项目/云南丽江古城.jpg', 676, '2015-07-21 06:42:57', NULL),
(36, '福建省武夷山', '1999-12', '文化与自然双重遗产', '武夷山市位于福建省北部，属中亚热带地区。境内东、西、北部群山环抱，峰峦叠嶂，中南部较平坦，为山地丘陵区。市区海拔210米。地貌层次分明，呈梯状分布。地势由西北向东南倾斜，最高处黄岗山海拔2158米，在我国内地被称为“华东屋脊”，最低处兴田镇，海拔165米（河床标高海拔160米）。最高点与最低点相差1993米，地势高低相差之大，为全省之最。', 'Home/Images/heritage/中国世遗项目/福建省武夷山.jpg', 584, '2015-07-21 06:42:57', NULL),
(37, '重庆大足石刻', '1999-12', '文化遗产', '大足石刻是大足县境内主要表现为摩崖造像的石窟艺术的总称。大足县是重庆市所辖郊县，始建于唐乾元元年（758年），大足石刻规模宏大、刻艺精湛、内容丰富，具有鲜明的民族特色，具有很高的历史、科学和艺术价值，在我国古代石窟艺术史上占有举足轻重的地位。', 'Home/Images/heritage/中国世遗项目/重庆大足石刻.jpg', 575, '2015-07-21 06:42:57', NULL),
(38, '皖南古村落', '2000-11', '文化遗产', '西递：西递是黄山市最具代表性的古民居旅游景点，坐落于黄山南麓。据史料记载，西递始祖为唐昭宗李晔之子，因遭变乱，逃匿民间，改为胡姓，繁衍生息，形成聚居村落。故自古文风昌盛，到明清年间，一部分读书人弃儒从贾，他们经商成功，大兴土木，建房、修祠、铺路、架桥，将故里建设得非常舒适、气派。历经数百年社会的动荡、风雨的侵袭，虽半数以上的古民居、祠堂、书院、牌坊已毁，但仍保留下数百幢古民居，保留了明清村落基本面貌和特征。宏村：宏村位于黟县城西北角。村内鳞次栉比的层楼叠院与旖旎的湖光山色交相辉映，处处是景，步步入画。从村外自然环境到村内的水系、街道、建筑，甚至室内布置都完整地保存着古村落的原始状态，没有丝毫现代文明的迹象。', 'Home/Images/heritage/中国世遗项目/皖南古村落.jpg', 606, '2015-07-21 06:42:57', NULL),
(39, '四川青城山和都江堰', '2000-11', '文化遗产', '青城山，位于四川成都的都江堰风景区，是中国著名的道教名山。山内古木参天，群峰环抱，四季如春，故名青城山。青城山分青城前山和青城后山。前山景色优美，文物古迹众多；后山自然景物原始而华美，如世外桃源，绮丽而又神秘。都江堰位于四川成都平原西部的岷江上，建于公元3世纪，是中国战国时期秦国蜀郡太守李冰及其子率众修建的一座大型水利工程，是全世界迄今为止，年代最久、唯一留存、以无坝引水为特征的宏大水利工程，至今仍发挥巨大效益。', 'Home/Images/heritage/中国世遗项目/四川青城山和都江堰.jpg', 561, '2015-07-21 06:42:57', NULL),
(40, '云冈石窟', '2001-12', '文化遗产', '位于山西省大同市的云冈石窟，有窟龛252个，造像5.1万余尊，代表了公元5世纪至6世纪时中国杰出的佛教石窟艺术。其中的昙曜五窟，布局设计严谨统一，是中国佛教艺术第一个巅峰时期的经典杰作。', 'Home/Images/heritage/中国世遗项目/云冈石窟.jpg', 684, '2015-07-21 06:42:57', NULL),
(41, '江西三清山', '2008-07', '自然遗产', '三清山位于上饶东北部，有“江南第一仙峰”之称，因玉京、玉虚、玉华3座山峰高耸入云，宛如道教玉清、上清、太清3个最高境界而得名。三清山东险西奇、北秀南绝，四季景色绮丽秀美，有着其独特的花岗岩石柱与山峰，丰富的花岗岩造型石与多种植被、远近变化的景观及震撼人心的气候奇观相结合，创造了世界上独特的景观美学效果。', 'Home/Images/heritage/中国世遗项目/江西三清山.jpg', 634, '2015-07-21 06:42:57', NULL),
(42, '云南“三江并流”自然景观', '2003-07', '自然遗产', '“三江并流”自然景观位于中国西南部云南省青藏高原南部横断山脉的纵谷地区，由怒江、澜沧江、金沙江及其流域内的山脉组成，整个区域面积达4.1万平方公里。它地处东亚、南亚和青藏高原三大地理区域的交汇处，是世界上罕见的高山地貌及反映其演化的代表地区，也是世界上生物物种最为丰富的地区之一。长期以来，“三江并流”自然景观一直是科学家、探险家和旅游者的向往之地。', 'Home/Images/heritage/中国世遗项目/云南“三江并流”自然景观.jpg', 603, '2015-07-21 06:42:57', NULL),
(43, '福建土楼', '2008-07', '文化遗产', '福建永定土楼位于龙岩地区，是世界上独一无二的神奇的山区民居建筑，是中国古建筑的一朵奇葩。它历史悠久、风格独特，规模宏大、结构精巧。土楼分方形和圆形两种。“福建土楼”造型独特，规模宏大，结构奇巧。土楼文化根植于东方血缘伦理关系，是聚族而居传统文化的历史见证，体现了世界上独一无二的大型生土夯筑的建筑艺术成就，具有“普遍而杰出的价值”。', 'Home/Images/heritage/中国世遗项目/福建土楼.jpg', 538, '2015-07-21 06:42:57', NULL),
(44, '开平碉楼与古村落 ', '2007-06', '文化遗产', '开平碉楼位于广东省开平市，是乡土建筑的一个特殊类型，是一种集防卫、居住和中西建筑艺术于一体的多层塔楼式建筑。根据现存实证，开平碉楼最迟在明代后期（16世纪）已经产生，到19世纪末20世纪初发展成为表现中国华侨历史、社会形态与文化传统的一种独具特色的群体建筑形象。', 'Home/Images/heritage/中国世遗项目/开平碉楼与古村落.jpg', 561, '2015-07-21 06:42:57', NULL),
(45, '吉林高句丽王城、王陵及贵族墓葬', '2004-07', '文化遗产', '主体坐落于吉林省集安市的“中国高句丽王城、王陵及贵族墓葬”申报世界文化遗产成功，使我国世界遗产名录增加到30个。', 'Home/Images/heritage/中国世遗项目/吉林高句丽王城、王陵及贵族墓葬.jpg', 703, '2015-07-21 06:42:57', NULL),
(46, '澳门历史城区', '2005-06', '文化遗产', '“澳门历史城区”是连结相邻的众多广场空间及20多处历史建筑，以旧城区为核心的历史街区。覆盖范围包括妈阁庙前地、亚婆井前地、岗顶前地、议事亭前地、大堂前地、板樟堂前地、耶稣会纪念广场、白鸽巢前地等多个广场空间，以及妈阁庙、港务局大楼、郑家大屋、圣老楞佐教堂、圣若瑟修院及圣堂、岗顶剧院、何东图书馆、圣奥斯定教堂、民政总署大楼、三街会馆（关帝庙）、仁慈堂大楼、大堂（主教座堂）、卢家大屋、玫瑰堂、大三巴牌坊、哪吒庙、旧城墙遗址、大炮台、圣安多尼教堂、东方基金会会址、基督教坟场、东望洋炮台（含东望洋灯塔及圣母雪地殿圣堂）等20多处历史建筑。', 'Home/Images/heritage/中国世遗项目/澳门历史城区.jpg', 698, '2015-07-21 06:42:57', NULL),
(47, '中国南方喀斯特', '2007-06', '自然遗产', '由云南石林的剑状、柱状和塔状喀斯特、贵州荔波的森林喀斯特、重庆武隆的以天生桥、地缝、天洞为代表的立体喀斯特共同组成，形成于50万年至3亿年间，总面积达1460平方公里，其中提名地(核心区)面积480平方公里，缓冲区面积980平方公里。 ', 'Home/Images/heritage/中国世遗项目/中国南方喀斯特.jpg', 650, '2015-07-21 06:42:57', NULL),
(48, '四川大熊猫栖息地', '2006-07', '自然遗产', '四川大熊猫栖息地世界自然遗产包括卧龙、四姑娘山、夹金山脉，面积9245平方公里，涵盖成都、阿坝、雅安、甘孜4个市州12个县。这里生活着全世界30%以上的野生大熊猫，是全球最大最完整的大熊猫栖息地。它曾被自然保护国际选定为全球25个生物多样性热点之一，被全球环境保护组织确定为全球200个生态区之一。 ', 'Home/Images/heritage/中国世遗项目/四川大熊猫栖息地.jpg', 637, '2015-07-21 06:42:57', NULL);

