--
-- 表的结构 `news_active`
--

CREATE TABLE IF NOT EXISTS `news_active` (
  `ID` int(11) NOT NULL,
  `Title` varchar(255) DEFAULT NULL,
  `Content` text,
  `OriAuthor` varchar(255) DEFAULT NULL COMMENT '原作者',
  `Author` varchar(255) DEFAULT NULL,
  `Editor` varchar(255) DEFAULT NULL,
  `PubTime` datetime DEFAULT NULL,
  `BrowseNumber` int(11) DEFAULT '0',
  `Source` varchar(255) DEFAULT NULL,
  `ImgPath` varchar(255) DEFAULT NULL,
  `InsertTime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `Headline` int(5) NOT NULL DEFAULT '0',
  `Abstract` text
) ENGINE=MyISAM AUTO_INCREMENT=98 DEFAULT CHARSET=utf8;

--
-- 转存表中的数据 `news_active`
--

INSERT INTO `news_active` (`ID`, `Title`, `Content`, `OriAuthor`, `Author`, `Editor`, `PubTime`, `BrowseNumber`, `Source`, `ImgPath`, `InsertTime`, `Headline`, `Abstract`) VALUES
(1, '京杭大运河终入海', ' 2013年12月30日，历时10余年改造的杭甬运河全线通航。京杭运河由此向东延伸近240公里，千年历史水道终于有了出海口。\r\n    杭甬运河起自杭州三堡，途经萧山、绍兴、上虞、余姚，在宁波甬江有了出海口，全长约239公里。\r\n杭甬运河全线通航还将激活以“水文化”为内核的“运河文化”。全线串起了以天童寺为代表的佛教文化，以梁祝、古桥为代表的吴越文化和河姆渡文化，运河贯通将带动失落文明的复兴。\r\n', '周竟', '', '何水', '2014-07-09 16:30:11', 531, NULL, NULL, '2015-07-21 05:49:00', 0, '2013年12月30日，历时10余年改造的杭甬运河全线通航。京杭大运河由此向东延伸了近240公里，这条千年历史的水道终于有了出海口，京杭大运河终入海。'),
(2, '大运河南旺枢纽等两处遗址公园晋升为“国家级”', '日前国家文物局正式公布山东省曲阜鲁国故城、大运河南旺枢纽为第二批国家考古遗址公园；临淄齐国故城、城子崖为第二批国家考古遗址公园立项名单。由此,山东省成为全国唯一一个有两处遗址公园同时挂牌的省份。\r\n     曲阜鲁国故城考古遗址公园和大运河南旺枢纽考古遗址公园于2010年立项,目前考古遗址公园建设初具规模,部分对游客开放。\r\n     除了这两处遗址晋升“国家级”外,临淄齐国故城考古遗址公园和城子崖考古遗址公园还进入了第二批国家考古遗址公园立项名单。', '王蕾', '', '何水', '2014-07-09 16:30:14', 662, NULL, NULL, '2015-07-21 05:49:00', 0, '日前国家文物局正式公布山东省曲阜鲁国故城、大运河南旺枢纽为第二批国家考古遗址公园；临淄齐国故城、城子崖为第二批国家考古遗址公园立项名单。'),
(3, '运河学者聚首江南大学研讨运河文化', '日前，中国传媒集团大运河文化研究院执行院长应晓萍，上海交通大学教授刘士林，江苏省社科院研究员王健，江南大学杂志社副社长、编审谢光前，《中国文化报•大运河文化专刊》副主编李道国，无锡滨湖发展研究院有关领导，无锡运河之光文化艺术交流有限公司有关负责人聚首江南大学，就中国大运河申遗及后申遗时代的走向进行了研讨。\r\n日前，中国传媒集团大运河文化研究院执行院长应晓萍，上海交通大学教授刘士林，江苏省社科院研究员王健，江南大学杂志社副社长、编审谢光前，《中国文化报•大运河文化专刊》副主编李道国，无锡滨湖发展研究院有关领导，无锡运河之光文化艺术交流有限公司有关负责人聚首江南大学，就中国大运河申遗及后申遗时代的走向进行了研讨。\r\n王健认为，大运河申遗，社会各方付出了艰辛的努力，研究大运河历史文化蔚然成风，成果颇多，但细加盘点，却有不少遗憾。他认为，保护大运河文化遗产必须求真务实严谨科学，这既是学术发展的精神所在，也是大运河永续长流的不竭之源。作为大运河文化研究专家，刘士林的《中国脐带——大运河城市群叙事》一书中的观点深受运河申遗各城市的重视，社会影响力非常大。就大运河申遗及申遗后的大运河走向，刘教授认为，中国大运河逐渐淡去其实用功能之后，以文化线路申遗的前景非常光明，申遗成功指日可待。但大运河文化研究界更关注申遗后的大运河走向，应该通过申遗唤醒全民对大运河的保护意识，让我们每个人都对大运河的源远流长有所担当，这才是最重要的。谢光前强调大运河是生生不息之河，大运河的流变必有其自身特质，保护大运河立法是关键，文化自觉是基础，生产性保护是必然。与会人员一致表示，将在大运河文化研究领域不遗余力地继续耕耘，为大运河的永续长流贡献自己的力量。\r\n', '', '何水', '', '2014-07-09 16:30:17', 575, NULL, NULL, '2015-07-21 05:49:00', 0, '运河领域重要人物应晓萍女士，刘士林教授，王健研究员，谢光前教授，李道国教授以及相关负责人聚首江南大学，就中国大运河申遗及后申遗时代走向进行了研讨。'),
(4, '《运河情缘》百幅楹联书写运河情', '由中国国际文化出版社出版、无锡市横云书画院秘书长冯和年主编的《运河情缘》百幅楹联书法作品集日前面世。无数全国美协、书协和楹联学会会员纷纷投稿，更有90高龄的耄耋老者和小学生热情参与。该书由6000多幅楹联作品精选出100幅汇集成册。在这些楹联中，充分描绘了中国大运河千百年文化遗存，表达了参与者对运河的浓厚感情。该书对呼唤人们感恩运河、关爱运河有重要意义。', '', '明 月', '', '2014-07-09 16:30:21', 548, NULL, NULL, '2015-07-21 05:49:00', 0, NULL),
(5, '无锡百年渡口再获新生 成京杭大运河“活标本”', '4月22日，京杭运河无锡段“乌龙潭渡口”重新启航，当地古运河畔最后一个渡口再获新生，作为古文化遗产的一部分，该渡口将作为记录江南水乡文化的“活标本”，为“江南版清明上河图”再添新彩。\r\n无锡是京杭大运河唯一穿城而过的城市，南长区清名桥历史文化街区已成为中国大运河沿河18座城市的申遗示范点。沿河的清明桥街区集寺、塔、河、街、窑、宅、坊、弄、馆等众多古代人文景观于一体，运河文化、江南民俗文化、民族工商业文化、宗教文化等多种文化交相辉映，被誉为“运河绝版地、江南水弄堂”。\r\n4月22日，京杭运河无锡段“乌龙潭渡口”重新启航，当地古运河畔最后一个渡口再获新生，作为古文化遗产的一部分，该渡口将作为记录江南水乡文化的“活标本”，为“江南版清明上河图”再添新彩。\r\n无锡是京杭大运河唯一穿城而过的城市，南长区清名桥历史文化街区已成为中国大运河沿河18座城市的申遗示范点。沿河的清明桥街区集寺、塔、河、街、窑、宅、坊、弄、馆等众多古代人文景观于一体，运河文化、江南民俗文化、民族工商业文化、宗教文化等多种文化交相辉映，被誉为“运河绝版地、江南水弄堂”。\r\n旧时江南，河港密布，摆渡是唯一的出行方式。本次百年渡口“乌龙潭渡口”是无锡古运河畔的最后一个渡口，作为古文化遗产的一部分，无疑成为江南水乡文化及枕河长廊的“活标本”。江苏古运河投资发展公司相关负责人介绍。\r\n“民国时期无锡城区有渡口50多个，仅古运河城区一段就有20多个。此次编号‘锡渡11号’的‘乌龙潭渡口’将成为清名桥古运河旅游的体验式新景点。”该负责人表示。\r\n据悉，近年来，无锡清名桥古运河景区凭借古建筑群、古遗址、名人故居等文化遗产，开辟了‘东方民族画廊’的水陆特色游览项目。5年间，接待游客50万人次，实现旅游收入2000多万元。', '洪晓红 李一芳', '', '何水', '2014-07-09 16:30:25', 537, NULL, NULL, '2015-07-21 05:49:00', 0, NULL),
(6, '京杭运河济宁段部分航道将“扩容” 接通运河苏北段', '近日，山东省济宁市启动京杭运河济宁段部分航道升级计划，京杭运河济宁段航道由三级航道升级为二级航道，升级后与京杭运河苏北段二级航道接通。\r\n据了解，目前正在通航的京杭运河济宁段北起济宁南至韩庄，全长126公里，是目前京杭大运河中“鲁运河”的唯一仍在通航的河段。随着物质运输增加及水体污染的要求，京杭运河济宁段将将由三航道全面升级为二级航道，航道底宽由现在的50米扩大至70米，单体通航船只吨位由1000吨提升至2000吨。提升为二级航道后，航道的整体运输能力扩大一倍。此外，长沟、邓楼、微山一线、嘉祥、韩庄复线五大船闸的工程建设，将大幅提升京杭运河航道的通过能力。\r\n', '相旺 张则鹏', '', '何水', '2014-07-09 16:30:28', 706, NULL, NULL, '2015-07-21 05:49:00', 0, NULL),
(7, '全国首艘运河游船下扬州', '日前，一艘名为“运河名城1号”的豪华游船缓缓驶进施桥船闸一号闸，标志着扬州大运河水上旅游航线悄然兴起。\r\n\r\n    据了解，“运河名城1号”造价近700万，核载150人，全长33.8米、宽8.5米，是目前为扬州大运河水上旅游专线新定制的全国第一艘运河豪华游船。\r\n\r\n扬州作为大运河申遗的牵头城市，今年4月18日旅游节将首航推出“扬州—邵伯—高邮”的大运河水上旅游专线。线路部分水域在古运河封闭水域内，部分水域在大运河混合航道内，全程长达45公里，途经邵伯船闸。\r\n', '', '施科 顾晓平 邱建生 林玲', '', '2014-07-09 16:30:32', 596, NULL, NULL, '2015-07-21 05:49:00', 0, NULL),
(8, '无锡申遗古运河开始搭建“生态长廊”', '今年，京杭大运河申遗项目将正式进行评审。作为申遗点，被誉为“江南水弄堂，运河绝版地”的无锡古运河在近年的保护利用中，确立了不破坏沿河街坊肌理和空间轮廓的原则，重点打造清明桥历史文化街区，保护和修复特色古民居和名人故居，打造中国丝业博物馆、古窑群博物馆和古运河博物馆，鼓励沿运河人家创办旅游服务业……如今的古运河沿线，已成为无锡历史积淀最深的文化片区。\r\n为了美化运河环境和净化水质，近日，相关部门开始在古运河段搭建“生态长廊”，开展水面“种植”实验，把美人蕉、睡莲等花草固定在水面的生态浮床上，通过植物对水中污物的吸附作用，平衡水域生态环境，净化水质，美化河道。\r\n', '张立伟', '', '何水', '2014-07-09 16:30:36', 559, NULL, NULL, '2015-07-21 05:49:00', 0, NULL),
(9, '扬州曹庄隋炀帝墓入选2013十大考古新发现', '2013年度全国十大考古新发现4月9日下午揭晓，江苏扬州曹庄隋唐墓(隋炀帝墓)等考古成果入选。\r\n“隋炀帝墓”真身自去年重见天日以来，一直备受关注、争议不断。尽管专家确认其为隋炀帝墓无误，但是此前全国已出现过三座所谓杨广墓，有一座甚至被列为历史纪念遗迹。此外，此次出土的墓志铭疑点重重，尸骨何在等谜团始终未能解开。去年3月，江苏省扬州市邗江区西湖镇曹庄一处房地产项目施工时发现了两座砖室墓。4月中旬，一号墓内的“隨故煬帝墓誌”出土。事实上，在曹庄隋炀帝墓被确认之前，隋炀帝杨广到底葬身何处一直争议不断。此前就有3地号称有杨广之墓，包括陕西咸阳武功县、河南洛阳东宋乡，但人们普遍认定的还是仅距新墓十余里的邗江区槐泗镇隋炀帝陵。陕西所谓的“隋炀帝墓”位于咸阳城西50公里处武功县武功镇西塬上。该墓与隋文帝墓遥遥相望，清乾隆年间陕西巡抚毕沅所书“隋炀帝陵”碑已被毁坏，一片荒芜。\r\n\r\n据考证，历史上隋炀帝墓确实多次迁移，但如果后人将隋炀帝墓迁移，则不可能还保留原来的墓，因此目前判定陕西的隋炀帝墓是衣冠冢。另有古书记载，隋炀帝陵在今河南洛宁，如今当地百姓都将洛宁城北一座山称为“香炉山”，专为杨广供奉香火，而圆形山包则是杨广墓顶，但这一说法可能性很小。\r\n\r\n去年11月16日，国家文物局和中国考古学会召开论证会，经中国社科院、北京大学、南京博物院等多位专家确认，扬州曹庄隋唐墓葬就是真正的隋炀帝墓，也就是隋炀帝杨广与萧后最后的埋葬之地。', '新华报业', '', '何水', '2014-07-09 16:30:39', 666, NULL, NULL, '2015-07-21 05:49:00', 0, NULL),
(10, '人文纪录片《大运河》开机', '4月13日，由中央电视台科教节目制作中心、中国联合国教科文组织全国委员会、中央新影集团等单位联合投拍的大型人文纪录片《大运河》，在北京举行开机仪式。    \r\n    这次央视投拍《大运河》，是为挖掘传承中华民族的优秀历史文化，助力我国正在进行的大运河整体申遗事业的一次努力，得到了包括我市在内的运河沿岸各相关城市和有关部门及专家的支持。在当天上午的开机仪式现场，《大运河》总制片人杨才旺表示：这次纪录片《大运河》拍摄是一次历史性的拍摄活动，并持续开展大运河文化传播的相关系列活动，包括建立大运河频道、成立大运河书画院等大运河文化挖掘传播工作。\r\n    著名画家陈谋、师鸿、王桂堂、吴东魁、沨竹子、董魁聚、张东林、郝爱平、包少茂、李庆富、马未定、杨志斌、刘建武、王文峰、于桂敏、于小艳等艺术家展示了他们大运河题材的艺术精品画作。这些画作将会同其他大运河书画作品一起在本片拍摄完成后举行书画巡展并结集出版。\r\n     据了解，开机仪式后，摄制组将在各沿岸城市全面开启拍摄之旅。', '', '', '何水', '2014-07-09 16:30:42', 559, NULL, NULL, '2015-07-21 05:49:00', 0, NULL),
(11, '宋庄画家到大运河畔采风', '2014年4月，北京成己成美文化传播有限公司和宋庄梧桐艺林画廊共邀林平、王绍德、薛金拥、李宏斌、李大鸿、李磊等6位画家到北京通州大运河畔采风，用画笔记录美丽的大运河。经过数天写生，6位画家用画笔描绘出春季运河畔的繁华景象：葱茏的树木掩映着燃灯古塔；大运河上远帆点点；玉带桥上霓虹照水等通州运河景色。李宏斌曾在不同时期描绘过北京已经“申遗”成功的6处“世界文化遗产”:长城、故宫、周口店猿人遗址、天坛、颐和园、明十三陵。他说：“习主席到法国巴黎参观了联合国教科文组织总部并向世界介绍了中国文化遗产，我希望通过画笔把中国运河展现给全世界，让中国运河和万里长城、秦始皇陵兵马俑一样成为中国的亮点。” ', '', '王 丹', '', '2014-07-09 16:30:45', 678, NULL, NULL, '2015-07-21 05:49:00', 0, NULL),
(12, '古运河楹联文化讲座受欢迎', '前不久，无锡市百草园书店里人头济济。近百名楹联爱好者兴趣盎然的聆听无锡古运河楹联文化讲座。无锡市楹联学会理事尤荣福以习近平总书记引述的“百姓联”为例，介绍了《联律通则》的6条基本要求并以古运河申遗为契机，介绍无锡古运河两岸南水先庙、清名桥、跨塘桥、南长街、布码头、南禅寺、西水墩、接官亭、黄埠墩、吴桥、皋桥11个景点的22副古今楹联，受到了普遍欢迎。', '', '心 诚', '', '2014-07-09 16:30:48', 587, NULL, NULL, '2015-07-21 05:49:00', 0, NULL),
(13, '“因爱而生 为爱而动”拍卖活动', '4月12日，心灵海国际教育集团•力石伯乐2014千训圆梦年会在运河畔同里古镇隆重举行，700余名企业家参加了“因爱而生，为爱而动”的爱心慈善拍卖活动，浙江千训爱心基金会共募得慈善爱心款项863万元，其中中国文化报大运河文化专刊副主编李道国的《金刚般若波罗蜜经》拍得善款30万元。', '', '惠 畅', '', '2014-07-09 16:30:52', 618, NULL, NULL, '2015-07-21 05:49:00', 0, NULL),
(14, '《遇见大运河》：用世界的语言去表达中国大运河', '杭州歌舞剧院历经三年筹备的舞台剧场《遇见大运河》5月１２日与媒体见面， 5月２１日在杭州大剧院首演。它以世界的语言表达中国大运河的历史，助力中国大运河“申遗”。\r\n   《遇见大运河》分为两条情节线，一支是展现现代人对运河灵魂的探寻，一支则展现运河的千年历史——开凿、繁荣、遗忘、涅槃与再见。它也是２００８年北京奥运会开幕式主创团队时隔６年以后完成的作品。与传统舞剧只有肢体语言不同，《遇见大运河》中的舞者不但有舞蹈，也有人物对白，显得更为冒险。\r\n    总导演崔巍表示，《遇见大运河》表达的是用生命、泥土和水堆砌起来的运河的故事，也不仅仅是讲述一个只属于中国的故事。“我们遇见运河，讲的是人与水之间的感情、人类对生命与大自然的渴望、对真善美及共同繁荣的追求，让世界各地的观众产生情感上的共鸣与感动。”\r\n    在杭州首演后，舞蹈剧场《遇见大运河》将在中国大运河沿线２７城市巡演，最终走向世界舞台。主创团队艺术总监萧加介绍说，为了配合《遇见大运河》海外演出，团队将会通过策划系列摄影、美术、快闪等文化活动，普及中国历史及大运河的知识，增进世界对中国文化的理解。\r\n', '董芮 冯源', '', '何水', '2014-07-09 16:30:55', 561, NULL, NULL, '2015-07-21 05:49:00', 0, NULL),
(15, '南水北调让大运河航运北延 山东东平将成通航最北点', ' 5月22日，在由山东济宁市港航局组织的座谈会上，记者获悉，借助南水北调东线工程之利，2015年底，京杭大运河向北的最远航点有望由济宁延伸至泰安东平。\r\n    据济宁市港航国内局党委委员、总工高永刚介绍，浙江、江苏、山东等经济发达省份越来越重视运河航运。浙江宁波为满足运输需要，还新挖了一条运河。在对运河使用最重视的江苏，每年公路和水运在整个运输中所占比例达到35%-40%。去年底，南水北调东线工程取得突破性进展，“南水”已经注入泰安的东平湖。\r\n    行水河道被定名为京杭大运河，作为新辟运河，与元明清时期的老运河基本平行。经多方论证、协商，在这段全程79公里的新运河上，将增加航运功能，2015年底可实现通航。届时，船舶从杭州沿京杭大运河北上，最远点将不再是济宁，而是东平。\r\n', '乔显佳 张榕博 汪泷', '', '何水', '2014-07-09 16:30:58', 666, NULL, NULL, '2015-07-21 05:49:00', 0, NULL),
(16, '常州运河文化研究系列活动', '近日，常州市举办了一场由该市文联发起的运河文化学术研讨会，常州市政府、常州大学、总工会、文联的相关领导和专家参加了会议。原常州市副市长、市政协副主席薛锋，常州市文联主席荣凯元，常州市政协学习与文史委副主任蒋英慧，常州市文联副主席池银合、胡军生，常州大学人文社科处副处长葛金华，常州市诗词协会副会长张平生等参加了研讨。会议上诸多专家学者对《常州运河研究》文集稿件进行了深入的分析探讨，就大运河在常州的历史和现状的论述，提出许多宝贵的修改意见。该文集共搜集有关常州大运河的33篇原创专稿，撰写成员包括全国各地社科院、高校、文史委等众多学者及运河文化票友。近阶段中常州市开展的运河文化研究系列活动共分5个单元。除了社出版的《常州运河研究》文集外，还将出版运河诗词、楹联、影像等系列专著。', '', '明 月 银 合', '', '2014-07-09 16:31:01', 702, NULL, NULL, '2015-07-21 05:49:00', 0, NULL),
(17, '通州举办运河申遗祈福茶会', '前不久，第一届燃灯佛舍利塔禅茶会暨中国运河“申遗”祈福禅茶会在通州举行。此次活动由北京静荷塘发起，活动旨在通过禅茶会的形式让中国的茶文化得以普及和弘扬。静荷塘成立于2006年，以弘扬茶文化为己任。禅茶会上静荷塘塘主葛静贤带领30余席茶友为各界参观燃灯塔的人们义务泡茶，讲解中国茶文化，普及“申时茶”健康理念，“申时”即每天15：00—17：00，此时人体的膀胱经开始排毒，身体需要充足的水分参与运化，达到健康目的。三泡茶品鉴结束，静荷塘塘主引领所有茶友绕燃灯佛舍利塔三周，为中国大运河申遗祈福。', '', '王 丹', '', '2014-07-09 16:31:04', 599, NULL, NULL, '2015-07-21 05:49:00', 0, NULL),
(18, '运河情散文《回眸远方》面世', '一部反映运河生活的散文集《回眸远方》最近由中国国际文化出版社出版，全书共分9个部分，142篇作品，计24万字。作者冬雪，毕业于河北师范大学，曾在北京大学进修英语2年。中国作家协会江苏分会会员，中学退休教师，先后出版过8部涉及江南运河水乡的长篇小说及其他文集，读冬雪的《漫步在钱钟书故居》、《春风又绿江南岸》、《二泉映月赋》等浸润于运河之水的散文，犹如品茗运河之水，让人久久回味。徜徉在她的文字里，总被她清新淡雅的文风、深厚的文学底蕴以及她对江南运河丝丝入扣的描写所吸引。', '', '江 南', '', '2014-07-09 16:31:07', 600, NULL, NULL, '2015-07-21 05:49:00', 0, NULL),
(19, '《常州运河史话》出版发行', '《常州运河史话》最近由凤凰出版社和常州市文联共同出版，《常州运河史话》是常州运河文化研究系列丛书中出版的第一部著作，全书共29篇文章33.6万字，由邵志强、张戬炜撰写。《常州运河文化研究》编委会由中共常州市委常委、宣传部部长徐缨任主任，常州市文联副主席、法学博士胡军生主编。述说了常州运河的前世今生，内容涉及常州运河的历史、地理、风貌、变迁、遗产以及运河对常州经济、文化、政治的影响等，这对常州大运河甚至中国大运河的保护、利用和申遗有着重要意义。', '', '银 合 明 月', '', '2014-07-09 16:31:10', 697, NULL, NULL, '2015-07-21 05:49:00', 0, NULL),
(20, '18天18城,徒步大运河收官', '6月1日,京杭大运河考察队来到杭州,走到京杭大运河南端拱宸桥。至此,由齐鲁晚报联合京杭大运河沿线6家省级主流媒体与阿里公益天天正能量共同发起的“徒步大运河穿越古今接续文明”考察报道活动在杭州完美收官。\r\n1日下午,初夏的杭州下起雨来。考察队来到大运河杭州段,远远地就看见一座石拱桥,经年风雨让桥身斑驳。拉着煤炭、沙石的货船从拱门下穿行而过。雨水打在石板路上,撑着雨伞的人们悠闲地在桥上走动。\r\n这里就是京杭大运河南端,古时乘船外出的杭州人,见到拱宸桥就知道回到了家乡。\r\n从北京通州燃灯塔到杭州拱宸桥,大运河水浩浩荡荡千余公里,考察队用18天时间沿途走来,对沿岸居民、水利设施等进行考察。队伍走到拱宸桥,标志着徒步考察活动完美收官。\r\n在拱宸桥下,考察队发出关于加强京杭大运河研究与保护的倡议。之后,考察队成员在“徒步大运河寻访考察活动”队旗上签字留念。\r\n考察队从5月15日在北京通州燃灯塔出发,沿途经过大运河流经的河北、山东、江苏、浙江等六省份共18个城市。\r\n', '马云云　乔显佳', '', '何水', '2014-07-09 16:31:13', 583, NULL, NULL, '2015-07-21 05:49:00', 0, NULL),
(21, '中国大运河冲刺“世遗” 结果最快于20日起陆续公布', '6月15日，第38届世界遗产大会（全称联合国教科文组织世界遗产委员会会议）在卡塔尔首都多哈开幕。在10天的会期里，包括丝绸之路、大运河、“中国南方喀斯特”扩展项目在内的三个中国项目将接受大会审议，结果最快于20日起陆续公布。\r\n大运河于2006年12月列入《中国世界文化遗产预备名单》。2007年9月26日，国家文物局正式宣布扬州为中国大运河申报世界文化遗产牵头城市。2009年4月，由国务院总牵头，8个省市和13个部委联合组成大运河保护和申遗省部际会商小组，正式建立省部协商机制，大运河申遗上升为国家行动。2013年初，国家文物局正式确定了首批申遗点段，它们分布在8个省市的31个遗产区，涉及27段河道和58处遗址点，其中扬州境内6段河道10个遗产点入选。\r\n2006年，我国与中亚有关国家开展了丝绸之路跨国系列申报世界遗产工作。项目将使用吉尔吉斯斯坦2014年的申遗名额，中国境内的项目名称为“丝绸之路：起始段与天山廊道的路网”，其中包含22处考古遗址、古建筑等遗迹。项目全线中，哈萨克斯坦境内有8处遗迹，吉尔吉斯斯坦境内有3处遗迹。\r\n2007年，“中国南方喀斯特”入选世界遗产项目，今年申报的扩展项目包括重庆金佛山与贵州施秉、广西桂林、广西环江。\r\n', '赵天 王鹏', '', '何水', '2014-07-09 16:31:16', 671, NULL, NULL, '2015-07-21 05:49:00', 0, NULL),
(22, '大运河丝绸之路进世遗名录', '6月22日下午，在卡塔尔多哈召开的联合国教科文组织第38届世界遗产委员会会议审议通过中国大运河项目和中国、哈萨克斯坦、吉尔吉斯斯坦跨国联合申报的丝绸之路项目列入《世界遗产名录》。\r\n国家文物局有关负责人表示，大运河、丝绸之路是在世界范围内具有广泛影响力和号召力的超大型线性文化遗产。此次申报成功，表明大运河、丝绸之路的突出普遍价值、真实性、完整性以及中国政府为保护这些珍贵遗产付出的艰苦努力，得到了世界遗产委员会和国际专业咨询机构的一致认可。\r\n国家文物局副局长童明康在多哈会议发言中表示，大运河、丝绸之路申遗是史无前例的遗产保护过程。大运河的申遗使世界遗产的保护理念在中国大运河沿线8个省（市）、1.7亿民众中得到广泛传播并深入人心。而作为世界上最伟大的贸易之路、对话之路、和平之路和发展之路，丝绸之路跨国联合申遗进一步增进了中国和哈、吉等中亚国家的传统友谊，成为新时期中国与中亚地区各国和平对话、共同发展的新纽带。\r\n他表示，大运河、丝绸之路申遗成功以后，中国政府将恪守《世界遗产公约》及其操作指南的有关要求，继续为大运河、丝绸之路珍贵文化遗产提供最好的保护，并将继续团结各利益相关方，进一步巩固跨地区跨行业对话和协调机制，深入探讨巨型线性文化遗产，尤其是活态文化遗产的保护、管理和利用模式，“让蕴含丰富精神内涵的大运河流淌向可持续发展的未来，让古老的丝绸之路再次绽放灿烂的光彩。”童明康说。\r\n', '张然', '', '何水', '2014-07-24 15:05:30', 713, '人民网', NULL, '2015-07-21 05:49:00', 0, NULL),
(23, '大运河申遗成功邮票及纪念封今日首发', '中国大运河项目成功入选世界文化遗产名录，6月23日，在中国大运河的重要节点城市——浙江杭州市，庆祝申遗成功的《运河十景名家书画》个性化邮票及纪念封进行了首发。\r\n据悉，纪念封一套3枚，邮票选用2009年发行的“京杭大运河拱宸桥特种邮票”。纪念封上书“中国大运河正式列入‘世界遗产名录’”几字，3枚纪念封的左下角分别印有京杭大运河杭州拱宸桥、浙东运河宁波庆安会馆、隋唐大运河洛阳含嘉仓遗址的图案，显得0枚，主图为“马踏飞燕”，附图选用当代书法名家曾宓、孔仲起、吴山明、韩天古朴而有文化韵味。\r\n个性化邮票1组两版，共2雍等有关杭州“运河十景”的书画作品，包括：广济通衢、西陵怀古、富义留馀、拱宸邀月等等。\r\n韩天雍是中国美术学院的教授，他特地用大篆写了陆游的《访客至北门，抵暮而归》，他说：“大篆是是西周晚期普遍采用的字体，距今有近3000年的历史。中国大运河建于公元前486年，已有2500年历史了，用大篆这种字体与运河呼应，有一种历史的纵深感。”\r\n据悉，北京时间6月22日15时许，中国大运河正式申遗成功，由隋唐大运河、京杭大运河和浙东大运河组成，申报的河道总长度1011公里，涉及8个省、直辖市。这次的邮票和纪念封经中国邮政集团公司批准，由中国集邮总公司发行。\r\n', '赵晔娇 李晨韵', '', '何水', '2014-07-24 15:10:03', 598, '中新', NULL, '2015-07-21 05:49:00', 0, NULL),
(24, '枣庄台儿庄区邮政局发行中国大运河申遗纪念邮戳', '6月22日，中国大运河项目成功入选《世界文化遗产名录》，成为我国第32项世界文化遗产及第46项世界遗产。6月25日，为纪念中国大运河申遗成功，枣庄台儿庄区邮政局发行中国大运河申遗成功纪念邮戳一枚。\r\n作为大运河申遗城市之一的台儿庄，凭借3千米古运河河道、11座古码头、960米的古驳岸、以及运河水工遗存、运河附属遗存、运河相关遗产点入选其中。中国大运河成功申遗也意味着台儿庄区首个世界文化遗产诞生。\r\n6月25日，枣庄台儿庄区邮政局发行中国大运河申遗成功纪念邮戳。纪念邮戳以台儿庄古城西大门为背景，配以世界遗产标志，书有“纪念‘中国大运河’列入《世界遗产名录》”字样。\r\n据了解，大运河枣庄段，文物古迹众多，遍及运河两岸，尤其是台儿庄城内的3公里运河古道，是目前全国保存最为完好的古运河河段之一，被称为“活着的运河”。京杭运河途径台儿庄，在这里形成了独特的鲁南运河文化。\r\n这里保存基本完整的古街巷、古建筑、古民居、古码头、古村落等在山东省乃至全国的北方地区独具一格，具有“遗产村庄”的性质。特别是与运河古码头连为一体的古街区，临水而建，与江南水乡极为相似，但又有自身的特色，这里的建筑融南北特征于一身，成为南北文化的交汇，融合的典范。 市近日在杭州联合成立“旅游推广联盟”，旨在打造一条全新的世界级精品旅游线，进一步开发与保护京杭大运河。\r\n', '张严新 ', '', '何水', '2014-07-24 15:10:40', 565, '人民网', NULL, '2015-07-21 05:49:00', 0, NULL),
(25, '京杭大运河旅游推广联盟成立', '京杭大运河成功申遗之后，运河沿线的6省18个城“旅游推广联盟”主要包括北京、天津、嘉兴、杭州等城市。杭州旅游委相关负责人表示，沿线城市将推出市场吸引力强的国际旅游专线，提升大运河城市旅游整体形象。', '黄平、金敏丹', '', '何水', '2014-07-24 15:11:08', 692, '经济日报', NULL, '2015-07-21 05:49:00', 0, NULL),
(26, '江苏五项措施做好大运河申遗成功后保护工作', '大运河江苏段全长约700公里，由北向南依次流经江苏省的徐州、宿迁、淮安、扬州、镇江、常州、无锡、苏州8市，沟通微山湖、骆马湖、洪泽湖、高邮湖及太湖，连接淮河、长江两大水系。目前，大运河江苏段是整个京杭大运河中通航条件最好、船舶通过量最大、经济社会效益发挥最为显著的区段。大运河申遗成功之后，江苏省文物工作者非常兴奋，奔走相告。他们表示，申遗成功，是大运河保护和利用的新起点。\r\n江苏省文物局局长刘谨胜说，江苏将从五个方面继续做好大运河保护与利用工作：一是继续发挥江苏省大运河申遗与保护会商小组的作用，不断完善协调管理机制。扬州是大运河联合申遗的牵头城市，申遗成功后，江苏将继续做好这方面的工作，尤其是做好大运河保护和申遗有关市厅际会商小组工作，充分发挥其协调、指导、综合执法的作用，在政策、经费等方面给予继续支持。二是进一步加强大运河保护规划的落实。其一，确保运河遗产保护各项任务落到实处，充分发挥《规划》对大运河保护的规范与引导作用。其二，深入开展大运河沿线重点文物保护抢救、整理和展示工作。其三，加强运河沿线非物质遗产及传承人的保护，有力的传承弘扬江苏优秀的运河文化。通过以上措施，使运河沿线历史文化资源得到有效保护与永续利用。三是完善省、市预警和监测平台和档案中心功能运作，完善常态化管理。在申遗过程中，我省运河沿岸城市探索了一套大运河保护预警机制，并在实践中取得了很好的效果。申遗成功后，江苏将巩固这些成果，并将成果转为了常态化管理，进一步完善档案中心功能。四是保护传承利用好大运河。江苏境内的大运河不仅是一条历史遗存丰富的文化之河，更是一条交通动脉和黄金水道，在全省经济社会发展中一直发挥着重要作用。江苏将不断研究和探索运河遗产的多种保护利用模式，做到既妥善保护遗产又尽量发挥运河各种实用功能，充分发挥运河资源优势和特色，加快发展特色文化产业，真正实现大运河遗产保护在保护、传承、利用、发展和惠民上和谐共赢。五是积极开展大运河保护的宣传工作，进一步增进社会各界对大运河保护工作的理解与支持。同时，通过多种形式的民众参与活动，让保护运河、传承文化、美化家园的理念深入人心。\r\n', '佚名', '', '何水', '2014-07-24 15:11:41', 675, '江苏省文物局', NULL, '2015-07-21 05:49:00', 0, NULL),
(27, '京杭大运河成功申遗促进文学交流', '在京杭大运河成功实现“申遗”之际，有关部门在杭州举办“运河与文学”专题研讨会。以大运河题材创作出小说、诗歌、散文的著名作家到会交流了创作心得。\r\n本次座谈会由中国作协原副主席、浙江省作协名誉主席黄亚洲主持，中国作协副主席何建明、杭州市政协主席叶明等出席会议。何建明说，这次运河与文学专题座谈会，是欢庆京杭大运河申遗成功热潮中的一次生动的文学解读和文化诠释，大运河是我们的母亲河，也是一座文学的宝库，我们的祖先留下了许多伟大的作品。在大运河成为世界遗产的时候，当代作家要有责任和使命，写出新的传世作品。中国作协也将积极搭建运河文学交流的平台，推动后运河文学新的繁荣。黄亚洲表示，从文学的角度深入探讨大运河精神的内容与实质、大运河历史风貌及当代风貌的表现，探讨运河精神与时代精神的关系、运河精神在当代生活中的呈现，从而大力宣传与弘扬新时代的运河精神，进一步推动运河文化的研究。长篇小说《大运河》作者杨义堂、散文随笔集《一个人的运河》作者柯平、诗集《一条大河里的中国》作者张国云、运河诗人龙彼德、大运河申遗办主任、学者王水法等到场探讨大运河的文学渊源。\r\n他们在发言中表示，大运河也是中国长篇小说的母亲河，为长篇小说的诞生和发展提供了丰厚的土壤。明清时期一批生活在大运河畔的作家在宋元话本的基础上，开始写作长篇章回体小说，使明清小说成为继唐诗、宋词、元曲之后的新的文学高峰。《三国演义》作者罗贯中、《水浒传》作者施耐庵都长期生活在杭州，《西游记》的作者吴承恩是江苏淮安人，《金瓶梅》故事的发生地在山东临清，《红楼梦》的作者曹雪芹生于南京，少年时代过着钟鸣鼎食的豪华生活，后来在通州运河边上写成了《红楼梦》。杨义堂的长篇小说《大运河》选取明代永乐年间开通大运河的一段历史，由一个农民担任治河总师，工部尚书宋礼、漕运总督陈瑄、济宁同知潘叔正等带领百姓疏通大运河的悲壮故事，展示了一部壮怀激烈的民族史册，也热情歌颂了一对对运河儿女独特、坚贞、凄美的爱情，刻画了一幅明代《金瓶梅》般的运河风情画卷。杨义堂说，回归中国长篇小说的传统，也是文化自信的表现。\r\n', '佚名', '', '何水', '2014-07-24 15:12:02', 545, '中华工商时报', NULL, '2015-07-21 05:49:00', 0, NULL),
(28, '淮安市历史文化研究会庆祝大运河申遗成功', '7月4日 ，淮安市历史文化研究会举行“庆祝大运河申遗成功报告会”。庆祝大运河申遗成功。\r\n隋唐大运河、京杭大运河和浙东大运河三段合称“中国大运河”申报世界文化遗产名录的，申遗涉及35个市，共有31个遗产片区、58个遗产点，在申遗工作中，淮安是我省第一个交出保护规划的市。中国大运河在淮安共有两个遗产片区、5个遗产点、6000余公顷的遗产缓冲区、3000余公顷的遗产核心区。中国大运河在全国共有7万公顷的遗产区，淮安占了七分之一。\r\n', '佚名', '', '何水', '2014-07-24 15:12:57', 601, '江苏网', 'Home/Images/home/ad.jpg', '2015-07-21 05:49:00', 0, NULL),
(29, '淮安专家：大运河申遗后遗产点保护须原汁原味', '“大运河申遗成功了，有人可能会想在那些遗产点上搞一些开发，比如在遗产点上复原一些新的建筑，以提升观赏性和视觉冲击力。这点是要不得的!开始申报遗产点时，清晏园、惠济祠都报了，最后被筛了出来，就因为不是原始风貌，有后期人为修复、改动的因素。”7月4日，淮安文史界人士聚集一堂，先行开展了大运河遗产保护专题报告会。对大运河遗产点进行原汁原味保护的理念，正被越来越多的人接受。\r\n6月22日以来，人们在兴奋于大运河成功申遗的同时，怎样处理好保护与开发的关系，如何借助大运河遗产这一名片助力淮安发展的探讨一直在进行着。市文广新局副局长李倩在昨日的报告会上说，申遗成功之后，艰巨的任务才真正开始，如何处理世界遗产的保护、传承、开发，是值得思考和解决的问题。李倩介绍说，作为大运河重要节点城市，淮安遗产区面积最大，占据总面积的1/7。但相比扬州、苏州等城市，淮安遗产点大多地处荒郊野外，并呈现散落状态，保护难度更大。“遗产点核心区是肯定不能动的。”李倩说，对世界遗产的保护需要更新理念，应尽最大努力保护遗产点的原有风貌，保护其真实性和完整性。“像古罗马斗兽场，虽然断壁残垣，但恰恰是其价值所在，不能人工修复。”  结合大运河淮安遗产点地处郊外，通达性还不够理想，周边环境还不如人意的状况，李倩认为，做好遗产点周边环境整治，改善通达遗产点的交通状况是现阶段需要做好的选项，另外，还需继续做好大运河历史文化研究工作。\r\n', '江安 ', '', '何水', '2014-07-24 15:13:20', 646, '人民网', NULL, '2015-07-21 05:49:00', 0, NULL),
(30, '杭州将推新大运河两岸景观', '京杭大运河申遗成功后，杭州段的运河两岸将呈现怎样的面貌？这几天《京杭运河杭州段两岸城市景观提升工程规划》方案成果就正在杭州市城市规划展览馆公示并征集意见，下一步将对方案进行最终优化，使之更加体现杭州特色。\r\n此次公示的三套方案各有不同。由上海同济城市规划设计研究院设计的方案A就提出打造“水文化、水城市、水创意”的概念，重点做足“水文章”，引水入街入城入园，将两岸功能缝合串联，加密亲水横向路网，强化两岸跨河联系，串联城市绿轴成网。\r\n由东南大学设计的方案Ｂ则提出“河系千年、城纳百工、人观万象”的概念，强调运河的提升要与城市发展相结合，运河和两岸的人要融入一体，同时严控运河两岸的建筑高度、外观风貌等，解决古今风貌分离的问题。\r\n而在中国城市规划设计研究院上海分院的方案Ｃ中，则对运河两岸的景观提升提出了生态的郊野、活力的都市、静谧的小镇三个理念。\r\n据了解，3个方案在广泛征集意见后将进行最终优化，并增加可操作性。\r\n', '梅璎迪', '', '何水', '2014-07-24 15:13:49', 695, '光明网', NULL, '2015-07-21 05:49:00', 0, NULL),
(31, '山东建“大运河历史文化长廊”', '中国大运河成功入选《世界文化遗产名录》，作为涵盖大运河8个河段的大运河重要组成部分，山东也正在着力推动着大运河山东段“大运河历史文化长廊”的编制建设工作，以此激活运河文化遗产的潜力。\r\n据悉，山东从2013年起开始筹划建设“大运河历史文化长廊”，串联起大运河山东段沿线文化遗产，目前该项目的规划编制工作已经有了初步成果。而随着中国大运河申请世界文化遗产名录的成功，大运河山东段作为中国大运河的重要组成部分，也成为了山东继泰山、“三孔”、齐长城之后的第四处世界文化遗产。\r\n专家考证认为，大运河山东段历史上就是通行条件最困难，维修保护工程技术最复杂、最巧妙的著名河段。会通河水道和水源工程规划，以及水资源调度和管理，代表了中国大运河乃至整个农业社会卓越的水运技术成就。运河沿线的德州、临清、东昌、济宁、台儿庄，成为历史上交通转输和贸易的重镇，积淀了深厚的运河文化，留下了丰富的历史文化遗存。大运河历史文化长廊建设规划将以大运河山东段8段河段、15处遗产点为中心，充分研究运河遗产的文化内涵特点，以创新思路进行编制，以激活运河文化遗产潜力，展示大运河实际情况，尊重历史、保持原貌。\r\n《山东省大运河遗产山东段保护管理办法》作为中国第一部由省级人民政府颁布实施的大运河保护专项政府规章于2013年10月1日正式实施，这是山东省政府继2012年出台《大运河遗产山东段保护规划》后再次以政府规章形式来保护大运河。\r\n“大运河申遗从来不是目的，而是加强保护的手段和途径。”山东省文物局副局长由少平认为，合理利用是对大运河最好的保护，大运河山东段的“大运河历史文化长廊”规划，就是通过遗址公园、博物馆展陈、运河小道、展示平台等各种方式手段，有效提升和展示了大运河遗产的历史、科学、文化价值，打造出汶上南旺分水枢纽考古遗址公园、台儿庄古城、德州南运河段等一批文化遗产保护利用的亮点工程。\r\n据悉，大运河山东段申遗点段，包括南运河德州段、会通河临清段(元运河、小运河)、会通河阳谷段、会通河南旺枢纽段、小汶河、会通河微山段、中河台儿庄段等8段运河15处遗产点，总长186公里，遗产区面积为16603公顷，缓冲区面积29501公顷。申遗遗产点涵盖了各类水工设施遗存，初步再现了传统运河工程的技术发明创造性和技术体系的典范性，是大运河整体申遗最有力的支撑点段之一。\r\n', '杨晓卫', '', '何水', '2014-07-24 15:14:18', 636, '中新网', NULL, '2015-07-21 05:49:00', 0, NULL),
(32, '大运河江苏段水质处于“亚健康”状态', '大运河申遗成功令人振奋，不过，有这样一群大学生则利用自己的专业知识提醒人们，如何进一步保护大运河值得我们深思。来自扬州大学环境学院的20多名同学开始了为大运河江苏段体检的工作，经过对流经7座城市的河流断面进行水样分析，12日，大学生们拿出了手绘的大运河江苏段“体检表”，不过现实情况提醒我们，大运河江苏段水质已处于“亚健康”状态。\r\n记者了解到，为了获得准确的监测数据，所有的水样都必须在当日送到学校的实验室。根据监测结果，徐州解台闸、宿迁项王大桥和淮安运河大桥水质较差，根据7月9日采集水样监测结果评价为Ⅴ类水；扬州市宝应县京杭大桥水质为Ⅳ类水；扬州市广陵大桥和文昌大桥水质一般为Ⅳ类水；镇江西门桥水质一般为Ⅳ类水；常州西瀛里和苏州何山桥水质较差为劣Ⅴ类水。\r\n根据不同断面水质指标分析结果，大学生们手工绘制出了大运河江苏段水质污染状况图，分别标明了3种污染物的含量以及综合标价标准，用由浅至深的颜色表示水质由优至劣。\r\n从评价结果分析可以看出，与江北运河相比，江南运河水质较差。分析报告指出，这与江南运河从北向南的流向也有一定的关系。针对大运河水质污染状况成因调查，大学生们也随机走访了当地居民和专家，根据各地居民反映，水质较差主要是工厂排放的废水和生活污水不合理排放导致的，农村的农药污染和养殖废水的随意排放也是其中部分原因。\r\n', '俟名', '', '何水', '2014-07-24 15:14:50', 536, '新华报业网', NULL, '2015-07-21 05:49:00', 0, NULL),
(33, '“大运河遗产保护办”将挂牌', '中国大运河项目正式列入《世界遗产名录》，成为我省继苏州古典园林、南京明孝陵之后第三处世界文化遗产。昨天下午，省政府在扬州召开江苏省大运河申遗工作总结会议。会上传达了省长李学勇《关于大运河江苏段保护与申遗工作的批示》，副省长、省大运河保护和申遗市厅际会商小组组长曹卫星出席会议并讲话，省政府副秘书长、会商小组副组长肖泉主持。\r\n大运河江苏段列入世界文化遗产的河段长度为325公里，占全长的1/3；遗产区7个，占遗产区总面积的46%；遗产点22处，占总数的40%。江苏的运河遗产资源数量最多、价值最大、类型最全、分布最密，其中扬州是全线列入遗产最多的遗产区。\r\n省长李学勇在批示中强调，各地各有关部门应坚持“保护为主、抢救第一、合理利用、加强管理”的方针，以世界遗产标准做好大运河遗产保护管理各项工作。\r\n曹卫星强调，如何建立长效机制，保护管理好大运河这一活态文化遗产，是亟需面对的首要问题。要高质量做好大运河的保护、管理和利用，共同推动运河沿线经济社会全面协调可持续发展。\r\n作为大运河申遗牵头城市，副市长董玉海说，国家文物局已同意，拟将大运河保护与申遗城市联盟更名为大运河世界遗产保护与管理联盟；将大运河联合申遗办公室更名为大运河遗产保护管理办公室。今年9月举办的第八届世界运河名城博览会，将争取审议通过新的联盟章程，为大运河遗产保护管理办公室挂牌等。同时，我市也将坚持世界遗产标准，精心打造大运河第一城，为沿线城市提供新的示范。\r\n', '陶敏', '', '何水', '2014-07-24 15:15:15', 703, '和讯网', NULL, '2015-07-21 05:49:00', 0, NULL),
(34, '大运河申遗江苏头功', '7月17日，江苏省政府在扬州召开的江苏省大运河申遗工作总结会议上，如何以世界遗产标准保护好运河文化成为关注的焦点，大家达成的一致共识是：有能力成功申遗，更要有能力保护好大运河这条母亲河。\r\n大运河的源头，可以追溯到春秋时期伍子胥为吴王夫差开邗沟，江苏不仅是孕育大运河的摇篮，也是大运河河道路线最长、流经城市最多、运河遗产最丰富、列入大运河申遗点段最多的省份。江苏境内大运河全长690公里，流经徐州、宿迁、淮安、扬州、镇江、常州、无锡和苏州8个地级市；江苏省有7个遗产区、6条河道、22处历史遗存列入运河世界遗产名单，遗产点数量约占全线的40%，位居沿线各省市第一。\r\n在运河沿线省和直辖市中，江苏率先完成保护规划编制、率先制定运河遗产保护地方性法规、率先建成数字管理和监测预警平台，自2010年以来江苏省共投入5000万元用于制定保护规划，并对大运河江苏段沿线重点文物抢救，淮安总督清运公署遗址、扬州重宁寺、宿迁陈家祠堂、常州万安桥、无锡大窑路窑址群和苏州关税司署旧址等遗产点成为各地最亮丽的名片。\r\n国家文物局领导对江苏的大运河申遗工作给予高度评价：江苏是遗产点环境最优美、解说词最具说服力、评估现场最具互动性，同时也是对居民生活干扰最小的省份，如果申遗成功，江苏当居首功。\r\n对运河遗产，什么是最好的保护？专家认为，将其融入城市发展战略，成为地方最优质的文化资源，成为百姓身边的景观和生活的内容，古老的运河才会重现活力。\r\n具体做法是：在保护传承好的前提下，将价值突出，功能完善的遗产点辟为专项展示场所或考古遗址公园、遗址博物馆；以运河遗产为主题，加快发展特色文化产业，实现运河遗产保护与文化创意、娱乐休闲、旅游观光等相关产业发展的良性互动。\r\n常州市则尝试工业旅游模式，“运河五号”创意产业园利用原常州恒源畅厂、常州第五毛纺织厂、常州梳篦厂等工业遗存，加以改造和利用，在运河两岸汇聚一批创意、动漫、摄影、美术、演艺和展览等文化业态，融传统与现代为一炉。\r\n《大运河江苏段保护规划》的编制者、东南大学教授朱光亚表示，申遗成功当然值得高兴，但申遗是手段，保护才是目的，我们要警惕打着保护利用的旗号，对运河遗产过度开发，使之成为地方领导建功立业、实现自己理念的“实验场”。\r\n对此，江苏副省长曹卫星特别指出，省内有些运河沿线城市已经在制定开发和利用计划，但前提是要科学规划，合理利用，切忌一哄而上，盲目开发、过度利用，保护好、传承好运河文化遗产必须被放在第一位。', '王宏伟', '', '何水', '2014-07-24 15:15:46', 564, '人民网', NULL, '2015-07-21 05:49:00', 0, NULL),
(35, '北京将修缮通州运河古建筑群', '北京作为京杭大运河的北端城市和漕运终点，京杭大运河北京段涉及昌平、海淀、西城、东城、朝阳、通州6个区。\r\n北京共有两处河道和两处遗产点入选此次直接申报名单。河道分别为通惠河北京旧城段——西城区什刹海（包括前海、后海、西海）和东城区玉河故道（西起万宁桥，东至东不压桥，长约0.5公里），通惠河通州段（西起永通桥，东至通州通惠河与北运河交叉口，长约5公里）。遗产点分别为西城区澄清上闸（万宁桥）和东城区澄清中闸（东不压桥）。\r\n申遗过程中北京实施了一系列保护和申遗准备工作，昌平区白浮泉遗址、海淀区广源闸、西城区万宁桥、东城区东不压桥遗址、通州区燃灯塔、通运桥等均进行了不同程度的修缮加固。此外，海淀区长河、西城区什刹海、东城区玉河北区、朝阳区通惠河、通州区北运河等均进行了环境整治工作，建成了庆丰公园、大运河森林公园，使大运河遗产本体保护状况和环境风貌得到明显改善。\r\n目前北京已经建立了一个初步的监测平台体系，下一步将尽快完善，在运河沿线确定合适的监测点，对今后从事监测的工作人员进行系统培训。针对大运河活态的、线性的遗产特点，今后将进一步发掘遗产历史文化内涵，开展对通州运河古建筑群修缮等工作，启动东城区玉河南区考古工作及历史景观恢复的研究。特别是将不断改善大运河水质与水环境。\r\n', '水冰晖', '', '何水', '2014-07-24 15:16:17', 642, '中国文化报 ', NULL, '2015-07-21 05:49:00', 0, NULL),
(37, '2014年中国大运河世界遗产保护座谈会发言(张廷皓)', '本次座谈会的《倡议书》征求了多方意见，包括地方政府相关部门的意见，也得到国家文物局和政协领导的大力支持。我就《倡议书》征求意见稿作几点说明：第一，大运河申遗是重大文化系统工程，政协起到了协商民主的作用，要继续发挥其不可替代的作用。第二，建议建立完善大运河世界遗产保护的管理机制。在申遗过程中，管理机制有所创新。孙主席推动了35个城市联盟，这种组织形式非常有效，要继续发挥这种机制的作用。第三，依法保护、依法管理。继大运河保护办法后，推动国务院颁布的大运河文化遗产保护条例。针对当前的问题提出建议，坚持保护规划的问题，加强检测、预警问题。通过与水利史、水利科学研究院商议，提出深入大运河保护的发展战略的研究，这是基础工作，包括了很多研究工作。通过专家领导提出适时启动大运河保护的后续申遗工作，动员全社会的力量，参与大规模的文化遗产保护。《倡议书》的草案还不够成熟，中国文物学会希望各位专家领导提出建议，能够把倡议书改得更好更成熟。', NULL, '张廷皓（全国政协委员，中国文化遗产研究院原院长，中国文物学会、大运河专业委员会会长）', '', '2014-08-13 13:07:39', 741, '2014年中国大运河世界遗产保护座谈会', 'Home/Images/news/active/张廷皓.jpg', '2015-07-21 05:49:00', 0, NULL),
(38, '2014年中国大运河世界遗产保护座谈会发言(李迪华)', '在申遗成功那天，大家都很激动。奔走相告。今大以这种方式庆祝也是很好的一种方式。我认为，有四个问题值得思考。1、大运河如何做到整体保护?大运河是复杂的，落实整体保护是当务之急。2、大运河如何造福地方经济。这个问题在申遗成功后需要密切关注，否则就可能导致反弹。3、如何确保对大运河的持续研究。4、大运河是民族和世界身份证，如何发挥它的教化功能，从而激发文化自觉和认同。我提议以后对考察运河时最好是徒步和骑自行车的方式，因为运河具有无穷魅力，需要我们每个人设身处地体验。关于倡议，我有几点看法：标题是否可改成“大运河保护和可持续利用的倡议”，因为还有一些没进入世界遗产名录的运河段和遗产点，它们也应在保护之列，有关的条例的制定还是要强调“保护和可持续利用”，关于“文脉之河”是否可改为“文明之河”。另外，增加对大运河非物质文化遗产的保护，因为非物质文化遗产也是构成大运河文化遗产的重要组成部分。“爱我运河，护我家园”的口号可以作为一个标题，突出其价值。还有就是现在热议的运河旅游，我倡导每个中国人都应步行或骑车游运河。再就是建议设立使大运河成为大运河基金，支持运河研究者倾力投入研究工作。还要加强运河解说系统的编制和解释，避免出现用世俗的言词去解释我们民族文化之魂，解说必须彰显大运河文化无穷的魅力。', NULL, '李迪华（北京大学景观设计学研究院副院长）', '', '2014-08-13 13:08:55', 776, '2014年中国大运河世界遗产保护座谈会', 'Home/Images/news/active/李迪华.jpg', '2015-07-21 05:49:00', 0, NULL),
(39, '2014年中国大运河世界遗产保护座谈会发言(谭徐明)', '大运河申遗对水利史的科学研究起了很大的推动作用。可以说，若不是申遗，我们就不会对祖国江河的深刻理解。申遗使我们既强化了水利专业知识的学习，同时又扩展了社会科学的知识领域。因为申遗过程中存在着诸多矛盾，各部门的价值诉求各不相同，这就需要应用社会科学知识去协调、去解决矛盾，要正确处理好各方面的关系。毕竟，大运河是我们民族身份的一个象征。水利、文物、交通等部门都要从国家和民族历史的高度去看待这条河，所以，申遗成功之后涉及到运河保护的突出问题时，就不能再自说自话，目前的头等大事是建立和健全管理机制。设立一个权威机构，代表国家行使对大运河的管理职能。可以向先进国家如美国学习，突破部门与行业的束缚。实施跨行业跨部门的统筹管理。其次是国家财政要给予支持，没有财政支持就很难有统一的管理，比如要维护河道形态，就必须有专项的财政支持。同样实施保护也是如此。再就是要建立综合性的监测系统，水质、文物、航运等各方面的检测要形成一个完整的体系，不可偏废。而在制定规划和谋求发展时，都要考虑可持续性和可操作性。最后就是关于后续的申遗工作仍需开展，因为现在仍有许多标志性的工程和遗址在世界遗产名录之外。所以我们应马上开展工作，制定计划和实施方案，最终使大运河的完整性得到展现。 ', NULL, '谭徐明(大运河专业委员会副会长，中国水利学会水利史学会会长）', '', '2014-08-13 13:09:21', 702, '2014年中国大运河世界遗产保护座谈会', 'Home/Images/news/active/谭徐明.jpg', '2015-07-21 05:49:00', 0, NULL),
(40, '2014年中国大运河世界遗产保护座谈会发言(李道国)', '作为一名大学教师，长期从事艺术设计、文化产业策划的教学、研究工作。同时，兼任《中国文化报大运河文化专刊》副主编。近三年来，伴随着运河城市联合申遗的步伐，专刊紧密配合国家申遗战略，传播大运河申遗新闻、解读申遗过程中出台的各项重要举措，客观反映、评价大运河申遗过程中的现状，得到了运河城市政府、工程技术人员、管理者、申遗专家、学者的大力支持。\r\n读了座谈会倡议的第七条“广泛动员社会力量参与大运河保护和管理”，我深有感触。这些年大运河文化专刊的工作，正是全民“爱我运河，护我家园”的真实写照。民众参与、学术群体参与、企业家参与、媒体参与，就能形成一股强大的社会正能量，这是大运河世界遗产保护的坚实基础。我个人认为在“加强中国大运河世界遗产保护的倡议”中要协调好以下几种关系：\r\n国家意志、学者建言、政府决策与地方发展规划的协同性关系；大运河世界遗产保护法规、管理机制的长效性与短期利益的平衡性关系；大运河世界遗产代表性段落、遗址与未包括在本次申遗中的其他段落、遗址点共同保护的共有性关系；大运河世界遗产的价值取向的多元性与遗产保护的原则性关系；大运河城市发展理念与大运河世界遗产保护意识的失衡，在发展与保护的双重决择中所形成的需求性关系；遗产保护现状的普遍性与个别性、管理标准的统一性与具体对象的特殊性关系。\r\n我们认为，在大运河世界遗产的保护和发展中，至少在以下诸方面可以有所作为，从而去应对更大的挑战。\r\n1、大运河城市经济发展状况不同，地区政府在遗产保护经费投入上，存在较大的差异性，而且保护经费来源单一，需要以政府资金为主导、民间资本为补充，构成保护经费的多元互补性关系，缓解或释放政府压力。\r\n2、大运河世界遗产保护是一个涉及到各个学科门类，其学科的多样性、交叉性、综合性、整体性为我们面向问题、解决问题构架了复杂性关系，避免顾此失彼，在学科的深层性结构、制度性的建设上保持高度的一致性，调和其中的矛盾，需要我们高度关注。\r\n3、大运河世界遗产作为一种活态文化，在对待历史遗产的认知上、保护模式上，形成一种解构、建构与重构的关系。面向当代、面向世界文化语境，探索一条可持续发展的道路，是我们当代人必须直面的时代命题。其中，需要我们在充分尊重、保护我们的文化传承基础上，探寻创造性的转化，解构、诠释历史文化，建构、滋养现世文化，重构当代乃至未来的时代文化。传承、保护、发展是大运河世界遗产保护的主旋律。\r\n4、大运河世界遗产的科学利用，必然造福于民生，促进运河两岸文化与经济发展。将世界遗产保护与当代文化旅游、当代区域经济发展、当代生产方式的创新发展有机地结合起来。\r\n总之，我们需要一代一代坚持不懈地探索下去。作为媒体我们将一如继往地做好大运河文化研究、大运河世界遗产保护宣传工作。\r\n', NULL, '李道国（江南大学教授  大运河文化研究院秘书长）', '', '2014-08-13 13:10:40', 762, '2014年中国大运河世界遗产保护座谈会', 'Home/Images/news/active/李道国.jpg', '2015-07-21 05:49:00', 0, NULL),
(41, '2014年中国大运河世界遗产保护座谈会发言(杜晓帆)', '世界遗产很重要，这个概念在当代的影响巨大。但我们是否有必要把它放到一个至高无上的地位，因为世界遗产概念产生于20世纪70年代，当时它只是一个项目，但随后才在世界各国产生了广泛影响。我觉得全国文物保护单位的意义可以高于世界遗产，在运河申遗前，国家经过各方统筹协调，共谋世界遗产，取得了重大成果，这是可喜可贺的。但申遗成功后的保护问题是突出的。它是否应该与经济发展、水利事业紧密结合，申遗成功毕竟是一个契机，我认为应该认真研究。运河沿线的经济发展是不平衡的，这种不平衡就带来了保护方法、态度和管理上的不平衡。世界遗产将来能带来什么，政府肯定有压力。因此，把世界遗产与经济发展结合起来必将带来新的希望。我们如果理清大运河的核心价值，可能会更好促进大运河的保护。另外，关于大运河的管理模式，我想不应该是单一的模式。而是应该分类保护，针对具体问题作具体分析。从专业上，学术上深入研究，让世界遗产真正使百姓受益，起到实际的作用。', NULL, '杜晓帆（联合国教科文组织驻华代表处文化官员）', '', '2014-08-13 13:11:58', 716, '2014年中国大运河世界遗产保护座谈会', 'Home/Images/news/active/杜晓帆.jpg', '2015-07-21 05:49:00', 0, NULL),
(42, '2014年中国大运河世界遗产保护座谈会发言(朱炳仁)', '大运河遗产保护已进入到后申遗时代，我认为一方面应该提出新的保护理念。因此，就应该考虑发展的问题，发展是高级的保护，没有发展就不是活态的运河，要提出运河遗产的发展工程，沿线的文化因素要不断充实。我们应该找到推动运河文化发展的社会主体。第二方面，对运河文化中的非物质文化遗产加以重点保护。运河沿岸的非遗非常丰富，是沿岸社会经济发展的标示。众多的非物质文化遗产实际上存留着运河文化的历史记忆，为我们追寻大运河的文脉提供了重要的依据。所以，在保护大运河的同时，对这些非物质文化遗产也细心呵护，其重要意义是不言而喻的。', NULL, '朱炳仁（中国工艺美术大师、中国铜装饰艺术大师）', '', '2014-08-13 13:12:27', 685, '2014年中国大运河世界遗产保护座谈会', 'Home/Images/news/active/朱炳仁.jpg', '2015-07-21 05:49:00', 0, NULL),
(43, '2014年中国大运河世界遗产保护座谈会发言(靳怀堾)', '今年6月，从卡塔尔的多哈传来振奋人心的消息，中国大运河申遗成功。我们在衷心祝福大运河永远“好运”的同时，还要认真面对大运河“后申遗”时代的问题，做好大运河遗产保护与利用的文章。\r\n清醒认识大运河保护与利用存在的问题。大运河被列为全国文保单位和“申遗”对象以来，从中央到地方各级政府都在人财物上加大了对大运河保护的力度。目前大运河虽然申遗成功，保护的任务更加繁重，而且，大运河文化遗产保护与利用的矛盾愈加突出。1、自清末漕运中止后，大运河随之失去了国家层面的统一管理和维护，地方分条管理，水利、航运等部门分块管理，条条分割，条块分割，各行其是。尤其是大运河申遗成功后，保护与开发利用的“刚性”矛盾愈加凸显。比如，文物部门强调保护第一、抢救第一，要求不改变运河原状；水利部门则强调功能第一、安全第一，要求对运河进行必要的维修和治理。2、建设性开发破坏严重。除了因功能丧失导致部分运河改道、废弃以及毁旧建新外，一些河段在“文化搭台、经济唱戏”名义下遭到“建设性破坏”，如部分穿越城市的运河被肆意改造，成为观赏性十足的“景观河”，严重影响了大运河历史风貌的真实性和完整性。3、些河段生态环境恶劣，污水横流、垃圾遍布，满目疮痍。如黄河以北运河段，要么干涸，要么有水皆污。4、运河的基础研究不够。如对运河的重大问题研究不够，一些提法有待商榷；一些关键节点考古发掘、文化内涵、科技元素、施工工艺等研究不够；关于运河的历史信息资料不全甚至错谬，等等。5、宣传展示不到位。如宣传方式单一，缺乏有震撼力、影响力的运河“作品”；展示中对运河遗产水利科技元素的忽视或说不清楚。6、运河遗产保护投入不足。大运河遗产保护经费不足，除了中央投入外，地方政府的投入有限。\r\n做好大运河保护与利用的顶层设计。“后申遗”时代，要从根本上解决运河保护与利用的矛盾和问题，必须走改革创新之路，尤其是要搞好顶层设计。1、依法保护与开发利用大运河。2、尽快编制出台《大运河文化遗产的保护与开发利用规划导则》，“导则”要针对不同的运河段，因地制宜、有的放矢地提出保护与开发利用的思路、原则和技术路径、控制性指标等。3、建立高层次的大运河保护与管理协调领导机构。4、加强政策和资金支持，解决大运河管护经费不足等问题。可以考虑建立大运河保护与开发利用开发基金，在政府主导的前提下，鼓励民间社会资本介入。5、加强大运河的基础研究和普及宣传工作。研究方面，国家应在自然基金和社科基金中列出专项，支持大运河的基础研究，为运河遗产的保护与利用提供重要支撑。宣传方面，要在普及运河知识、宣传运河价值上下功夫，增强国人“爱我运河、护我运河”的意识，让世界更好地了解中国大运河，了解中国古代水利文明。\r\n大运河保护与利用应遵循的原则。1、统筹兼顾。鉴于大运河是一个线性、活化、可再用的文化遗产，必须按照统筹兼顾的原则，妥善处理好保护与在用的关系、保护与治理的关系、遗产管理与水利管理的关系、文化功能与水利功能的关系等。一方面，大运河作为文化遗产，保护优先、强化保护是必须的、责无旁贷的，绝不能以“使用”的名义而毁掉祖先留给我们的宝贵财富；另一方面，大运河作为活态、再用的文化遗产，也不能因为需要“保护”而放弃对它的利用，影响它的使用价值发挥。2、扰动最小。扰动最小化，即在加强运河文化遗产的保护的前提下，加以科学、适度的可持续利用，绝不能在运河河道及堤防上大兴土木、大动干戈，随意破坏运河的真实性、完整性和持续性。比如，过去在运河整治中，某些河段出现了“三化”即河道平面形态直线化、河道断面几何化、护岸材料硬质化的问题，再比如在运河两岸大量修建旅游服务设施，这些无疑是对运河文化遗产“建设性的破坏”，需要紧急刹车。鉴于运河是“活态文化遗产”的特殊性，对运河河道的治理，更要强调遵循生态治河的理念，尽力保持运河河道堤防的原始风貌。要大力提倡采取传统的河工构建和施工艺加固堤防、稳定河势，并使滩区自然落淤，恢复河岸河滩特有的生态环境和历史景观。3、突出重点。大运河既然是文化遗产，对其加以全面保护是十分必要的。但是，运河工程历史这样久远，规模这样宏大，区域这样广泛，涉及的地区和部门这样多，加之本身又是线性、活态的可再用的文化遗产，因此保护工作的长期性、复杂性、艰巨性可想而知。这就需要本着实事求是的态度，既注重全面保护，又强调突出重点，即对那些具有重要历史、文化和科技价值的运河文化遗存，特别是列入申遗名录的节点，必须强化保护，避免自然和人为的破坏。4、改善生态。大运河作为中华文明的象征，其生态环境好坏不仅关系到运河文化遗产的保护与申遗，也关系到运河自身的生命健康，还影响到运河沿岸的生态和人文环境。因此，要切实建立健全运河生态环境保护体系，努力打造生态运河、美丽运河、文化运河。当务之急：优化配置水水资源，让干涸的运河段“复活”；大力治理水污染，改善水环境，让运河成为清水廊道；大力治理运河周边的环境，拆除运河堤、滩上的违章建筑，禁止向运河中乱倒垃圾杂物，保持堤防和其他水工设施的完整、清洁。\r\n', NULL, '靳怀堾（水利部海委漳卫南局副局长，中华水文化专家委员会副主任委员，中国水利作家协会副主席）', '', '2014-08-13 13:21:26', 691, '2014年中国大运河世界遗产保护座谈会', 'Home/Images/news/active/\\靳怀堾.jpg', '2015-07-21 05:49:00', 0, NULL),
(44, '2014年中国大运河世界遗产保护座谈会发言(顾浩)', '在中国文明史上，司马迁第一个提出了“水利”一词。水利与中华文明的进步密切相关。涉及到水利的遗产很多，都江堰、哈尼梯田、苏州园林、丽江等历史遗产都与水相关。在反映深厚文化积淀的时候，水文化的意义就彰显出来。怎么看大运河？我认为它是重大的战略性水利工程。它类似于现代的三峡水利工程，它是为国家的统一战略服务的。强国出大气，而大气才能出强国。隋炀帝动用大量人力物力贯通运河是有大气的。盛唐的出现是享受了隋代的福利。大运河具有潜在的效应，要使其长期存在，必须满足需要和符合自然规律。大运河申遗成功，但挑战仍在。首先是要保护大运河河道的形态存在。其次是必须保证生态系统的连续性和水质的良好。所以，运河北方段要尽快修复，对脏乱现象要严加整治，要把科学发展与严密保护相结合。水利发展要有利于大运河的保护，要持续和谐发展。我建议：1、继续宣传和树立保护观念，这是一个联系过去和历史的纽带。所以要使全社会形成自觉保护大运河的理念。2、机制上是依法保护。管理条例的设计要科学，因为大运河是活态的，现在出现新问题，就需要新的对策。水利、文物、航运等应多方面结合，进行周密的设计。3、充分发挥规划的作用。当保护和利用有冲突的时候，地方政府如何正确发挥作用，是要统一规划的，千万不要急功近利，不要简单地讲开发利用。规划要强调科学性，找准方向。我们要有国家层面上的规划指导意见。综合各方面的因素，做好保护和发展相结合。在规划过程，要明晰各个领域的底线是什么。文物、水利、交通、城建等各部门要明确核心部分的底线，有一个缓冲区，从而保证大运河世界遗产的持续有序发展。', NULL, '顾浩（中国水利学会常务副理事长，水利部办公厅原主任）', '', '2014-08-13 14:01:51', 680, '2014年中国大运河世界遗产保护座谈会', 'Home/Images/news/active/顾浩.jpg', '2015-07-21 05:49:00', 0, NULL),
(47, '2014年中国大运河世界遗产保护座谈会发言(郭涛)', '大运河申遗成功是实至名归、当之无愧。为世界文化遗产又树立了一座丰碑。现在的当务之急就是制定大运河保护条例。对大运河的保护，很显然就如孙主席所说的：“保护做得如何，关键在于我们的认识水平如何？我们对大运河价值的认识不到位，我们的保护肯定也不到位，我们的宣传工作肯定也做不到位。”所以从这个意义上讲，要做好大运河保护的前提是继续做好对大运河的基础研究。\r\n大运河文化遗产的核心价值是什么？我认为，它是中国古代水利文明的载体。由于有了这个工程，才有运河城市的兴盛和发展，才有大运河的文化。这样就应把这一水利工程的成就研究透彻了，犹如都江堰水利工程一样。对大运河水利工程的代表杰作要充分发掘，从科技探索其价值。第二个方面是机制问题。传承、教化、使用是大运河的功能，这些功能的发挥要加化管理机制的作用，对保护对象进行分类管理，有部分不用就用管文物的办法，而对活态的部分则要区别对待。还有就是对不同区段明确责任。最后就是加强宣传。\r\n', NULL, '郭涛（中国建筑工程总公司原党组书记，十一届全国政协委员）', '', '2014-08-13 14:02:50', 676, '2014年中国大运河世界遗产保护座谈会', 'Home/Images/news/active/郭涛.jpg', '2015-07-21 05:49:00', 0, NULL),
(57, '江苏将举办运河旅游节', '江苏省政府日前下发的《关于全面构建“畅游江苏”体系促进旅游业改革发展的实施意见》中透露，江苏将以京杭大运河申遗成功为契机，大力发展运河沿线旅游，打造世界级运河旅游产品，并创办运河旅游节。\r\n据悉，大运河在江苏段最长，约700公里，沿途文化遗存最多、保存状况最好、利用率也最高。目前，大运河江苏段仍然是一条黄金水道，年运输量超过10条铁路。这次申遗江苏列入申遗点段的河道有6段，历史遗存22处，比重约为40%。\r\n　　据了解，江苏将以沿运河8市为节点，将宿迁、淮安、扬州、常州、无锡、苏州、镇江、徐州8市的旅游“串点成线”。加快大运河沿线风景的开发建设，适当开通运河水上旅游专线。\r\n　　值得一提的是，这是首次提出以江苏省的名义举办运河旅游节。不过，运河旅游节的具体名称、举办频率还没有敲定。\r\n　　首届江苏运河旅游节会定在哪天呢？对此，江苏省旅游局有关人士表示，此前考虑过选大运河申遗成功这个日子，但是还没最终确定。不过，他们表示，欢迎市民参与讨论。\r\n市民可以在江苏旅游政务网上给他们留言，或是在新浪微博上@江苏微旅游提意见。至于举办地点，该人士表示也没有定，有可能8市联动，并设一个主会场。\r\n', '刘伟娟  项凤华', NULL, '何水', '2014-09-01 15:30:53', 156, '人民网', NULL, '2015-07-21 05:49:00', 0, NULL),
(48, '2014年中国大运河世界遗产保护座谈会发言(安家瑶)', '对于大运河申遗成功有点出乎意料，从完整性上看有些缺憾。但毕竟成功了，让人高兴。现在看大运河，申遗前后变化确实很大。经过申遗，让人高兴的是，运河两岸的百姓对运河更加热爱，对家乡由衷感到自豪。在申遗过程中有许多考古发现，也保护了一批遗址。在申遗之后，我们的责任就更大了，道路就更长了，因为还有2/3的运河没有在申遗名单内，需要增扩，让这次没有进入名录的地方积极推进保护。要有一套对其保护和管理的办法。毕竟它们是国家文物保护单位。在涉及到城市建设与遗产保护的问题上，媒体要大力宣传，这是一件任道重远的事业。另外，人大、政协在制定法规条例时要充分发挥作用，确保大运河世界遗产可持续发展能在法律的保护下运行。', NULL, '安家瑶（中央文史研究馆馆员，九、十、十一届全国政协委员）', '', '2014-08-13 14:03:50', 700, '2014年中国大运河世界遗产保护座谈会', 'Home/Images/news/active/安家瑶.jpg', '2015-07-21 05:49:00', 0, NULL),
(49, '2014年中国大运河世界遗产保护座谈会发言(刘庆柱)', '大运河申遗成功之后要明确责任和权力问题。申遗是国家行为，保护也是国家责任。政府是第一责任人。这是必须要明确。现在面临的操作问题，既然是法制社会就必须有法规。大运河的复杂程度比长城更大。所以必须制定法规。文化产业不宜把文化商业化，文化有产业，但产业与事业是不同的。操作上有两个层面：第一是组织机构，申遗有申遗办，但现在申遗后必须有更有权威的机构，需要权力机构支撑。它不应该是松散的俱乐部。大运河是实实在在的，就要有实在的机构。运河只有一条，是由人大还是国务院出台法规，应早明确。运河是中国多民族核心文化的载体，所以组织建立严密机构，保护意义重大。在运河问题上，不是各部门协调的问题，而是在组织上更加明确，必须一针见血，抓住问题的实质。', NULL, '刘庆柱（中国社科院学部委员、考古研究所原所长，十、十一届全国政协委员）', '', '2014-08-13 14:04:59', 643, '2014年中国大运河世界遗产保护座谈会', 'Home/Images/news/active/刘庆柱.jpg', '2015-07-21 05:49:00', 0, NULL),
(50, '2014年中国大运河世界遗产保护座谈会发言(谢辰生)', '我觉得国家文物局和各级政府需要警惕两个倾向。一个是旅游，搞得不好就是旅游风暴，导致大拆大建，破坏古迹。第二是文化产业。目前对文化产业没有很准确的认识。有时候反而好心办坏事。比如敦煌的月牙泉，泉在流沙中，干旱不枯竭，风吹沙不落，蔚为奇观，是自然形成的独特的山泉地貌。由于文化产业建设，在此风景区建立了中华回乡文化园，破坏了原来的自然生态，等二三十年之后，月牙泉就极有可能消失。所以，我们要警惕这两个倾向。', NULL, '谢辰生（中国文物学会名誉会长）', '', '2014-08-13 14:05:30', 661, '2014年中国大运河世界遗产保护座谈会', 'Home/Images/news/active/谢辰生.jpg', '2015-07-21 05:49:00', 0, NULL),
(51, '2014年中国大运河世界遗产保护座谈会发言(彭卿云)', '长城与大运河是古代的中国奇迹，是中华民族具有形象地位的工程，建议在倡议书中把《大运河的保护条例》纳入其中，由国务院正式公布。针对倡议内容，有两点可以推敲。第三条，“成为经济之河，文脉之河，生态之河。”建议把文脉之河放在前面，因为保护遗产的重点在文脉。第七条的“爱我运河，护我家园”的口号，是否可以“爱我中华，保我运河”，这样就把大运河与中华文明联系起来了。申遗成功，个人认为，在当代具有重大意义。其中最有价值的一点还在颠覆历史上被人认为“中国人不会保护古迹”的偏见。大运河申遗成功，有力回击了这一谬论。同时扩大了我们保护历史、保护古迹的视野。', NULL, '彭卿云（中国文物学会名誉会长）', '', '2014-08-13 14:06:48', 753, '2014年中国大运河世界遗产保护座谈会', 'Home/Images/news/active/彭卿云.jpg', '2015-07-21 05:49:00', 0, NULL),
(52, '2014年中国大运河世界遗产保护座谈会发言(韦建桦)', '大运河申遗成功，令人欣喜与自豪。同时，我们又十分清醒与冷静。申遗成功为我们研究、宣传和保护、利用大运河提供了巨大动力和宝贵契机，同时也使我们更加直接、更加紧迫地面对一系列挑战和压力。全国政协委员、文物学家张廷皓同志说过，在我国现有的47项文化遗产中，大运河的保护是难度最大的。申遗成功当天，考古学家刘庆柱同志就对《人民政协报》记者说：“申遗成功不是终点，而是新起点” 。这句话反映了强烈的责任意识和使命意识，体现了严谨的科学态度和历史眼光，成为申遗成功第二天报纸上的醒目标题，同时也表达了所有关心和热爱大运河的各界人士共同的心声。\r\n新的起点要求我们的遗产保护确立新的理念和新的思路，采取新的步骤和新的举措；而这就需要我们具有更加鲜明的问题意识和忧患意识，一方面要总结和提炼迄今为止行之有效的经验，另一方面要揭示和分析各种直接妨碍或间接影响大运河遗产保护的隐患与疏漏，包括认识不够统一、论证不够周密、规划的不够科学、法规的不够完善、机制不够完善、措施不够落实，以及人力、物力、财力保障不够到位等问题。这些问题尽管只是散见于某些领域的局部问题，但对运河遗产的整体保护具有消极影响。针对这些问题，我进行了思考，并提下列七点建议：\r\n1、建议国务院成立统筹运河全线保护工作的协调机构，由文物、水利、交通、农业、科技、环保和国土资源等部门联合组成，制定近期和长远规划，编制运河保护指标体系，对各地进行严格的监督检查，定期进行会商，并向全社会公布运河保护的情况、问题和解决办法。\r\n2、建议中央政府明确要求运河沿线的两个直辖市、六个省以及下属的二十五个地级市政府在各自管辖的区域内承担起全面保护和妥善利用大运河的责任。各级政府必须切实履行职责，对所辖地段存在的问题进行排查，提出整治方案，并保证在确定的期限内实现自己的承诺。\r\n3、建议国家文物局进一步强化运河沿线城市在遗产保护中的联合行动机制。在申遗过程中，国家文物局在调动和发挥地方积极性方面做了大量的工作，专门成立了“大运河联合申报世界文化遗产办公室”，并责成江苏省扬州市作为牵头单位承担办公室具体工作。目前，这个办公室已经完成申遗阶段的任务，并在这个过程中创造了经验，培养了人才，积累了资料，形成了有效的联络协调机制。建议保留并充实这个机构，让它承担起促进大运河联合保护、管理、监督和宣传的职能，以保证各项工作有效推进。\r\n4、建议制定和完善大运河遗产保护的专门法规和具体条例。鉴于运河保护工作的特殊性和复杂性，我希望人大与相关部门在调查研究的基础上，尽快制定涉及运河沿线文化遗址保护、生态环境保护、历史风貌保护的法规条例。实践证明，“有法可依、有法必依”是遗产保护工作顺利实施的基本前提。\r\n5、建议启动大运河的系统研究工程，集中历史学、考古学、水利学、地质学、经济史、科技史、文化史和艺术史等领域的专家学者，围绕大运河在经济、政治、军事、民族凝聚、国家统一等方面的重要价值以及大运河的深厚历史文化内涵，设置研究课题，列入国家社会科学基金重大项目，并以大运河历史文献综录和研究系列丛书等形式，向国内外推介研究成果。这样的系统研究不仅有助于我们进一步认识祖国的悠久历史，有助于各国人民了解中华民族的灿烂文明，而且可以为保护和利用大运河的科学决策提供可靠的历史依据和学术支撑。\r\n6、建议高度重视和充分发挥人民政协在运河遗产保护中的重要作用。在申遗工作中，全国政协以及运河沿线各省市政协以其独特优势和不懈努力做出了卓越贡献，这是有目共睹的。申遗成功后，人民政协应当在新的历史起点上再接再厉，进一步凝聚各界专家的智慧，增进各地各部门的联系，不断增强共识，为运河遗产保护利用工作做出新的贡献。\r\n7、建议进一步广泛宣传大运河历史文化遗产的科学价值和现实意义。大运河是一部流动的历史，是进行历史唯物主义教育、爱国主义教育和社会主义核心价值观教育的生动教材。我们应当动员各领域的专家、学者和新闻、文艺工作者在深入研究的基础上，以各种通俗易懂、活泼生动的形式，向人民群众讲述动人心弦的运河故事，让他们从运河的历史记忆和沧桑巨变中认识中华民族的发展轨迹和光辉未来，从而增强自信，振奋精神，凝聚力量，为实现中国梦而努力奋斗，同时进一步热情关注和积极支持运河遗产保护的重要工程。\r\n老子说过：“上善若水。”大运河恰恰是以水为物质载体的独特的世界历史文化遗产。孕育生命的水，泽被万物的水，是大运河的主体和灵魂，也是我们民族精神的象征。愿我们的工作也像运河的水那样奔流不息，永续前进；只有锲而不舍，持之以恒，运河的保护工作才有持久的成效，我们才能在世界遗产研究和保护领域创造崭新的业绩，我们这一代人才能真正无愧于前人，有功于后代。\r\n', NULL, '韦建桦（全国政协常委，中央编译局原局长）', '', '2014-08-13 14:07:20', 731, '2014年中国大运河世界遗产保护座谈会', 'Home/Images/news/active/韦建桦.jpg', '2015-07-21 05:49:00', 0, NULL),
(53, '2014年中国大运河世界遗产保护座谈会发言(郭旃)', '这次中国大运河被列入世界遗产，它的意义和价值到底在哪里？它是中国历史上最伟大的水利工程。它的建成对两岸人民产生的深远影响。它穿越了2000多年，体现了中国人民的传统和信仰。世界遗产委员会对信仰的要求和突破。一个民族、一个国家在遗产保护上的伟大事件。\r\n我们成功的经验。1、我们的遗产必须用国际语言加以阐述，让国际组织的赞同。中国文化遗产院在国际语境下成功地描述了对大运河的认识。大运河申遗成功证明了其意义和价值得到国际组织的赞同。联合申报过程中，统一的协调机构是保障，形成申遗联盟，成立了扬州的申遗办公室，这是功不可没的，联盟和机构的合作需要常态化和持续化。2、各个部门的协调机构发挥了很大的作用。面对这样一个复杂的系统工程，各方求同存异，以博大的胸怀为申遗工作尽心倾力、保证了申遗过程的顺利展开。\r\n当然，我们该清醒地面对申遗后存在的问题。这次进入世界遗产名录的运河段是1011多公里，在完整性上有缺憾。要认识到整体的运河对人类的意义，而不仅仅只是申报成功的这些点、段。目前在保护和管理存在着一些问题。在大运河的解读和展示上也还不够。虽然存在问题，但是世界遗产委员会考虑到中国大运河同历史传统和国家信仰联系紧密，所以还是顺利通过。但是必须清醒意识到，在这种认同中更多地包含一种鼓励和鞭策。申遗成功以后。另外，旅游前景很好，但是旅游不能破坏遗产，需要继续关心和完善，保证大运河可持续发展。再者，是制定保护条例方面。大运河是全国文保单位，当时在制定法规的时候，运河包括的要素和因素还很模糊，所以需要进一步明确和细化。在大运河的保护和管理上我们希望得到各界的批评，这样才能改正，得到关注，这也是文化遗产效益的一个体现。媒体和国际的关注是对大运河可持续发展一个很好的促进。\r\n文化遗产是对民族身份的认知和历史的认知。运河申遗成功是提升中华民族的自豪感、自信心，增强凝聚力的最好的一次机会，这比空洞的爱国教育更有效。虽然我们的世界遗产数量位居世界第二，但是大运河包含了27条河段、58个遗产点，如果这些分开申报的话，我们将遥遥领先。长城、运河、丝绸之路，我们申报了整个中国和半个世界，这么重要的成就，我建议要把这次申遗成功作为一个特殊的案例，大力宣传。\r\n', NULL, '郭旃（国际古迹遗址理事会副主席）', '', '2014-08-13 14:08:23', 788, '2014年中国大运河世界遗产保护座谈会', 'Home/Images/news/active/郭旃.jpg', '2015-07-21 05:49:00', 0, NULL),
(54, '2014年中国大运河世界遗产保护座谈会发言(顾风)', '经过8年的努力，代表国家和民族身份的中国大运河终于得到了充分的肯定和展示。可以明确的是，这次申遗成功，至少在6个方面得到了巨大收获。1、我们保护文化遗产的宣传教育日益广泛深入。2、一大批遗产点得到修缮。3、运河环境得到了整治。4、培养了一批申遗人才和专业骨干。5、创作性地创建了跨部门、多城市合作的联动机制。6、保护文化遗产的实践水平和能力提升了一个台阶。这一系列的收获必将转化为我们民族的物质和精神财富。\r\n在申遗过程中，全国政协委员们的推动起到了重要作用。他们的倡议、提案、考察等作为功不可没。在全国政协的带领下，地方政协也积极推动，把协商民主的功能充分发挥出来，共同促进申遗工作的展开。大运河联合申遗办成立以后，我们感受到上级领导对申遗工作的慎密指导和积极推动。在整个申遗过程中，申遗办至少在以下方面为申遗成功作出了贡献。1、在申遗的宣传、规划、制定文本等方面做了一系列工作。扬州作为运河城市召开连续的高层论坛。接待了各方媒体和不同团队。不遗余力地推广和宣传大运河。还组织志愿者活动，使大运河申遗和民众参与密切结合。另外，拍摄了《中国大运河》的系列电视片。2、督查和推进工作的开展。在各地进行问题的收集，经验的推广，理念的指导，签署了联合申遗协定。3、在示范引领作用的体现上，扬州起到了应有的作用，得到运河沿岸城市的认可。建设了大运河扬州段的检测中心。探索大运河检测机制的建设。在世遗专家考察扬州的过程中，扬州得到高度的评价。组织“大地与水，文化景观”的研讨会以及各种国际研讨会，为大运河申遗做了大量的学术研究的准备工作。春秋两季的活动开展工作，为大运河申遗得以顺利展开，创造了有利的条件。我们以诚恳踏实的努力赢得各方面的信赖，不辱使命，完成了国家所给予的嘱托。今后，将继续与兄弟城市携手发挥我们的作用。\r\n', NULL, '顾风（大运河专业委员会副会长，大运河联合申遗办公室原主任)', '', '2014-08-13 14:09:49', 806, '2014年中国大运河世界遗产保护座谈会', 'Home/Images/news/active/顾风.jpg', '2015-07-21 05:49:00', 0, NULL),
(55, '2014年中国大运河世界遗产保护座谈会发言(刘曙光)', '众所周知，一项文化遗产列入世界遗产名录的要求包括“价值”、“真实性与完整性”以及“保护管理”三个方面。大运河的列入，意味着在申遗阶段我们所建立的保护管理机制和相关规划，已经获得了国际组织和同行的认可。\r\n换句话说，我们卧薪尝胆、艰苦奋斗这么多年围绕大运河保护而产生的诸多具有开创性的成果，已经从理论上、理念上、策略上、政策上回答了如何保护管理好大运河的问题，并且得到了广泛的认可和一部分成功实践的检验。这包括大运河的“整体保护、部分申遗”策略；分阶段编制的多层次的大运河遗产保护规划体系；国家-省-市县各层级的会商和协同保护机制；专门设立的专家委员会咨询制度；以及覆盖大运河全线的遗产监测和档案体系等等。\r\n但当真的列入世界遗产之后，大家还是有莫大的压力、众多的困惑、不尽的担忧。究其根本，我想，不是因为我们不知道应该如何做，而是担心做不到，也就是如何落实政策、实施规划的问题。\r\n从这个角度来看，进一步完善、加强大运河遗产监测工作既是可行之道、也是优选之道。我们说，世界文化遗产监测不是为应对国际组织而作表面文章，而是根植于自身需求的、支持有效保护、科学管理的必要工具——这对规模巨大、利益相关者众多、管理情况复杂的大运河而言，尤其贴切。\r\n目前，大运河遗产监测系统覆盖了31个遗产区，涉及监测工作机构41处，工作人员共 727人（包括巡查管理、监测信息系统管理、档案管理人员，但不含监测协作单位人员）。中国大运河监测与档案中心设在文研院，建立了国家总平台，各遗产区都设有相应的遗产地平台。共计1个国家总平台和31个遗产地平台。这是由大运河保护和申遗省部际会商小组会议决定，从国家层面推动建设的遗产监测系统，在全世界都是独一无二的，曾经在申报遗产阶段发挥过不可替代的重要作用。在未来的工作中，如何保持这个系统的先进性、保证它的有效性？我们的意见是：\r\n1、保证监测系统是“活”的。加强监测管理相关制度建设和能力建设，使监测工作常态化、规范化，保证有负责监测的专职人员，落实责任到人，建立考核机制。尤其是一些最紧要的、关键性的监测项，如规划实施情况、建设项目、水质、使用功能监测，应该作为硬性指标，实行催报制度。\r\n2、使监测成果真正有助于保护、管理。一方面，强化协同监测机制，通过采集监测数据，使分属多个行业的大运河管理机构、使用者们参与到遗产保护的事务之中；另一方面，应该探索依据监测成果进行有关大运河的保护管理决策的方式。\r\n3、利用好信息化的特点和优势，将大运河监测数据尽可能多地、及时地向社会公布，完善大运河国家监测预警平台的公众服务和宣传展示功能。以不断增进保护共识、主动为公众参与遗产保护提供条件为目标，做到及时向社会公布定期监测报告、反应性监测报告，发挥公众、媒体对大运河保护管理工作的监督作用，并使日常监测数据在相关部门之间共享。 \r\n4、结合监测持续深入地开展研究，在积累客观数据和实例的基础上，不断完善保护标准、保护规划、技术规范，使大运河的保护措施、利用模式越来越合理、越来越精细。\r\n目前存在的严重问题是，一是极个别的地方申报成功后很快就出现了不利于保护管理的行为，二是各遗产地开展大运河监测工作的情况差异太大。从以往的例子来看，一些监测系统常常在申遗成功后“功成身退”。但我认为，大运河监测只可兴、不可废。幸运的是，大运河监测系统的建设是有较好的顶层设计、扎扎实实地做的，承担这项工作的许多管理机构和专业团队都对它寄予厚望。而且，国家文物局在文研院设立了中国世界文化遗产监测中心，开展中国世界文化遗产监测预警系统国家总平台建设，现已基本完成系统搭建工作，计划今年上线运行。大运河监测系统将与之互通互联。这也从大环境上，从专业技术、业务规范方面为大运河监测提供了动力和保障。因此，我期待并且相信，通过坚持不懈地推进和完善大运河监测工作，把监测作为“抓手”，支撑协调机制、落实规划实施、促进承诺兑现，可以使我们、使公众的焦虑逐渐舒缓下来，达成更广泛的共识，实现对大运河的科学、有效保护。\r\n大运河申遗是个创举，是中国对全球世界遗产事业的莫大贡献，将大运河保护好、使它的价值传承下去，丰富和造福沿线人民的物质和精神生活，将会是一个更伟大的成就。\r\n', NULL, '刘曙光（中国文化遗产研究院院长）', '', '2014-08-13 14:53:43', 660, '2014年中国大运河世界遗产保护座谈会', 'Home/Images/news/active/刘曙光.jpg', '2015-07-21 05:49:00', 0, NULL),
(56, '2014年中国大运河世界遗产保护座谈会发言(舒乙)', '早先与大运河建立密切的关系，是因为罗哲文先生。罗先生在一次政协会议前找到我，说自己已不是新一届政协委员。但希望通过我提交大运河申遗点议案，我欣然接受。在那次政协之后，便连续四次参与政协组织的大运河考察活动，深受感动。2099年给总理写信，提出要国务院统筹协调大运河申遗与保护，得到总理批示，成立了大运河申遗省部际协商小组。我在四次考察大运河后出版过一本考察亲历记，发现浙北、浙东的水系运河网有着重要价值，它反映了这一地域居民特殊的生活方式，是值得重视的运河现象，应随时获取文物、水利、交通等信息的一部分。申遗成功后，我认为成功不是终点。而是起点，我希望在国家层面上尽快设立其有权威性的管理机构，并且尽快出台大运河保护和管理条例，使大运河保护有法可依，在具体问题上，我建议：1、对于已成为世界遗产的运河段。如果要进行相关项目建设，必须建立审批制度，不能在运河沿线随心所欲搞建设。2、在运河沿线设立严密的监测系统。3、要认真学习相关遗产标准和法规。让政府的领导者自觉形成文化遗产保护的意识。以确保遗产的真实性和完整性。', NULL, '舒乙（中央文史研究馆馆员，九、十届全国政协委员）', '', '2014-08-13 14:58:50', 827, '2014年中国大运河世界遗产保护座谈会', 'Home/Images/news/active/舒乙.jpg', '2015-07-21 05:49:00', 0, NULL),
(58, ' 江苏推动国家公园试点工作', '《关于全面构建“畅游江苏”体系促进旅游业改革发展的实施意见》中指出，将推动在江苏开展国家公园试点工作。推进无锡万达文化旅游城、苏州古城旅游区、淮安世界运河文化旅游区等投资超百亿元项目及南京江宁温泉旅游度假区、无锡清名桥旅游区、常州东方盐湖城、苏州吴江东太湖旅游度假区等投资超50亿元重点旅游项目建设。\r\n　　推动南京汤山、溧阳天目湖、无锡阳山、苏州阳澄湖半岛等以及苏中、苏北地区有条件的省级旅游度假区创建国家级旅游度假区。促进镇江茅山、连云港花果山、淮安周恩来故里、盐城大丰麋鹿生态旅游区、徐州云龙湖等景区创建国家5A级旅游景区。支持无锡蠡湖、苏州镇湖、徐州潘安湖等景区创建国家级生态旅游示范区。\r\n　　到2020年，全省力争新认定5家以上国家级旅游度假区，每市至少创建1家国家5A级旅游景区或国家级生态旅游示范区，全省50个旅游重大项目力争完成投资超过2000亿元。\r\n', '刘伟娟  项凤华', NULL, '何水', '2014-09-01 15:32:10', 145, '人民网', NULL, '2015-07-21 05:49:00', 0, NULL),
(59, '“畅游江苏”平台，将在9月下旬上线', '9月下旬，集全省旅游信息发布、电子商务于一体的“畅游江苏”网络信息服务平台将上线运行。这对于国庆长假到江苏旅游的游客来说，无疑是个好消息。届时，买门票、订酒店等都可以登录该网站搞定。到2020年，实现全省4A级以上景区全部达到智慧旅游景区标准。\r\n　　据了解，“畅游江苏”网络信息服务平台是旅游目的地的公众咨询服务系统。目前，正在进行图文等旅游数据信息的完善。该平台上线后，外地游客到江苏旅游的“吃住行，游乐购”都可通过这个平台实现。\r\n　　若是想来一场说走就走的旅行，只要持有江苏智慧旅游卡即可。届时，进景区、吃饭、购物等，不仅刷卡即可，还能享受一定的优惠。而且，这张智慧旅游卡是面向全国游客的。\r\n目前，江苏全省共有4A级以上景区约160家。江苏省旅游局工作人员透露，智慧旅游景区标准已经制定好，主要分为智慧的服务、智慧的管理和智慧的营销三个方面。待通过专家评审后，即可实施。届时，主要旅游场所、旅游公交实现免费无线网络覆盖。目标是把江苏建设成为国内一流、世界知名的旅游目的地，让游客“顺畅、舒畅、欢畅”游江苏。\r\n', '刘伟娟  项凤华', NULL, '何水', '2014-09-01 15:32:44', 110, '人民网', NULL, '2015-07-21 05:49:00', 0, NULL),
(60, '扬州建大运河预警平台 全方位监控所有遗产点', '作为大运河的发源地和运河申遗的牵头城市，与大运河同龄的江苏扬州被称为“中国运河第一城”，该市共有9个遗产点和6段河道列入首批申遗点段，成为大运河全线列入遗产最多的遗产区。为了在“后申遗时代”继续加大保护世界遗产力度，扬州率先在全国建设了运河监测预警平台，对所有遗产点进行全方位监控。此外，扬州还将申遗工作列入政府职能部门年度考核，以制度建设为大运河的申遗与保护提供强有力的支撑。\r\n    2011年10月，扬州率先建设了大运河扬州段监测预警平台，给大运河守护装上“电子眼”。这套系统对大运河河道及沿河遗址、建筑、墓葬、水工遗存等32个遗产点设置了5大项176个小项指标，整合水利、住建、航运、气象、环保和水文等30多个单位，运用空间信息技术，采取人工巡查、仪器监测和在线视频等手段进行监测预警，确保对遗产点实现全天候全方位监测，其中关于水质的监测每5分钟就会上传一次数据，“有任何异常情况都会在第一时间发现，并分配至相关部门及时处理。”\r\n2013年，扬州在监测预警平台的基础上，又开发了大运河遗产监测预警通用平台，以实现大运河遗产全线的监测预警。目前这套监测系统已经推广到全国27座城市、31个遗产地，并且在北京建成了一个综合性总平台，成为覆盖全线的监测网络。\r\n', '朱殿平 陈震坤', NULL, '何水', '2014-09-01 15:34:00', 106, '人民网', NULL, '2015-07-21 05:49:00', 0, NULL),
(61, '  无锡：古运河改造探索新模式', '无锡依水而建，因水而兴，是全国唯一被运河“环抱”的城市。2007年起，遵循“以水为本、以城为根、以文为魂”的规划理念，无锡环城古运河风貌带进入综合整治阶段。\r\n“我们承接古运河改造已近10年，最先开展的工作就是水环境治理。十年前这里的河水远不如现在清澈，07年太湖水危机后，清淤洁污工作正式开始。”无锡城市发展集团有限公司总裁、党委书记邹百青告诉记者，2013年开始，“一城一岛一带”的古运河风光带治理也提上日程。\r\n在第二阶段的城市综合改造中，当地政府充分做到了生态优先。“特别是老城区改造，涉及60万人口，18万平方公里城市基础设施的提升。其中包括文化景观长廊、生态旅游项目、十里风光带等多个项目“据邹百青介绍，无锡在抢救沿河历史遗迹、挖掘人文资源、提升中心城区文化能级等方面取得了阶段性成果。\r\n在大运河申遗过程中，江苏不少城市都打出“运河牌”。作为唯一被运河“抱城”的无锡段，在遗产点的保护和城市规划上也有独到之处。无锡古运河注入了更多的无锡吴文化、工商文化、运河文化内涵，成为中国大运河世界文化遗产的重要组成部分，也被无锡市民评为“无锡经典十一五”最具影响力的工程之一。\r\n无锡先后保护和修复了光复门、望湖门，并依托与历史沿革息息相关的“蓉湖溯源”、“北塘米市”、“莲蓉烟雨”、“站前灯火”、“熙春朝晖”、“望湖熏风”、“梁溪晓月”、“旧城怀古”八大文化主题景区，重塑老城区历史空间架构。\r\n据悉，无锡运河公园，占地16万平方米的城区大型文化生态公园，在合理利用工业遗产上也独具匠心，探索了”老瓶装新酒“的新模式：利用原塑料仓库改建成“何振梁与奥林匹克陈列馆”；九丰面粉厂旧址修缮开辟成“无锡市书画博物馆”；市粮食七库旧址改造成“中国民族音乐博物馆”等都各具特色。除此之外，在建成228米的《运河无锡图纪》汉白玉浮雕长廊的同时，还在古运河沿岸打造了《运河无锡图记》、《古运河梁溪风情图》、《锡山隐渔图》、《莲蓉烟雨图》、《名人咏无锡》、《无锡竹枝词》等书画碑廊，彰显了无锡特色文化。\r\n', '俟名', NULL, '何水', '2014-09-01 15:34:29', 179, '东方网', NULL, '2015-07-21 05:49:00', 0, NULL),
(62, '“运河重镇-世遗”全国摄影展在南浔启动', ' 为大力打造“南浔古镇”旅游品牌，日前，南浔古镇管委会和南浔区委宣传部、中国摄影家协会等联合举办“运河重镇——世界文化遗产地南浔全国摄影大展”。\r\n据介绍，浙江湖州南浔作为中国大运河列入世界文化遗产地中的古镇，中国十大魅力名镇、中国历史文化名镇，拥有国家级、省级、市级文物保护单位16处，其中嘉业藏书楼、小莲庄、张静江故居、张石铭旧宅、沿河民居建筑群——百间楼等为经典景观。该摄影大赛旨在吸引摄影人群以敏锐的洞察力和独特视角进行摄影创作，通过优秀的摄影作品充分展示我市南浔的自然景观、人文风情、民俗节庆、历史古迹、文化遗产、社会风貌等，突出南浔古镇景区和景点的代表性，以摄影带动旅游。\r\n', '俟名', NULL, '运光', '2014-09-01 15:35:14', 96, '湖州日报 湖州网	', NULL, '2015-07-21 05:49:00', 0, NULL),
(63, '大运河沿线城市餐饮“抱团”', ' 今年6月中国大运河申遗成功，由苏州市大众餐饮行业协会倡议，北京、天津、济宁、苏州、宁波五地餐饮协会汇聚苏州，探索大运河沿线城市餐饮业连点成线新举措，共同发起成立“中国大运河餐饮联盟”。在8月18日的启动仪式上，来自全国10余个省市的150余位企业家、研究学者等，共同交流探讨大运河餐饮合作。\r\n大运河沿线餐饮实力雄厚，据介绍，目前全国有200多万家餐饮企业，大运河沿线的北京就有8万多家餐饮企业，苏州有2万多家。中国大运河餐饮联盟将以大运河文化遗产为纽带，整合餐饮产业链资源，加快沿线城市餐饮行业协调发展，推出区域间系列经济合作举措，全面推进大运河沿线城市餐饮名企、名厨、名菜、名食材等餐饮元素的交流合作，推动沿线城市餐饮、文化旅游一体化进程，实现区域间餐饮产业链资源共享。\r\n', '俟名', NULL, '何水', '2014-09-01 15:35:39', 99, '江苏网', NULL, '2015-07-21 05:49:00', 0, NULL),
(64, '大运河边文庙学政署明年7月修复完毕 将开国学班', '今年6月22日，中国大运河申遗成功。作为大运河最北端的一段，通惠河两岸文物的保护工作显得十分必要。为了保护好大运河周边文物，通州区对文庙西路“学政署”进行原貌修复，预计明年7月修复完毕，明年将开办国学班，向大众宣扬运河文化。\r\n　　今年5月，通州开始了对文庙整体修复的二期工程，“学政署”便包括在二期工程内。据通州区博物馆馆长郑旭东介绍，此次复建建立在大量考古勘探的基础上，整个工程以遗址原貌为样本进行恢复。根据规划和条件，文庙将被恢复到明清原貌。因此，恢复后的“学政署”也将呈现出明清时的样貌。\r\n　　作为文庙中的一处重要建筑，“学政署”位于文庙西路，在明代又称学台，是管理教育机构，相当于现在学校的教务处。建国后，“学政署”曾被拆除。“学政署及三教庙都与运河文化息息相关，是运河文化的重要载体，而我们此次对运河城市段的整治工程，重点也就是修复学政署、三教庙这些运河两岸的文物。”郑旭东说。“建好后，‘学政署’将有两个用途。一是作为国学教育的重要活动场地，发挥其本身作为教育管理机构的历史功能；二是用作跟运河文化相关的展览展示的场所和平台。因为‘学政署’本身地处运河文化核心区，而通州区目前又正在打造发展运河文化，所以必然会被用作宣扬运河文化，让大众更好地了解运河文化。”郑旭东如此诠释重建完成后的“学政署”如何保护和利用。\r\n　　目前，“学政署”的地下基础建筑工程已按照明清原貌修复完成了，即将开始其地面工程的修复建设。修建中的“学政署”面积大约600平方米，预计明年7月份完工。\r\n　　据北京运河湾研究专家周良介绍，通州区在文庙的整体修复中出土了大量有价值的文物，如：皇木、铁锚及大量瓷片。同时，通州区与北京市文物研究所合作，共同对通州多处建设用地进行了考古勘探和挖掘，发掘出土了上百件铜、铁、陶、瓷等多种地下文物。\r\n', '张会', NULL, NULL, '2014-09-01 15:36:42', 104, '千龙网', NULL, '2015-07-21 05:49:00', 0, NULL),
(65, '北京每日巡查监测大运河水质', '中国大运河申遗成功，北京有两处河道和两处遗产点入选世界遗产名录，相关负责人表示，河道和遗产点所涉及的东城、西城和通州在修缮沿岸文物的同时，将依托市级平台，巡查运河水环境，监测运河水质，让古运河荡清波。\r\n早在北京奥运会之前，玉河这条被纳入大运河遗产项目中的河道就已启动修缮和治理。“其实这里并不是活水，为了能够保证水质，我们在河道里安装了水泵，定期循环。同时，还加强了河道两边的环境监测和保护。”东城区文物管理所相关负责人介绍说，2011年起，管理所还加强河道周边环境整治，比如加强巡检力度，禁止市民下河、垂钓和往河道倾倒垃圾等。\r\n什刹海与玉河河道只隔着一座万宁桥，桥边已立下一个界桩——中国大运河遗产点。西城区文委相关负责人介绍，什刹海的保护一直严格按照历史文化名城保护条例进行。申遗过程中，经多方协调，将什刹海的出水口高梁桥纳入保护区，形成一个完整水系。同时，景区还与首都博物馆合作，汇编了现存的所有什刹海文史档案和文献，编成134册的完整史料集。相关部门定期清理水域，每个月还对水位、水质进行监测比对。\r\n大运河遗产北京段中，位于通州区的运河河道有42公里长，占北京段的60%。记者获悉，通州区正在增加污水处理设施，逐步改善通惠河水质状况，并加强通惠河沿岸施工管理与环境风貌控制，推进燃灯塔周边集展示、旅游、休闲、教育于一体的“运河文化景观区”建设，逐步恢复大运河的历史风貌，保护好运河文化景观。目前，该区已完成第一阶段10公里河道的治理。\r\n据北京市文物局相关负责人介绍，目前北京市运河水质监测平台已初步建立，水务、文物等相关部门原有的监测平台已经打通，有关运河保护的数据双方交流共享。两部门工作人员每天对运河进行巡查，监测内容包括水流、水位、水藻等。同时，遗产点已安装摄像头，每天巡查监控遗产点情况，每个月将监测数据汇总至北京市文物局，再上报至国家文物局，确保运河遗产各项保护措施的落实。\r\n', '刘可', NULL, '何水', '2014-09-01 15:37:19', 97, '北京日报 ', NULL, '2015-07-21 05:49:00', 0, NULL),
(66, '大运河将原址复原三庙一塔', '8月中旬，通州司空小区1000余户居民，为了“抢”拆迁后的安置房，提前6天排队，引起不小关注。更令人关注的是，这个位于通州新城运河核心区的最老小区，完成的绝非一次简单的拆迁工作，它预示着大运河已步入后申遗时代。据了解，司空小区腾退后，80%的土地将用来复原大运河畔“三庙一塔”的完整风貌，将“消失”的两进院落还给“三教庙”，并进行原貌的复建工作。未来，通州区将大刀阔斧地推进大运河的改造和保护工作，包括继续推进“申遗”，并全面开展河道治污工程。\r\n    今年6月22日，“大运河”申遗申请中共有两处河道、两处遗产点入选。河道分别为通惠河北京旧城段（包括什刹海和玉河故道）、通惠河通州段；遗产点分别为，西城区澄清上闸（万宁桥）和东城区澄清中闸（东不压桥）。申遗前，本市共有40处大运河遗产纳入保护规划。按照国际惯例，申遗成功后，还可以继续补充申报遗产点，这也成为了后申遗时代的重要工程。目前，这一后续申遗工作已启动，未入选的38处遗产点，正在完成修缮和整治工作，以争取纳入世遗范围。\r\n　　位于司空小区南侧的“三庙一塔”，是通州争取补充申报的一个遗产点。“三庙一塔”由“三教庙”和“燃灯佛舍利塔”组成，“三教庙”为儒教的文庙、佛教的佑胜教寺和道教的紫清宫，是全国唯一一座集三教于一体的庙宇；“燃灯佛舍利塔”则是古运河的镇河之塔。按照通州区打造“一片四景”的建设规划，“燃灯塔”将作为运河历史文化景区的核心，被重点保护和修缮。通州区文委副主任杨根萌表示，未来通州将结合大运河的保护和北京副中心的建设，打造老城区内的“一片四景”，包括南大街历史片区、以燃灯塔为核心的运河历史文化景区、以宝通银号为主的商业文化景区、以静安寺为主的民俗文化景区和以潞河驿为主的皇家文化景区。\r\n', '林艳', NULL, '何水', '2014-09-01 15:37:46', 104, '北青网', NULL, '2015-07-21 05:49:00', 0, NULL),
(67, '《中国大运河历史文献集成》出版', ' 由聊城大学运河学研究院组织编纂的《中国大运河历史文献集成》日前已经出版发行。作为大运河文献资料的首次集结出版，将为大运河研究工作的深入发展起到极大的促进作用。\r\n据了解，聊城大学运河学研究院的20余位专家学者，花费近十年的时间，将与大运河有关的文献进行了统一梳理，共计80册，收录了130余种专门著作、7000余万字、5000余幅图片，被业内人士誉为“运河百科全书”。据工作人员介绍，“中国大运河历史文献题材广泛，来源复杂，数量庞大。在搜集整理过程中，着重选取专门以运河为记述对象的书籍。”该丛书收入的100余种运河文献，分为运河工程类、运河水利类、治黄保运类、漕运关志类，收录范围包括元、明、清至民国初期等时期700余年的历史。在出版过程中，采用影印的方式，为研究者提供了便利，也为关心运河及其历史文化遗产的人们提供基本的阅读材料。\r\n    据介绍，本次对大运河文献进行整理，是我国历史上对大运河有关史料的第一次集中整理，内容详尽、完整。为研究运河沿岸地区的经济社会发展，提供了重要资料。同时，由于大运河在我国古代的交通大动脉作用，对其文献的整理，也对研究我国古代的政治、经济、文化具有十分重要的史料价值。同时，由于该丛书所收文献采用的基本都是一手资料，因此具有相当高的版本价值。在研讨会召开期间，该丛书吸引了大量专家、学者的关注，中国运河博物馆、杭州市图书馆分别订购了若干套，为今后当地的学术研究提供便利。 ', '祝伟康 ', NULL, '何水', '2014-09-01 15:38:16', 108, '聊城日报', NULL, '2015-07-21 05:49:00', 0, NULL),
(68, '村史馆藏有秦汉陶器', '拥有2200年历史的通州区潞城镇胡各庄村近日开放了其村史馆。包括秦汉陶器等文物，共同见证了小村的历史。\r\n潞城镇胡各庄村位于通州东部，被京杭大运河、运潮减河和潮白河三水环抱。据通州区文物工作者考古研究证明，该村有人类活动和居住的历史至少可以追溯至2200年前。解放后，村民徐凯等人在村内西大坑挖掘出大量秦汉时期的陶器、钱币、瓦当、灶台，说明在秦汉时期就有人在胡各庄一带居住、生产、生活。\r\n“历经280天，118名村民参与其中，走东家、串西家，收集到胡各庄村不同历史时期的各种照片、资料265份，实物、文物、收藏200余件。”胡各庄村党支部书记徐立说，今年7月，村史陈列馆成立。\r\n目前，村史陈列馆分室外和室内两部分，免费对外开放。\r\n室外展区510平方米，挂放着胡各庄村上世纪50年代十二景图和全景图共13幅，大庙、西大坑、油坊、市口、毛家小铺、张家大铺子等12个“著名”村景跃然于画；陶罐、风箱、耕田的犁、布票、房契……200余平方米的室内展厅一部分存放着该村反映自解放以后政治、经济变迁及村民生产、生活等方面的老物件，另一部分存放着历经百年乃至上千年的文物。\r\n', '金耀飞', NULL, '何水', '2014-09-04 13:57:23', 89, '北京日报', NULL, '2015-07-21 05:49:00', 0, NULL),
(69, '大型原创歌剧 《运之河》预演', '8月31日晚，由江苏省精心打造的大型原创歌剧《运之河》在紫金大戏院预演。\r\n《运之河》由著名作曲家、中央音乐学院作曲系教授唐建平作曲，著名编剧冯柏铭、冯必烈撰写剧本，知名导演邢时苗执导。从培养我省艺术人才、优秀青年演员和利于剧目的常态化可持续化演出考虑，本次歌剧《运之河》排出了三组演出阵容。该剧将于本月20日在南京保利大剧院揭幕的“江苏省第二届艺术节”开幕式上正式首演。届时，著名歌唱家戴玉强、殷秀梅将作为男女主角进行表演。\r\n《运之河》的创作以大运河今年成为中国第46个世界文化遗产为背景，以大运河的开掘、通航为经，以隋、唐的朝代更迭为纬，编织出了一部回肠荡气、催人警醒的历史正剧。\r\n', '冯兴', NULL, '何水', '2014-09-04 14:03:01', 93, '南报网', NULL, '2015-07-21 05:49:00', 0, NULL),
(70, '嘉兴欲打响运河文化旅游品牌', '自中国大运河“申遗”成功后，嘉兴动作频频。如今，一条串联长虹桥、南湖旅游区、清池温泉、濮院、石门、崇福、长安闸的运河旅游“黄金带”已浮出水面。\r\n据悉，大运河嘉兴段北接苏州，南连杭州，总长110公里，占申遗河道总长的十分之一以上。大运河嘉兴段现有嘉兴长虹桥和海宁长安闸两个遗产点，苏嘉运河、嘉兴环城河、杭州塘、崇长港、上塘河、桐乡崇福—余杭塘栖6段河道，共8个点(段)列入世界文化遗产名单。\r\n嘉兴作为大运河流经的主要城市之一，沿途文化遗存丰富，两岸风光秀丽，是我市宝贵的旅游文化资源。放大运河“世遗效应”，就需要旅游这一载体，在保护的前提下，合理开发旅游资源。\r\n　　而在国家旅游局的定位中，京杭大运河将被打造成除万里长城、丝绸之路之外，我国第三条世界级旅游线路。\r\n　　翻开《嘉兴市旅游图》，可以清晰地看到，大运河嘉兴段贯穿我市秀洲区、南湖区、桐乡市和海宁市，辐射整个嘉兴。就拿列入世遗名单的长虹桥和长安闸来说，其背后的文化遗存就为开发运河旅游提供了强力的支撑。长虹桥位于秀洲区王江泾镇，是中国大运河上罕见的巨型三孔实腹石拱大桥，因气势宏伟，形似长虹而得名。而位于海宁市长安镇的长安闸，是古代连接不同水系崇长港和上塘河的一个重要枢纽及管理机构，始建于唐贞观年间，宋代为江南运河三大堰之一，是江南古运河上规模最大的运河设施之一。此外，五芳斋、嘉欣丝绸、丰同裕蓝印布等工业旅游示范点，中共一大会址、文生修道院、罗家角遗址等全国重点文物保护单位也分布在运河两岸。\r\n据不完全统计，目前嘉兴运河沿线散列着国家5A级景区——南湖、乌镇，各类相关文化遗产81处，其中运河三塔更是京杭大运河的标志。\r\n运河两岸处处散列风景，处处蕴涵文化，如何合理开发？市旅游局给出的答案是，“要在运河沿线景点串珠成链、串点成线的基础上，努力做好扩线成片、连片成面，将运河古城与周边重点景区、生态自然景观有机融合，做好水上游文章。”根据初步规划，下一步嘉兴将以运河为纽带，重点改造包括长虹桥、文生修道院、双魁巷、落帆亭、芦席汇、梅湾街、三塔塘和长安闸等景区(点)，并进一步加强旅游基础设施和配套设施建设，完善旅游公共服务功能，不断壮大旅游产业规模。\r\n', '俟名', NULL, '何水', NULL, 45, '嘉兴日报', NULL, '2015-07-21 05:49:00', 0, NULL),
