CREATE TABLE IF NOT EXISTS `travel_college` (
  `NewsID` int(11) NOT NULL,
  `City` varchar(255) DEFAULT NULL,
  `Title` varchar(255) DEFAULT NULL,
  `Content` text,
  `Pictures` varchar(255) DEFAULT NULL,
  `Level` int(11) DEFAULT '0',
  `BrowseNumber` int(11) DEFAULT '0',
  `Time` datetime DEFAULT NULL
) ENGINE=MyISAM AUTO_INCREMENT=24 DEFAULT CHARSET=utf8;

--
-- 转存表中的数据 `travel_college`
--

INSERT INTO `travel_college` (`NewsID`, `City`, `Title`, `Content`, `Pictures`, `Level`, `BrowseNumber`, `Time`) VALUES
(14, '河南商丘', '应天书院', '应天书院，位于商丘市，为北宋时期四大书院之一。应天府书院即应天书院、睢阳书院，前身是南都学舍，是五代后晋时的商丘人杨悫创办，位于河南省商丘市睢阳区，国家4A级风景区商丘古城南湖畔。\r\n应天府书院的历史，可以追溯到五代时的后晋，唐朝覆亡后，中国历史提交内容进入“五代十国”分裂时期，官学遭受破坏、庠序失教，中原地区开始出现一批私人创办书院，应天府书院由此而生。当时有邑人杨悫“乐于教育”，在将军赵直的支持下，聚徒讲学。杨悫去世后，他的学生楚丘人戚同文继承师业，继续办学，培养出诸如宗度、许骧、陈象舆、高象先、郭成范、王砺等后来都成为台阁重臣的著名人物。使应天府书院扬名的另一位人物就是那个吟出“先天下之忧而忧，后天下之乐而乐”的范仲淹。应天府书院书院在曹诚等人尤其是应天知府、著名文学家晏殊的支持下，得到很大发展。著名政治家、文学家范仲淹等一批名人名师在此任教，更使书院人才辈出，显赫一时。\r\n北宋初书院多设于山林胜地，唯应天书院设于繁华闹市，人才辈出。随着晏殊、范仲淹等人的加入，应天书院逐渐发展为北宋最具影响力的书院，是古代书院中唯一一个升级为国子监的书院，被尊为北宋四大书院之首。\r\n', 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\应天书院.jpg', 0, 638, '2014-07-25 10:45:15'),
(15, '河南郑州', '嵩阳书院', '嵩阳书院位于河南省登封市嵩山南麓。宋代理学的“洛学”创世人程颢、程颐兄弟都曾在嵩阳书院讲学，此后，嵩阳书院成为宋代理学的发源地之一。明末书院毁于兵燹，清代康熙时重建。嵩阳书院经历代多次增建修补，规模逐渐形成，布局日趋严整。书院的建制，古朴雅致，大方不俗。嵩阳书院与河南睢阳书院、湖南岳麓书院、江西白鹿洞书院并称我国四大书院。\r\n嵩阳书院环境幽美，为读书胜地。书院内建筑布局保持着清代前的风格，现存殿堂廊房五百余间，共有五进院落组成。首为先师祠，供奉与书院有关的先师先贤，其后为讲堂，讲堂后为道统祠，最后是藏书楼。两侧配房原为“程朱祠”、书舍、学斋等。院内廊房墙壁上镶嵌有历代文人、墨客题字留言，其内容书法各具特色，西偏院有清代嵩阳书院教学考场部分建筑。嵩阳书院历朝并置有学田，以充书院费用。\r\n', 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\嵩阳书院.jpg', 1, 739, '2014-07-25 11:06:05'),
(16, '山东德州', '董子读书台', '董子读书台位于山东省德州市，作为我国历史上与孔子、朱熹一起并称为三大文化巨人之一的董仲舒，是中国历史上著名的哲学家、思想家，西汉时期最具代表性的儒学大师，班固在《汉书》中称之为“群儒首”。他提出的“大一统论”、“天人感应”和“独尊儒术”等重要思想，树立了汉以来两千多年中国的国家意识，确立了中国文化发展的基本方向，形成了中国文化的基本特点，锻造了中华民族的基本品格和基本精神。    \r\n德州人民对董仲舒非常尊崇，隋朝时就已修建了董子读书台，书台夕照成为德州十景之一。历代钦官巡按、文人墨客，留下了大量歌咏诗篇。清乾隆皇帝南巡，驻跸德州，曾留下“天人三策对贤良，已见春秋大义彰。那更高台演繁露，转思董子失之详”“故里千秋疑信猜，城楼耸处久称台。都传繁露春秋演，谁识竹林与玉杯”等诗名。\r\n德州董子读书台始建于隋朝，民国时期毁于战乱。德州经济开发区党工委、管委会按照市委、市政府发掘历史文化资源，发展文化事业，打造区域经济文化高地的决策部署，于2007年9月开始复建董子读书台，并以此为核心，建成具有丰富文化内涵的景区公园。新建成的董子园景区总占地743亩，投资5亿元。\r\n', 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\董子读书台.jpg', 0, 624, '2014-07-25 11:08:00'),
(17, '浙江宁波', '天一阁', '天一阁位于浙江宁波市区，是中国现存最早的私家藏书楼，是世界上现存历史最悠久的私人藏书楼之一，也是亚洲现有最古老的图书馆和世界最早的三大家族图书馆之一。\r\n天一阁占地面积2.6万平方米，建于明朝中期，由当时退隐的兵部右侍郎范钦主持建造，1982年被国务院公布为全国重点文物保护单位。现藏各类古籍近30万卷，其中珍椠善本8万卷，尤以明代地方志和科举录最为珍贵。近年来，我馆事业发展迅速，先后新增中国地方志珍藏馆、银台第官宅博物馆、麻将起源地陈列馆等处。\r\n天一阁是一个以藏书文化为核心，集藏书的研究、保护、管理、陈列、社会教育、旅游观光于一体的专题性博物馆。现藏古籍达30余万卷，其中，珍椠善本8万余卷，除此，还收藏大量的字画、碑帖以及精美的地方工艺品。设有《天一阁发展史陈列》、《中国地方志珍藏馆》、《中国现存藏书楼陈列》、《明清法帖陈列》等陈列厅，书画馆常年开展各种临时展览和文化交流活动。天一阁分藏书文化区、园林休闲区、陈列展览区。此外，还在楼前凿“天一池”通月湖，既可美化环境，又可蓄水以防火。天一阁的建筑布局后来为其他藏书楼所效仿。\r\n', 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\天一阁.jpg', 0, 618, '2014-07-25 11:08:31'),
(18, '浙江嘉兴', '昭明书院', '昭明书院，位于乌镇市河西侧。昭明书院得名于曾在乌镇筑馆读书的南朝梁昭明太子萧统。萧统编辑整理的《文选》是我国第一本诗歌散文选集。在很长一段时间，《文选》和后来的《古文观止》、《唐宋八大家文钞》等是古代读书人案头必备的文学读本，影响深远。\r\n昭明书院，为桐乡市重点文物保护单位。据清乾隆《乌青镇志》记载，梁天监二年（503年），萧统曾随老师沈约来乌镇读书，并建有书馆一座。后来，书馆塌毁，遗迹残存。明万历年间，乌镇同知全廷训出于对萧统才学的敬仰，便在书馆旧址前建起一座石坊，里人沈士茂于石坊上方题写六朝遗胜、梁昭明太子同沈尚书读书处匾额。1977年茅盾欣闻故乡这一古迹在文化大革命之后仍大难不毁，在给家乡一首词中写下了唐代银杏宛在，昭明书室依稀的佳句。\r\n书院坐北朝南，半回廊二层硬山式古建筑群。主楼为图书馆，收藏有文化、社会科学、艺术、休闲旅游等方面的图书和杂志可供阅览，并设有电子阅览室、讲堂、书画、教室等。中为校文台，为著述编校之处。前方庭园中有四眼水池，四周古木参天，浓荫匝地。书院西为拂风阁，是喝茶、读书、交流的场所，水池中央有明代经幢。晚风轻拂时，坐在露天的亲水平台上捧一卷美文，品一壶香茶，闻一曲琴韵，会让你深深体会到读书的快乐。\r\n', 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\昭明书院.jpg', 0, 725, '2014-07-25 11:08:58'),
(19, '浙江杭州', '万松书院', '万松书院位于杭州市，西湖南缘凤凰山万松岭，始建于明弘治十一年（1498年）。万松书院曾名太和书院、敷文书院，是明清时杭州规模最大、历时最久、影响最广的文人汇集之地。明代王阳明、清代齐召南等大学者曾在此讲学，"随园诗人"袁枚也曾在此就读。清代康熙、乾隆两帝南巡时，分别赐额"浙水敷文"、"湖山萃秀"。　\r\n万松书院，始建于唐贞元年间，名报恩寺。明弘治十一年，浙江右参政周木改辟为万松书院。明代理学家王阳明曾在此讲学。清康熙帝为书院题写“浙水敷文”匾额，遂改称为敷文书院。现遗址尚存有“万世师表”四字的牌坊一座和依稀可见“至圣先师孔子像”的石碑等物。\r\n2001年7月，杭州市启动万松书院复建工程，按明代建筑风格样式修复，规划面积5万多平方米，建筑面积1200平方米。书院主体建筑包括仰圣门、明道堂、大成殿、毓秀阁等。其中毓秀阁原为接待各地访问学者的处所，现辟有"梁祝书房"，展现梁祝当年刻苦攻读、"促膝并肩两无猜"的场景。明道堂为书院讲堂，陈设展示中国历代科举文化。大成殿为祭祀孔子处，设有"孔子行教图"壁画。\r\n', 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\万松书院.jpg', 0, 670, '2014-07-25 11:09:25'),
(20, '浙江绍兴', '百草园', '百草园位于浙江省绍兴市东昌坊口鲁迅故居内，百草园是鲁迅家的后园，占地近2000平方米，因鲁迅在厦门时期所写《从百草园到三味书屋》而闻名。\r\n百草园是鲁迅童年时代的乐园，常来玩耍嬉戏。这个荒芜的园子，不仅瓦砾成堆，杂草丛生，而且有树木、藤蔓和飞鸟爬虫。《从百草园到三味书屋》中的景象即使现在已难寻旧踪，但文中的浓厚的生活情趣依然带给人丰富的想像。百草园易主朱家之后，小园中的平地垒起了假山，并构筑了鱼池和花厅，而它的主要部分仍基本上保持原样。\r\n这里还有个小故事就是，百草园里有鲁迅笔下的“皂荚树”，然而经专家考证，这里种的“皂荚树”其实是“无患子”。最后经由当地人民和专家努力，最终选中一棵真正的皂荚树移栽百草园，重现了鲁迅笔下的景致。\r\n', 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\百草园.jpg', 0, 761, '2014-07-25 11:09:51'),
(21, '江苏扬州', '文昌阁', '文昌阁位于扬州市，俗称文昌楼，学名文汇阁，位于江苏扬州汶河路、文昌路交叉处。建于明代万历十三年(1585年)，因是扬州府学的魁星楼，名为"文昌阁"。旧日阁上悬有"邗上文枢"匾额。扬州府学文庙建筑，已陆续圮毁，现在仅余文昌阁，为扬州闹市的一处佳景。\r\n文昌阁为八角三级砖木结构建筑，与北京天坛的祈年殿相似。阁的底层，四面辟有拱门，与街道相通，阁的第二三两层，四周虚窗，皆可输转。登楼四眺，远近街景，尽收眼底。每于节庆之夜，阁上彩灯辉耀街衢，为扬州闹市的一处佳景。文昌阁现为扬州市地标建筑、扬州市级文物保护单位，位于扬州市中心也是扬州最繁华的商业圈，文昌阁四周布满鲜花，通常不可让市民及游客参观游玩。\r\n文昌阁处于扬州最繁华的商业中心，坐落着金鹰、时代等大型购物场所，以及国内外知名的服装、珠宝等专卖店铺。素有“扬州商业第一街”之誉，旅游休闲是文昌阁与众不同之处。白天走在文昌阁购物区中，游客们会赏心于满目的绿色以及扬州人民精致美好的幸福生活，夜晚的文昌阁则是一座不夜城。每当入夜，建筑物顶部和立面霓虹齐放，错落有致，流光溢彩，成了灯的海洋。而文昌阁附近的文昌广场中，更成为扬州人民晚上散步娱乐的休闲场所。', 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\文昌阁.jpg', 0, 683, '2014-07-25 11:10:22'),
(22, '江苏无锡', '东坡书院', '东坡书院座落在宜兴市丁蜀镇东北隅的蜀山山麓。这里树木葱茏，鸟语啁啾，一条小河从院门前静静流过，环境十分雅致；院内建筑整齐壮观，古朴典雅，具有浓郁的民族风格。根据史书记载：东坡书院建于北宋年代，虽历经千年的风雨沧桑，天灾人祸，但代代乡党重文厚教，募资悉心修葺，至今仍保存完好。\r\n东坡书院是宋代大文豪苏东坡在宜兴丁山买田讲学之地。书院历经沧桑，多次被毁，多次重建，现今留下的建筑是明代重建的。郭沫若、邹韬奋等知名人士曾前来讲学。书院曾培育了潘序伦、倪维德、范保文等我国一流的学者。它还是陶都历史上诸多紫砂工艺大师的启蒙摇篮。由于年久失修，东坡书院逐年破落。 在建设历史文化名镇过程中，丁蜀镇决定修缮东坡书院。省规划设计院、苏州园林设计院精心设计了整修方案。\r\n东坡书院与我国历史上杰出的文学家苏东坡有密切的关系，他才华横溢，对我国文学艺术的发展有多方面的贡献，尤其是他的散文，为世人称道，被列为唐宋八大家之一。\r\n', 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\东坡书院.jpg', 0, 578, '2014-07-25 11:10:46'),
(23, '江苏无锡', '东林书院', '东林书院，位于江苏省无锡市解放东路867号，亦名龟山书院，是我国古代著名书院之一。1956年10月由江苏省人民委员会公布为省级文物保护单位，2006年6月入选第六批全国重点文物保护单位。\r\n东林书院创建于北宋政和元年即公元1111年，是当时为北宋理学家程颢、程颐嫡传高弟、知名学者杨时长期讲学的地方。后废。明朝万历三十二年，也就是公元1604年，由东林学者顾宪成等人重兴修复并在此聚众讲学，他们倡导“读书、讲学、爱国”的精神，引起全国学者普遍响应，一时声名大著。东林书院成为江南地区人文荟萃之区和议论国事的主要舆论中心。\r\n“风声雨声读书声声声入耳，家事国事天下事事事关心 ”，此联为明东林党领袖顾宪成所撰（《名联谈趣》275）。他在无锡创办东林书院，讲学之余，往往评议朝政。后来人们用以提倡"读书不忘救国"，至今仍有积极意义。上联将读书声和风雨声融为一体，既有诗意，又有深意。下联有齐家治国平天下的雄心壮志。风对雨，家对国，耳对心, 极其工整，特别是连用叠字，如闻书声琅琅。东林书院的石牌坊、泮池、东林精舍、丽泽堂、依庸堂、燕居庙、三公祠、东西长廊、来复斋、寻乐处、心鉴斋、小辨斋、再得草庐、时雨斋、道南祠、东林报功祠等主要建筑，均显现明清时期布局形制与鼎盛时期风貌。顾宪成曾撰有“风声雨声读书声声声入耳，家事国事天下事事事关心”这副心怀远大抱负之名联，后被广为传诵，成为许多学人、志士的共同心声和座右铭。此联现悬于依庸堂内。\r\n', 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\东林书院.jpg', 0, 659, '2014-07-28 14:30:07');

