--
-- 表的结构 `travel_garden`
--

CREATE TABLE IF NOT EXISTS `travel_garden` (
  `NewsID` int(11) NOT NULL,
  `Name` varchar(255) DEFAULT NULL,
  `Title` varchar(255) DEFAULT NULL,
  `Content` text,
  `Level` int(11) DEFAULT '0',
  `BrowseNumber` int(11) DEFAULT '0',
  `Pictures` varchar(255) DEFAULT NULL
) ENGINE=MyISAM AUTO_INCREMENT=21 DEFAULT CHARSET=utf8;

--
-- 转存表中的数据 `travel_garden`
--

INSERT INTO `travel_garden` (`NewsID`, `Name`, `Title`, `Content`, `Level`, `BrowseNumber`, `Pictures`) VALUES
(2, '个园', '个性竹石的堆叠', '扬州个园位于江苏省扬州市东关街的北段，是中国园林中以叠石见长的著名园林景观，亦是全国的四大名园之一。个园是以竹石取胜，连园名中的“个”字，也是取了竹字的半边，应合了庭园里各色竹子。\r\n江苏扬州个园，是一座独具风格的名园。它是清嘉庆、道光年间两淮盐总黄至筠在明代寿芝园旧址上兴建起来的。当时园中遍植翠竹，盖取东坡诗句：“宁可食无肉，不可居无竹；无肉使人瘦，无竹使人俗。”以示主人不俗。又因竹叶形状像一个“个”字，故名“个”园。个园以四季假山的堆叠精巧而著名。\r\n个园的叠石艺术，采用分峰用石的手法，运用不同石料堆叠而成“春、夏、秋、冬”四景。四季假山各具特色，表达出“春景艳冶而如笑，夏山苍翠而如滴，秋山明净而如妆，冬景惨淡而如睡”和“春山宜游，夏山宜看，秋山宜登，冬山宜居”的诗情画意。个园旨趣新颖，结构严密，是中国园林的孤例，也是扬州最富盛名的园景之一。', 1, 616, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\个园.jpg'),
(3, '何园', '晚清第一园', '何园坐落于江苏省扬州市的徐凝门街，被誉为“晚清第一园”，其中，片石山房系石涛大师叠山作品，堪称人间孤本。\r\n何园又名“寄啸山庄”，园名取自陶渊明“归去来兮……依南窗以寄傲，登东皋以舒啸”之意，由清光绪年间何芷舠所造。何园原址，为乾隆年间古园，名双槐园。园主何芷舠，其家族为与北洋大臣李鸿章、光绪帝师孙家鼐进退与共的姻亲三大家族，他从湖北汉黄德道台任上壮年致仕，归隐扬州，于光绪九年巨资良材建造大型私家住宅园林。曾在何园寓居过的名人有著名国画大师黄宾虹；著名作家朱千华先生。何园是扬州大型私家园林中最后问世的一件压轴之作。\r\n何园有冠盖中国造园艺术的四个“天下第一”，其中享有“天下第一廊”美誉的1500米复道回廊，构成园林建筑四通八达之利与回环变化之美，在中国园林中绝无仅有。何园还是国内著名的影视取景的天然基地，《红楼梦》、《还珠格格》续集、《苍天有泪》等近百部影视剧在此取景。\r\n', 0, 704, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\何园.jpg'),
(4, '蠡园', '演义唯美爱情', '蠡园位于无锡市风光秀美的蠡湖之滨，是国家重点名胜区“太湖”的主要景点之一。蠡园是一处以“堆造假山、巧借真水”而闻名的江南水乡园林，是演绎范蠡西施爱情传说的地方，相传春秋时越国大夫范蠡偕美人西施泛舟于此，湖因人而得名，园因湖而得名。\r\n蠡园因紧傍蠡湖而得名。蠡园始建于1927年，由号称“面粉大王”的王禹卿出资所建，因其仰慕范蠡的为人，故取名“蠡园”。1930年，王家的亲戚陈梅芳在蠡园旁建造了另一个园林，据范蠡在此与乡民一起养鱼的传闻，取名“渔庄”，并声称要胜过蠡园，故又别名“赛蠡园”。1952年，园林部门将蠡园和渔庄合并，沿用“蠡园”旧名，园内有四大景点，即假山耸翠、南堤春晓、长廊揽胜和层波叠影。\r\n蠡园三面环水，远眺翠嶂连绵，近闻长浪拍岸；南堤春晓，桃红柳绿；枕水长廊，步移景换；假山耸翠，曲折盘旋；亭台楼阁，层波叠影。当代大文豪郭沫若咏有佳句：“欲识蠡园趣，崖头问少年”。蠡园假山就水而叠，因水而活，尽显山水交融之“假山真水”的无限情趣。\r\n蠡园是蠡湖风景区中风景最盛的地方，长长的柳堤，临水的长廊，细巧的小桥，绚丽的轩亭，无论站在哪个点上，从什么角度去观赏，都是美的如诗如画。\r\n', 0, 621, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\蠡园.jpg'),
(5, '留园', '古典园林之典范', '留园位于江苏苏州，是中国著名古典园林，以园内建筑布置精巧、奇石众多而知名。与苏州拙政园、北京颐和园、承德避暑山庄并称中国四大名园。\r\n留园原是明嘉靖年间太仆寺卿徐泰时的东园。园内假山为叠石名家周秉忠(时臣)所作。清嘉庆年间，刘恕以故园改筑，名寒碧山庄，又称刘园。同治年间盛旭人其儿子即盛宣怀(清著名实业家政治家，北洋大学(天津大学）南洋公学（上海交通大学）创始人）购得，重加扩建，修葺一新，取留与刘的谐音，始称留园。\r\n留园全园分为四个部分，在一个园林中能领略到山水、田园、山林、庭园四种不同景色：中部以水景见长，是全园的精华所在；东部以曲院回廊的建筑取胜，园的东部有著名的佳晴喜雨快雪之厅、林泉耆硕之馆、还我读书处、冠云台、冠云楼等十数处斋、轩，院内池后立有三座石峰，居中者为名石冠云峰，两旁为瑞云，岫云两峰；北部具农村风光，并有新辟盆景园；西区则是全园最高处，有野趣，以假山为奇，土石相间，堆砌自然。池南涵碧山房与明瑟楼为留园的主要观景建筑。留园内的建筑景观还有表现淡泊处世之坦然的“小桃源（小蓬莱）”以及远翠阁、曲溪楼、清风池馆等。\r\n', 0, 547, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\留园.jpg'),
(6, '梅园', '为天下布芳香', '梅园位于无锡西郊的东山和浒山南坡，距市区7公里，距太湖1.5公里，园内遍植梅树，是江南著名的赏梅胜地之一。解放前曾是红色资本家荣毅仁的私家花园梅园。\r\n无锡梅园为著名民族工业家荣宗敬、荣德生兄弟以“为天下布芳香”的宗旨而建。此园遥临太湖烟波，背倚龙山翠屏，倚山建园，以梅饰山，近山远水，虚实相映，构成一幅天然图画。早春季节，梅花盛开，这里一片“香雪海”，香气馥郁，沁人心肺。\r\n梅园以老藤、古梅、新桂、奇石来显示出它的高雅古朴风格。园内植梅5500多株，品种繁多。著名的有素白洁净的玉蝶梅，有花如碧玉萼如翡翠的绿萼梅，有红颜淡妆的宫粉梅，有胭脂滴滴的朱砂梅，有浓艳如墨的墨梅，有枝杆盘曲矫若游龙的龙游梅等。还有造型幽雅、虬枝倒悬、枯树老干、疏影横斜的梅桩艺术盆景。园林设计者根据地势高低，结合梅园特点，以梅饰山，倚山植梅，梅以山而秀，山因梅而幽，别具特色。园内还有天心台等步步登高的观赏景点。\r\n', 0, 667, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\梅园.jpg'),
(7, '绮园', '江南典型私家园林', '绮园为中国十大名园之一，位于浙江海盐武原镇绮园弄，占地14.8亩。该园原为明代废园，后冯氏在此建园，人称冯家花园。为江南典型私家园林风格。\r\n绮园园主冯缵斋系清代诗人、剧作家黄燮清之次婿，黄家先后拥有拙宜园和砚园，黄燮清将两园作为次女黄秀陪奁。清咸丰年间（1851-1861），两园均遭兵火毁坏，同治六年，冯缵斋集两园山石精粹，并添置一些太湖石，修筑此园，同治十年初具规模。后又续建了亭台楼阁等，增设景点，并将其命名为绮园，意为“妆奁绮丽”。\r\n整个园林的建造，妙用了“水随山转，山因水活”的叠山理水园论。其特点是以树木山池为主，略点缀建筑，与今日以风景为主的造园手法相近；园自成一区，不附属于住宅区；用大面积水域，以聚为主，散为辅，水随山转、山因水活；大假山前后皆有丘壑，与苏州园林因面积小而略其背面的做法不同。园从西侧入口，中建花厅，前架曲桥，隔池筑假山，水绕厅东流向北，布局与苏州拙政园相近，水穿洞至后部大池。园内有潭影九曲、蝶来滴翠、晨曦罨画、海月小隐、古藤盘云、幽谷听琴、风荷夕照、美人照镜、百鸟鸣春、泥香三乐等景点。\r\n', 0, 572, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\绮园.jpg'),
(8, '泉山森林公园', '自然与人文的交汇', '徐州泉山森林公园位于徐州市三环南路，是1992年国家林业部批准成立的徐州环城国家森林公园的核心景区。\r\n景区由五座山峰组成，又称“五老峰”。最高峰东泉山海拔238米，为徐州市区第一高山，可俯瞰云龙湖风光，远眺徐州市区全貌。50多年前，这里还是岩石裸露的童山秃岭，解放后，经过50多年的植树造林，终于成功营造侧柏针叶林，森林覆盖率达95%以上。景区动植物资源十分丰富：有种子植物约590种，能明显反映出本地区植物区系的过渡性；动物200多种，常见的哺乳类动物有猕、狐狸、黄鼬、野兔、刺猬等，鸟类有鹦鹉、喜鹊、啄木鸟、黄雀、柳莺、斑鸠、百灵、八哥等；水禽有骨顶鸡、野鸭、大雁、天鹅等，各类小型软体动物及昆虫更是不计其数。景区主要景点有鸟悦园、天鹅湖、虚怀园、果老洞、烧烤营、阳光广场、滑雪滑草场、桃花园等。\r\n景区自然景观和人文景观相互融合，具有“绿、奇、秀、幽、野”的特色，被称作是“徐州绿肺”、“天然氧吧”。泉山森林公园已经成为徐州及周边地区市民森林生态旅游的主要景区之一。\r\n', 0, 644, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\泉山森林公园.jpg'),
(9, '狮子林', '妙夺天工的奇景', '狮子林位于江苏苏州市市城东北园林路，为苏州四大名园之一，至今已有650多年的历史。因园内“林有竹万，竹下多怪石，状如狻猊（狮子）者”，又因天如禅师维则得法于浙江天目山狮子岩普应国师中峰，为纪念佛徒衣钵、师承关系，取佛经中狮子座之意，故名“狮子林”。\r\n狮子林既有苏州古典园林亭、台、楼、阁、厅、堂、轩、廊之人文景观，更以湖山奇石，洞壑深遂而盛名于世，素有“假山王国”之美誉。\r\n狮子林假山，群峰起伏，气势雄浑，奇峰怪石，玲珑剔透。假山群共有九条路线，21个洞口。横向极尽迂回曲折，竖向力求回环起伏。游人穿洞，左右盘旋，时而登峰巅，时而沉落谷底，仰观满目迭嶂，俯视四面坡差，或平缓，或险隘，给游人带来一种恍惚迷离的神秘趣味。“对面石势阴，回头路忽通。如穿九曲珠，旋绕势嵌空。如逢八阵图，变化形无穷。故路忘出入，新术迷西东。同游偶分散，音闻人不逢。变幻开地脉，神妙夺天工”就是狮子林的真实写照。\r\n', 0, 613, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\狮子林.jpg'),
(10, '网师园', '宅园合一的私家园林', '网师园为苏州四大名园之一，是苏州园林中极具艺术特色和文化价值的代表作品。网师园始建于宋淳熙初年(公元1174年)始称“渔隐”，几经沧桑变更，至清乾隆年间(公元1765年前后)，定名为“网师园”，并形成现状布局。至今几易其主，围主多为文人雅士，且各有诗文碑刻遗于园内，历经修葺整理，最终形成了这一古典园林中的精品杰作。网师园为典型的宅园合一的私家园林。住宅部分共三进，自大门至轿厅、万卷堂、撷秀楼，沿中轴线依次展开，主厅“万卷堂”屋宇高敞，装饰雅致。\r\n全园布局外形整齐均衡，内部又因景划区，境界各异。园中部山水景物区，突出以水为中心的主题。水面聚而不分，池西北石板曲桥，低矮贴水，东南引静桥微微拱露。环池一周叠筑黄石假山高下参差，曲折多变，使池面有水广波延和源头不尽之意。\r\n园内建筑以造型秀丽，精致小巧见长，尤其是池周的亭阁，有小、低、透的特点，内部家具装饰也精美多致，网师园意谓“渔父钓叟之园”，园内的山水布置和景点题名蕴含着浓郁的隐逸气息。全园面积仅8亩多，做到了感觉宽绰而不显局促，主题突出，布局紧凑，小巧玲珑，清秀典雅，是苏州中型古典园林的代表作品。\r\n', 0, 632, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\网师园.jpg'),
(11, '西园', '园林与艺术的融合', '西园旧址在西街计家弄内，系明代朱氏别业，后出让给孙氏。园内有树木、花草、假山、亭池等，风景优美。东侧假山上有“听涛轩”茶室，因假山上有白皮松1株，高数丈，风来稷稷有声，故名。\r\n1920年冬天诗人柳亚子来西塘，曾住西园并与西塘南社社友在西园摄影留念，题名为“西园雅集第二图”。1990年3月，在镇西善兴建了总面积16.6亩的公园，为纪念柳亚子来西园，名为“西园”。入园处小桥流水，石狮门厅，园内环绕砖砌花格游廊、水榭、曲桥、假山、凉亭、人工瀑布。\r\n现在园内有“朱念慈扇面书法艺术馆”、“百印馆”、“南社陈列室”等展厅。“朱念慈扇面书法艺术馆”展出了国家级工艺美术大师朱念慈先生的精品扇面一百余件。\r\n', 0, 617, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\西园.jpg'),
(12, '锡惠公园', '山与水之间的明珠', '锡惠公园在江苏无锡市西郊,以锡山、惠山命名。包括锡山的全部和惠山东麓及连接两山的映山湖。锡惠公园把两山合成一园，内容丰富多彩，展现了南朝以来各个朝代的历史文化古迹，流传着许多生动的人文传说。\r\n它紧偎于京杭运河之旁，倚锡山、惠山而筑并名，山水灵秀，文化灿烂，有无锡“露天博物馆”和“精神家园”之誉。锡山是九龙山龙头上的一颗明珠。锡山顶上的龙光塔，又是无锡城市的风景标志之一。惠山古称华山、历山、西照山，相传西域僧人惠照曾居此处，故唐以后称惠山。山有九峰，婉蜒如龙，又称九龙山。山峰高近330米。有天下第二泉、龙眼泉等十余处泉眼，故俗称惠泉山。\r\n锡惠公园是拥有国内外知名度和广泛影响力，文物古迹众多、山水林泉俱佳的大型综合性城市公园，著名的风景名胜区。是首批国家重点风景名胜区——太湖风景名胜区的十四个核心景区之一，是国家重点公园，国家AAAA级旅游景区。\r\n', 0, 553, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\锡惠公园.jpg'),
(13, '冶春园', '为瘦西湖添美景', '冶春园位于扬州市，原是清代诗人王渔洋的私园，位于大虹桥以西，解放后，扬州市为增添瘦西湖沿岸景色，在乾隆御码头以西，将沿湖的升庆茶社、香影廊茶社、冶春花社等连成一片，建成园林，取用“冶春园”旧名。\r\n    冶春园选址好，漫步曲径之上，左为湖水，右为山丘。这山，虽然是一段丘阜，只因园筑其下，看似山，实非山也，可见当初构园者的构思精妙。水更有趣，说是湖，只不过是护城河的一段与市内小秦淮的河水在此汇合，形成一处较为宽阔的水面而已。但这片水确有道理，向东看，两道石壁上一桥，此为砖砌拱桥，名为天宁桥，桥东西上下各有一层白石围栏，随势造景，弯曲自如，河岸上有成片的女贞、松柏、梧桐、槐树、古木屈曲，浓荫遮天，把喧嚣的马路完全遮挡，制造出一片人工静域。\r\n狭长的瘦西湖从冶春园南面而过，临湖筑有草庐水榭，古朴清雅。凭栏观景，树影婆娑，湖水涟漪，清闲中别具情趣。园内还陈列着数万盆扬派盆景，数千尾各色金鱼，以及各种名贵的岛雀、花木，更添满园春色。这里园林和茶肆结合，在此游园、赏景、品茗、小吃，既领略了当地民间的乡风习俗，亦享受了古朴风情的自然乐趣。园内建有水绘阁、香影廊、餐英别野、问月山房等古典建筑，临水而筑，水景天成，以水取胜。\r\n', 0, 708, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\冶春园.jpg'),
(14, '颐和园', '皇家园林博物馆', '颐和园位于北京西北郊海淀区内，距北京城区15公里，是我国现存规模最大，保存最完整的皇家园林之一，也是享誉世界的旅游胜地之一。\r\n颐和园是利用昆明湖、万寿山为基址，以杭州西湖风景为蓝本，汲取江南园林的某些设计手法和意境而建成的一座大型天然山水园，也是保存得最完整的一座皇家行宫御苑，被誉为皇家园林博物馆。颐和园主要由万寿山和昆明湖两部分组成。万寿山后山、后湖古木成林，有藏式寺庙，苏州河古买卖街。后湖东端有仿无锡寄畅园而建的谐趣园，小巧玲珑，被称为“园中之园”。颐和园整个园林艺术构思巧妙，在中外园林艺术史上地位显著，是举世罕见的园林艺术杰作。\r\n颐和园景区规模宏大，园内建筑以佛香阁为中心，园中有景点建筑物百余座、大小院落20余处，3555古建筑，面积70000多平方米，共有亭、台、楼、阁、廊、榭等不同形式的建筑3000多间。古树名木1600余株。其中佛香阁、长廊、石舫、苏州街、十七孔桥、谐趣园、大戏台等都已成为家喻户晓的代表性建筑。\r\n', 0, 631, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\颐和园.jpg'),
(15, '颖园', '园林与艺术的相会', '颖园位于湖州市南浔区南浔古镇便民街的皇御河畔，是俗称南浔“八牛”之一的清朝富商陈熊的私家住宅花园，占地11.07亩，始建于清同治元年（公元1862年），于光绪六年（公元1875年）落成，系南浔镇文物保护单位。\r\n颖园以玲珑剔透、紧凑多姿、清幽雅致见胜。颖园内古木葱郁，有百年以上的广玉兰、香樟和紫藤等绿荫。楼、阁沿池而筑，太湖石假山堆垒错落有致，曲径通幽，可拾级登临。“赏月楼”出挑在菏花池一侧，楼边有一排紫红木的玻璃长窗，画梁雕柱，古色古香。池另一侧筑有一幢乌瓦粉墙“养心榭”，原为园主吟诗作画之处。“玉香阁”也建于池西，砖瓦木结构的建筑，线条洗练，别具匠心，若登临楼上可饱览园中景色。更吸引人的是园中假山上有一精巧的梅石亭，亭中有一块珍贵的梅石碑，高138厘米，宽79厘米，碑上梅石图为清代著名书法家王礼（公元1813~1879年）的晚年力作，刀法苍劲，乃石雕中之上品。\r\n颖园还以各种雕刻见长，有砖雕、石雕、木雕等，至为珍贵，几乎是一座小型雕刻艺术馆。如“养心榭”的门窗上是一幅幅刀法精湛、造型逼真的黄杨木雕，每幅均有名人书法。其中一幅《耕织图》，男耕女织，粗犷与细腻结合，形象栩栩如生。\r\n', 0, 577, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\颖园.jpg'),
(16, '拙政园', '精美的私家园林', '拙政园位于江苏省苏州市平江区，是苏州四大古名园之一，也是苏州园林中最大、最著名的一座，被列入《世界文化遗产名录》，堪称中国私家园林经典。\r\n拙政园是南园林的代表，全园以水为中心，山水萦绕，厅榭精美，花木繁茂，充满诗情画意，具有浓郁的江南水乡特色。花园分为东、中、西三部分，东花园开阔疏朗，中花园是全园精华所在，西花园建筑精美，各具特色。园南为住宅区，体现典型江南民居多进的格局。园南还建有苏州园林博物馆，是国内唯一的园林专题博物馆。\r\n拙政园的建园艺术以水见长，自然典雅，庭院错落，花木为胜。主要可供游览的景点有“兰雪堂”，取李白“独立天地间，清风洒兰雪”诗意而名，堂中南面置漆雕拙政园全景图，北向饰翠竹图；“涵青亭”居于一隅，空间范围比较逼仄。整座亭子犹如一只展翅欲飞的凤凰，给本来平直、单调的墙体增添了飞舞的动势；“盆景园”拙政园西部一片清影摇曳的竹篱墙内，集萃着苏派盆景的精品，它被称为“名园瑰宝”，拥有50余个品种，近万盆盆景，等等不胜枚举。漫步拙政园，相信你一定会为中国古典园林艺术的博大精深而感叹！\r\n', 0, 725, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\拙政园.jpg'),
(17, '醉园', '水乡西塘的缩影', '醉园位于嘉兴市西塘，共有三进院落，面积二百多平方米，是一个老宅花园，游客举步寻游，池石玲珑，回廊通幽，翠竹生妍，秀色醉人。\r\n醉园的意思有三层：一是因“醉经堂”——醉读经书之意；二是园内景色醉人；三是沉醉于版画艺术。“醉经堂”为王氏世祖王志熙修建，王志熙是清乾隆年间的著名书画家，攻楷、行、草，擅诗、画，园内至今还留有他的墨迹。现醉园主人王亨的先父王慕仁也擅长书法，园内有一副对联“烟开兰叶香风暖，岸爱桃花锦浪生”就是他的作品。\r\n园小而别致，尤其是在花瓶门对面朝南的墙上刻着“醉经堂”三个大字，为老宅增添了不少文化韵味。如今的醉园是“醉经堂”的沿续，书厅“艺香斋”现辟为“水乡风韵”版画陈列。主人的版画作品以反映江南水乡风情为主，如《江南雨》、《遥望环秀桥》、《静静的长廊》、《里仁港小景》。这些版画是水乡西塘的缩影，从版画上看西塘，可以找到水乡的另一种感觉。\r\n', 0, 636, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\醉园.jpg'),
(18, '寄畅园', '气机贯通而雅致', '寄畅园坐落在无锡市西郊东侧的惠山东麓，惠山横街的锡惠公园内，毗邻惠山寺。园址原为惠山寺沤寓房等二僧舍，明嘉靖初年（约公元1527年前后）曾任南京兵部尚书秦金（号凤山）得之，辟为园，名“凤谷山庄”。秦金殁，园归族侄秦瀚及其子江西布政使秦梁。嘉靖三十九年（公元1560年），秦瀚修葺园居，凿池、叠山，亦称“凤谷山庄”。秦梁卒，园改属秦梁之侄都察院右副都御使、湖广巡抚秦燿。万历十九年（公元1591年），秦燿因座师张居正被追论而解职。回无锡后，寄抑郁之情于山水之间，疏浚池塘，改筑园居，构园景二十，每景题诗一首。取王羲之《答许椽》诗：“取欢仁智乐，寄畅山水阴”句中的“寄畅”两字名园。1952年秦氏后人秦亮工将园献给国家，无锡市人民政府进行整修保护，逐渐恢复古园风貌。\r\n寄畅园属山麓别墅类型的园林。寄畅园的面积为14.85亩，南北长，东西狭。园景布局以山池为中心，巧于因借，混合自然。假山依惠山东麓山势作余脉状。又构曲涧，引“二泉”伏流注其中，潺潺有声，世称“八音涧”，前临曲池“锦汇漪”。而郁盘亭廊、知鱼槛、七星桥、涵碧亭及清御廊等则绕水而构，与假山相映成趣。园内的大树参天，竹影婆娑，苍凉廓落，古朴清幽。以巧妙的借景，高超的叠石，精美的理水，洗练的建筑，在江南园林中别具一格。\r\n寄畅园的成功之处在于它“自然的山，精美的水，凝练的园，古拙的树，巧妙的景”。难怪清朝的康熙、乾隆二帝曾多次游历此处，一再题诗，足见其眷爱赏识之情。北京颐和园内的谐趣园，圆明园内的廓然大公（后来也称双鹤斋），均为仿无锡惠山的寄畅园而建。\r\n', 0, 620, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\寄畅园.jpg'),
(19, '景山公园', '俯瞰紫禁城', '景山公园位于中国北京市西城区的景山前街，西临北海，南与故宫神武门隔街相望，是明、清两代的御苑。公园坐落在明清北京城的中轴线上，公园中心的景山，曾是全城的制高点。站在山顶可俯视全城，金碧辉煌的古老紫禁城与现代化的北京城新貌尽收眼底。\r\n景山又称煤山、镇山、万岁山，是一座人造山。现在其海拔高度89.2米，相对高度 45.7米。明永乐十八年（1420年）在修建北京城时，用拆除元朝宫殿的渣土和挖掘紫禁城护城河的泥土，在元代延春阁的基础上堆筑起一座土山，取名万岁山，又称镇山。考虑到所有的材料只能通过人、畜运输，其规模令人印象深刻。山上松柏遍布，园内鹤鹿成群。御园北部建有寿皇殿等宫殿楼阁，种植众多名贵果树。每逢重阳佳节，皇帝都要登山宴饮。\r\n在元、明、清三代，景山及其附属建筑不仅是一座供游赏的皇家园林，还具有习射、停灵、祭祖、官学、躬耕、戏曲、宗教等多重功能。目前公园占地23公顷，园内松柏葱郁，游人如织，是北京皇城内独具特色的所在。景山的主要建筑有：三座园门（景山门、山左里门、山右里门）；祭祀孔子的绮望楼；五座峰亭（观妙亭、周赏亭、万春亭、富览亭、辑芳亭）；景山山后的寿皇殿；东侧的永思殿和观德殿以及护国忠义庙。\r\n\r\n', 0, 602, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\景山公园.jpg'),
(20, '圆明园', '万园之园', '圆明园坐落在北京西郊海淀区，与颐和园紧相毗邻。始建于康熙46年（1709年），亦称“圆明三园”，是圆明园及其附园长春园、万春园的统称，是清朝帝王在150余年间创建和经营的一座大型皇家宫苑。\r\n圆明园继承了中国三千多年的优秀造园传统，既有宫廷建筑的雍容华贵，又有江南水乡园林的委婉多姿，又吸取了欧洲的园林建筑形式，曾以其宏大的地域规模、精美的建筑景群、丰富的文化收藏和民族文化内涵而享誉于世，被誉为“一切造园艺术的典范”和“万园之园”。“圆明园的园林造景多以水为主题，因水成趣，其中不少是直接吸取江南著名水景的意趣。圆明园后湖景区，环绕后湖构筑九个小岛，是全国疆域《禹贡》“九洲”之象征。各个岛上建置的小园或风景群，既各有特色，又彼此相借成景。\r\n圆明园还有个显著特点，就是大量仿建了全国各地特别是江南的许多名园胜景。乾隆皇帝弘历曾经六次南巡江浙，多次西巡五台，东巡岱岳，巡游热河、盛京（即沈阳）和盘山等地。每至一地，凡他所中意的名山胜水、名园胜景，就让随行画师摹绘成图，回京后在园内仿建。圆明园不仅以园林著称，而且也是一座收藏相当丰富的皇家博物馆。法国大作家雨果曾说：“即使把我国所有圣母院的全部宝物加在一起，也不能同这个规模宏大而富丽堂皇的东方博物馆媲美”。\r\n', 0, 627, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\圆明园.jpg');

