--
-- 表的结构 `travel_lake`
--

CREATE TABLE IF NOT EXISTS `travel_lake` (
  `NewsID` int(11) NOT NULL,
  `Name` varchar(255) DEFAULT NULL,
  `Title` varchar(255) DEFAULT NULL,
  `Content` text,
  `Level` int(11) DEFAULT '0',
  `BrowseNumber` int(11) DEFAULT '0',
  `Picture` varchar(255) DEFAULT NULL
) ENGINE=MyISAM AUTO_INCREMENT=8 DEFAULT CHARSET=utf8;

--
-- 转存表中的数据 `travel_lake`
--

INSERT INTO `travel_lake` (`NewsID`, `Name`, `Title`, `Content`, `Level`, `BrowseNumber`, `Picture`) VALUES
(2, '大明湖', '泉城明珠', '大明湖位于济南市区中心、旧城区北部，与趵突泉、千佛山并称济南三名胜。济南号称“泉城”，有泉水百余处，其中名泉72处。明湖即是由众泉汇流而成的天然湖泊，素有“泉城明珠”的美誉。早在唐宋时期，大明湖就以其撼人心弦的美景而闻名四海。泉水由南岸流入，水满时从宋代修建于北岸的北水门流出，湖底由不透水的火成岩构成，因而湖水“恒雨不涨，久旱不涸”，常年保持较固定的水位。\r\n公园面积86公顷，其中湖面46公顷。湖上有历下亭、汇泉堂、湖心岛等大小岛屿六处，公园自然景观优美宜人。湖水波光粼粼，鸢飞鱼跃，游船穿行。大明湖自古遍生荷莲，湖畔垂柳依依，花木扶疏，“四面荷花三面柳，一城山色半城湖”是她风景的最好写照。公园的人文景观十分丰富，历下亭、铁公祠、南丰祠、北极庙等30余处名胜古迹掩映于绿树繁花之间，展现出悠久的历史文化。2009年，大明湖荣膺中国世界纪录协会“中国第一泉水湖”称号。\r\n', 1, 606, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\大明湖.jpg'),
(3, '高邮湖', '水质良好', '高邮湖位于高邮市境内，又名璧瓦湖，江苏省第三大湖，水域总面积780平方公里。高邮湖在高邮市境内水域面积431.5平方公里。高邮湖属浅水型湖泊，同时又是一座“悬湖”。作为淮河的入江水道，淮河水的90%都要通过三河闸泄入高邮湖，后经新民滩、邵伯湖宣泄入江。\r\n高邮湖水域宽阔、水质良好，为各种鱼类、鸟类和水生植物的生长提供了得天独厚的生态环境。高邮湖盛产青、白、黑、鳊、鲤、鳗等鱼类，尤以青河虾、银鱼、高邮湖蟹闻名遐迩；水生植物有芡实、菱角、荷藕、莼菜等；湖面上有水鸟鸥、鹭、鹤以及野鸭等常出没于芦荡。\r\n海市蜃楼是高邮湖的一大奇观。万寿汊河人称之为：“显陈州府”。在天长境内，观看海市蜃楼的最佳场所是万寿乡汊河村汀溪尖附近，运气好的话，在下午三四点左右，天气略有薄雾，在大王庙与老鼠嘴之间就会现出一条街道，街道若隐若现，虚无飘渺，街上有贩夫走卒，挑葱卖菜，渔樵耕涣，商贾行人，熙熙攘攘，热闹非凡。\r\n高邮湖旅游景点资源也极其丰富，方圆内有诸如镇国寺，清水潭旅游度假区，高邮市东湖度假村，高邮湖旅游度假区，闵桥荷花荡，界首芦苇荡湿地公园，神居山文化旅游度假区，高邮西堤风光带等重点景区。\r\n', 0, 596, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\高邮湖.jpg'),
(4, '洪泽湖', '四大淡水湖之一', '洪泽湖位于江苏省西部淮河下游，是中国第四大淡水湖，古称富陵湖，两汉以后称破釜塘，隋称洪泽浦，黄河南徙经泗水在淮阴以下夺淮河下游河道入海，淮河失去了入海水道，在盱眙以东潴水，原来的小湖便扩大成为洪泽湖。\r\n历史悠久的洪泽湖，给人们留下很多宝贵景点，现存的主要景点有万顷碧波，港坞帆墙，百里长堤，泄洪大闸，镇水铁牛，奠淮犀牛，老君遗踪，龟山晚眺，明陵石刻，临淮观日，墓园春晓等。此外，洪泽湖还流传着许多传说，“九牛二虎一只鸡，彩船带姑娘，乾隆皇帝找父亲，刘基造堤，老子与劳子山，小龙眼等，宛若一件件文化瑰宝，伴着洪泽湖水世世代代的流淌和荡漾着。\r\n洪泽湖面积2069平方公里，最深处5.5米。湖水主要经由三河泄入高邮湖，再经邵伯湖入里运河，到三江营入长江，是为入江水道。洪泽湖水生资源丰富，湖内有鱼类近百种，以鲤、鲫、鳙、青、草、鲢等为主，尤以洪泽湖蟹闻名。此外，洪泽湖的水生植物非常著名。芦苇几乎遍布全湖，繁茂处连船只也难以航行。莲藕、芡实、菱角在历史上享有盛誉，曾有“鸡头、菱角半年粮”的说法。\r\n', 0, 699, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\洪泽湖.jpg'),
(5, '骆马湖', '优越的地理位置', '骆马湖，江苏省四大湖泊之一。地跨徐州、宿迁两市结合部。湖区北起堰头村圩堤,南至扬河滩闸口，西连中运河，东临马陵山南蘼—嶂山岭。明代后期万历年间黄患对泗运压抑和蹂躏愈演愈烈,为保障漕运,明采取避黄开河之举。自河开挖后,运北沂武诸水由于失去了泗通道,被迫寻找出路，最后阻塞在今新沂市境西南部的低洼地带。因东去有嶂山岭阻塞,就把原有的周湖、柳湖、黄墩湖、屿头湖等几个小湖连成一体,形成了骆马湖。\r\n骆马湖水多来自沂蒙山洪和天然雨水，沿湖无工业污染，常年水体清澈透明，湖滩浅水中生长密密扎扎的芦苇和众多浮游生物，为鱼类生产提供了良好的生态环境和水资源。湖中鱼类达56种，尤以银鱼、青虾、螃蟹、龙虾最佳。骆马湖银鱼，通体洁白透明如美玉，形状如根根银条，肉质细嫩鲜美。骆马湖大青虾，色青、个大、皮薄、肉饱，名闻遐迩。以大青虾为主要原料制作的“醉虾”，更是别有一番风味。骆马湖螃蟹，个体硕大，雄蟹脂白如玉，雌蟹脂黄似金，是难得的风味佳肴。\r\n骆马湖四周的一座座巨大水利工程，雄伟壮观,为国家一级水工建筑物，江苏省大型水闸之一。皂河抽水站，出水口通入骆马湖，进水口接连邳洪河，接转中运河，为国家南水北调第六梯级工程。\r\n', 0, 605, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\骆马湖.jpg'),
(6, '太湖', '贯通鱼米之乡的淡水湖', '太湖，中国五大淡水湖之一，位于江苏省南部，长江三角洲的南部,地跨苏州、无锡、常州三市；全部水域在江苏省境内，湖水南部与浙江省相连，是华东最大湖泊，也是中国第三大淡水湖，是中国著名的环太湖风景名胜区。\r\n太湖流域气候温和，特产丰饶，自古以来就是闻名遐迩的鱼米之乡。太湖水产丰富，盛产鱼虾，素有“太湖八百里，鱼虾捉不尽”的说法。银鱼、白鱼、白虾为太湖特产。银鱼无刺，鲜嫩爽口；白鱼肉质细腻，刺软；白虾壳薄，晶莹味鲜。还有太湖蟹，青壳、白肚、金爪、黄毛、体壮，个大体重，蟹黄肥厚，肉质细嫩，腴美异常。\r\n太湖景区包含众多风景区，主要的景色集中在苏州的西山景区和东山景区、无锡的蠡湖景区。太湖是平原水网区的大型浅水湖泊，湖区号称有48座岛、72座峰，湖光山色，相映生辉，有“太湖天下秀”之称。太湖景区旅游资源密集，可就近去观苏州园林、访无锡三国城水浒城、观常州洞天世界。\r\n', 0, 795, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\太湖.jpg'),
(7, '微山湖', '荷花美景', '微山湖位于山东省南部的微山县，亦名南四湖，由微山、昭阳、独山、南阳四个彼此相连的湖泊组成。它是中国第五大淡水湖，也是中国北方最大的淡水湖，是中国荷都、北方水乡、铁道游击队故乡。\r\n微山湖物产丰富，向来有“日出斗金”的说法，鱼类现有78种，以鲤鱼为主，水生植被盖度达89.9%，有“水下森林”的美誉。受国家一、二、三级保护的植物主要有水蕨、粗梗水蕨、中华秸缕草和野大豆等9种。微山湖矿产资源丰厚，煤炭已探明储量127亿吨，是中国重点煤炭基地之一。\r\n微山湖以荷花最为出名，其洋洋洒洒地铺到湖面上，有的多达几十万亩。区内有微山岛风景区和南阳古镇风景区。微山岛是微山湖中最大的岛屿，上有颇多历史文化遗迹，包括“三贤墓”：微子墓、目夷墓、张良墓等。微山湖文化古迹众多，境内有殷周微子墓、汉初张良墓、春秋目夷墓、伏羲陵（庙）、仲子路庙、六合泉、圣母泉、郗公墓、古木兰寺、泰山庙以及大量的庙宇亭台、古碑刻石、汉画像石宋老鹅庄，宋氏宗祠，石碾等古迹，还有铁道游击队纪念碑供人缅怀，具有较高的考古与观赏价值。微山湖的动人的美景也同时吸引了重多影视作品都在这里取景拍摄，如电影《铁道游击队》、《月夜》等。\r\n', 0, 691, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\微山湖.jpg');

