) ENGINE=MyISAM AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;

--
-- 转存表中的数据 `travel_park`
--

INSERT INTO `travel_park` (`NewsID`, `Name`, `Content`, `Level`, `BrowseNumber`, `Picture`) VALUES
(2, '江苏无锡运河公园', '运河公园西临惠山，三面环水。位于无锡古运河与新运河交会处的运河公园占地约16万平方米，其中绿地面积约占60%，是目前城区面积最大的文化主题公园。园基原为无锡米市和新中国成立后粮油运输、仓储、加工业的集中地，现在结合20世纪工业遗产保护，该园将成为集米市文化、运河文化、体育文化、娱乐文化、生态绿化、旅游休闲为一体的综合性开放式大型城市花园。\r\n据介绍，运河公园的景点较多：一座同步音乐水体景观，喷泉可随音乐节拍“翩翩起舞”；两座保护修复的文物建筑，分别为1910年建的九丰面粉厂与清光绪年问的储业公会；三组艺术石刻，用以表现无锡历史事件和运河公园的文脉，分别为《运河无锡图纪》浮雕长卷、古画线刻和《歌唱祖国》浮雕，全长218米的《运河无锡图纪》最为抢眼，长卷以汉白玉雕刻而成；四家特色展馆，包括体育类的何振梁与奥林匹克陈列馆，美术类的书画馆，音乐类的民乐馆，建筑类的古运河模型展示馆。\r\n运河公园还兼具水上游与陆上游功能，公园内包括兼顾新运河与古运河水上旅游休闲的两处游船码头(新运河漕运码头与古运河码头)；便于散步游览的两条亲水步行路线，市民游客可沿河而行或环园而行；两个特色体育运动广场，可满足不同年龄层次的市民活动；两片景观绿地，面积约5000平方米的大草坪与8000平方米的树林丘岗；两幢可进行室内活动的会馆，主要面向静态休闲的人群。\r\n', 1, 590, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\无锡运河公园.jpg'),
(3, '北京通州运河公园', '通州运河公园规划全线总长4600米，是集休闲娱乐教育，体育竞技健身、水上游乐观光为一体的综合性场所，也是京东地区面积最大的城市公园。北起北运河源水岛南端，南至六环路，西起滨河西路，东至北运河防洪堤。规划全线总长4600米，宽600-800米，总面积368万平方米，其中水面占92万平方米，绿化率达95%；自2004年11月1日至 2007年5月完成一期建设，景观改造面积超过100万平方米。\r\n据史书记载，通州历来为京东交通要道。漕运、仓储重地。运河漕运，国脉所系，历朝视通州为肘腋，于是通州有了“乃九重肘腋之上流，六国咽喉之雄镇”的美誉。一些回忆文章说，当时南来北往的船队、游客在这里相互交织、川流不息。码头、粮仓、商家、酒肆、客店，一应俱全。文化氛围也相当的浓厚，潞河书院里鸿儒往来，一些庙宇里香火不断。由运河而带动起来的通州，曾经极度地繁荣兴旺。\r\n运河已申遗成功，“京杭大运河是世界上最长的人工河，也是中国唯一南北走向的长河，它和长城一样是中国古代的两大工程奇迹。”大运河的开通，历史上有三次大开凿，最早一次是公元前486年，吴王夫差所开凿的邗沟。第二次是隋炀帝于公元605年至609年，重开邗沟并向北延伸。第三次是元朝至元二十年（今1283年），大力施工，形成至今之规模。郭守敬主持又开通惠河，于是南来之船可溯通惠河，直达京师（北京）城内。\r\n', 0, 653, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\高邮湖.jpg'),
(4, '大运河通州森林公园', '位于北京通州新城北运河两侧，北起六环路潞通桥、南至武窑桥，河道全长约8.6公里，左堤长8191米，右堤长3639米，总建设面积713公顷（约10700亩），其中水面面积约2500亩，绿化面积约8200亩。\r\n公园秉承“以绿为体，以水为魂，林水相依”的设计理念，以“生态优先、保护为主、坚持节约、创新管理”为原则，坚持“整体、特色、历史、综合利用”，构建了“一河、两岸、六园、十八景”。总体来讲，公园突出了四个方面的特点：生态功能完善，注重节能环保，文化底蕴深厚，区位优势明显。\r\n一河：是以北运河为中心展开规划，结合旅游观光的需要，沿岸建有5个功能各异的码头，作为水上游览的停靠地，便于游人上岸观赏。两岸堤路：是交通的路、安全的路，也是景观的路、导游的路。8－10公里的路程上，有起伏、有变化，有景致。堤岸绿化与运河景观互借互动。改变树林单纯、品种单一，融入复层种植、群落种植、混交林的基本概念。利用沿岸景致变化，形成了穿过树林的路、赏花的路和绚丽的路。六大景区分布在河道两侧，有潞河桃柳景区、月岛闻莺景区、银枫秋实景区、丛林活力景区、明镜移舟景区、高台平林景区。十八景点分布于六大景区之中，有桃柳映岸、榆桥春色、茶棚话夕、皇木古渡、长虹花雨、月岛画境、湿地蛙声、半山人家、银枫秋实、枫林茗香、大棚囤贮、风行芦荡、丛林欢歌、双锦天成、明镜移舟、平林烟树、枣红若涂、高台浩渺。\r\n', 0, 551, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\大运河通州森林公园.jpg'),
(5, '山东台儿庄运河国家湿地公园', '枣庄市台儿庄运河湿地公园是中国国内第一家以运河湿地为主的湿地公园。主要由涛沟河下游段、峄城大沙河分洪道下游段以及两河口之间的京杭运河段等河流湿地组成，将台儿庄城区包围其中，属典型的河流湿地。\r\n枣庄市台儿庄运河湿地公园是中国国内第一家以运河湿地为主的湿地公园。主要由涛沟河下游段、峄城大沙河分洪道下游段以及两河口之间的京杭运河段等河流湿地组成，将台儿庄城区包围其中，属典型的河流湿地。规划总面积2592公顷。其性质定位以湿地资源保护、修复为前提，以台儿庄运河湿地生态系统和历史文化为主要景观资源，以湿地观光、科普教育、度假休闲为主要内容的综合性湿地公园。在运河湿地公园的生态修复和保护工程上，着力维护湿地生态平衡、保护湿地生态功能、保障运河水质和湿地生物多样性，实现了生态系统的可持续发展。\r\n    京杭大运河是中华民族智慧的结晶。台儿庄城区内现存完好的3公里古运河河道及明清繁荣时期的古街、古巷、古码头等古城遗址，作为“活着的运河”和“京杭运河仅存的遗产村庄”，对世界文化史与人类文明史都有着不可估量的研究价值和利用价值。将其纳入台儿庄运河湿地公园规划区域，对其完整性、原本性施以强制性的基本保护与保存，具有战略性、学术性的高度。\r\n', 0, 692, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\山东台儿庄运河国家湿地公园.jpg'),
(6, '天津武清区北运河郊野公园', '为进一步改善全区的水生态环境、进一步提升武清区的对外形象、进一步推动武清的经济社会发展，结合市政府提出的“郊野公园”建设计划，2011年底武清区成立了北运河综合开发指挥部，计划利用2-3年的时间完成北运河剩余53km河道的开发工作，其中2012年重点实施北运河八孔闸至碱东路段10.8km的郊野公园建设。\r\n    该项工程重点突出“综合建设、联动发展”的建设理念，以运河景观带动周边发展、以周边配套反衬运河效应，形成了以运河为核心辐射至两路(东至武香路、西至103国道)的综合开发模式。主要包括滨水景观带、果林风光带、农业观光带和交通组织等建设内容。\r\n', 0, 716, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\天津武清区北运河郊野公园.jpeg');

