CREATE TABLE IF NOT EXISTS `travel_site` (
  `NewsID` int(11) NOT NULL,
  `Name` varchar(255) DEFAULT NULL,
  `Content` text,
  `Level` int(11) DEFAULT '0',
  `BrowseNumber` int(11) DEFAULT '0',
  `Picture` varchar(255) DEFAULT NULL
) ENGINE=MyISAM AUTO_INCREMENT=10 DEFAULT CHARSET=utf8;

--
-- 转存表中的数据 `travel_site`
--

INSERT INTO `travel_site` (`NewsID`, `Name`, `Content`, `Level`, `BrowseNumber`, `Picture`) VALUES
(2, '凤山水城门遗址', '中国大运河，作为中国大地上一度最繁忙的黄金水道，演绎出无尽的繁华、荣光和辉煌，又在多重历史因素的交互作用下无可奈何地走向断裂、静寂和没落。其实凤山门有水陆两门，元朝开始，钱江之水，“自龙山涌入凤山水门”，通过城内阡陌纵横的水道，出武林门水门，和京杭大运河连在一起。凤山门既是龙山河的北端端点，也是扼守江南运河通往钱塘江的咽喉。\r\n大运河之所以可以申报世界遗产，因为它拥有“天才的设想”、“消逝的漕运”、“建筑的范例”等几条重要价值组成。而每一条价值，都由若干个遗产点、段的独特性、历史性来支撑。“凤山水城门”，就是这样一个遗产点——这是杭州唯一真实存在的古城墙和古城门，像养在深闺的宝贝，差一点就被历史遗忘。杭州闹市区，沿着中山南路一直往南走，到了和中河路的交会处，就能看到凤山水城门横跨在中河上。\r\n城门北面，藏青色的石砖古朴而沉默。现在的城门两边都已经断头，用栅栏围了起来，只剩中间拱形的一段，顶部倒还是城墙的形状。转到南面，就看到了刻在拱门上方的“凤山水门”四个字。这是杭州唯一真实存在的古城墙和古城门，像养在深闺的宝贝，差一点就被历史遗忘——辛亥革命光复杭州后，杭州诸多城门在延续近半个多世纪的拆城墙运动中损毁，“十不存九”，1958年，庆春门残存的城墙，也被推倒，但凤山水城门却奇迹般地留了下来。\r\n', 1, 740, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\凤山水城门遗址.jpg'),
(3, '浚县黎阳仓遗址', '黎阳仓遗址位于河南省鹤壁市浚县伾山街道办事处东关村东，地处大伾山北麓，东邻黄河故道，东北距黎阳城遗址约1公里，西距卫河约1.5公里。因当地群众称此处为“囤上”，故又称“东关囤上遗址”。遗址因地处大伾山山麓，总体呈南高北低地形。遗址北部中心区域较为平坦，地面上为耕地，中心区域东侧高差1米～2米的断崖下种有大量杨树，西侧被民房占压，北侧断崖下临民房；遗址南部自西向东呈阶梯形，南侧有东关村与田庄村相通的水泥路，中间有已废弃的东西走向的沟路，地面上种有杨树，中间有一养猪场。\r\n黎阳仓是隋唐时期大运河沿岸重要的国家官署粮仓，其遗址长期以来备受史学界和考古学界的关注。为配合隋代大运河（永济渠）申报世界文化遗产工作的需要，去年11月，浚县聘请文物钻探队，对文献记载的黎阳仓所在地——大伾山北麓近10万平方米区域进行钻探调查。经过3次考古勘探，发现了与黎阳仓有关的主要遗迹，如仓城的城墙、仓窖、夯土台基、大型建筑基址、路、墓葬、灰坑等。\r\n根据文献，黎阳仓在隋朝建立，主要是隋唐时期使用。到北宋时期，隋唐黎阳仓仓窖之上，又曾建造了地上粮仓，地上粮仓约废弃于北宋末年。至于唐代中晚期黎阳仓因何而废，尚无科学依据。黎阳仓遗址的发现与发掘，对佐证鹤壁浚县隋唐大运河历史和大运河(永济渠)申报世界文化遗产具有重要意义。\r\n\r\n', 0, 692, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\浚县黎阳仓遗址.jpg'),
(4, '洛阳含嘉仓遗址', '含嘉仓遗址位于今洛阳老城区的北侧，是隋、唐时期大型官仓之一。东西宽约 600 米，南北长约 700 米，总面积 42 万平方米。1971 年开始对仓城遗址进行了发掘。在仓城内密集且有秩序地排列着 400 多座东西成行缸式地下粮窖，可储粮 580 多万石，占当时全国主要粮食储粮总数的一半。仓窖有大小之别，大窖口径为 18.5 米，小窖口径约 8 米，最深的距地表12 米。根据遗址中所出铭砖记载，大窖储粮一万数千石，小窖可储千石余。粮入窖时，要将储粮的时间、数量、品种、来源、仓窖位置及授领粮食的官员姓名，都要刻于砖上，放置窖中。含嘉仓城设有管理机构，且驻有军队，守护仓城。\r\n1972 年再次发掘时，发现在一座仓窖内保存着大约 50 万斤已炭化的小米，颗粒清晰可辨。含嘉仓的大小粮窖均为口大底小的圆缸形，其建造方法和结构较为科学。先从地面向下挖一口大底小的土窖，将窖底夯实，再用火烧硬，上面铺一层灰渣，以防潮湿，在灰渣上铺设木板，板上垫草，草上铺席。窖壁周围亦砌木板，在窖中分层堆放粮食，每层之间以席隔开，装满粮后，在窖口部位先盖一层 40～60 厘米厚的谷糠，其上盖席，最后用泥土密封窖口。这种仓窖，既可防火防虫，又能抗腐防盗。\r\n', 0, 735, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\洛阳含嘉仓遗址.jpg'),
(5, '柳孜运河码头遗址', '位于安徽省濉溪县百善镇柳孜村。柳孜原是隋唐大运河通济渠上的一个镇，因运河的开通而繁荣。通济渠唐宋时称汴河，流经濉溪县境40余公里，历隋、唐、宋三代五百余年，南宋时淤塞废弃。遗址内出土有唐宋时南、北方十几个窑口的大批瓷器，还发现唐代沉船八艘，出土三艘，清理出一座完整的宋代石筑码头，均属首次发现。柳孜镇，古称柳子镇，位于安徽省淮北市濉溪县境内，始建于东汉，因隋炀帝开掘的大运河之通济渠段穿镇而过而逐渐繁荣，成为唐宋时期淮北地区的政治、经济、军事和文化重镇，“运漕商旅，往来不绝”。\r\n据《宿州志》记载：柳孜为巨镇，有庙宇99座，井百眼。著名东晋音乐家桓伊和“竹林七贤”之一稽康就是生长在这里，可见当时柳孜文风之盛。然而，沧海桑田，瞬息万变，南宋光宗五年（公元1194年），黄河泛滥，洪水夺淮入海，通济渠从此淤塞，柳孜镇也因此衰落下来。一个世纪过去了，如今的柳孜镇又因安徽省文物局考古发掘队在这里的重大考古发现又一次吸引了世人的瞩目。八艘唐船、宋代码头、大批唐宋名窑瓷器的相继出土印证了这块土地昔日的繁华。二000年四月八日，中国国家文物局副局长郑欣淼在柳孜实地考察后，向世界郑重宣布：“柳孜隋唐大运河遗址的考古发掘是中国运河考古的重大成果，证明了大运河的流经路线，填补了中国运河考古的空白”。\r\n柳孜遗址还出土了大量陶瓷器，其中瓷器为多，其它为铜钱、铁器、石器等，陶器有灰、红色陶，釉陶和建筑陶等。器形有缸、盆、坛、和砖瓦等。铜钱均是方孔圆钱，有“开元通宝”、“嘉佑通宝”、“熙宁通宝”。铁器有三足釜、斧等，石器有磨、石柱、锚等。瓷器品种最多，有青、黄、白、黑、白地黑花、外黑或酱、内白等许多釉色，一般釉质较粗，少数青、白釉较细。瓷胎多为较粗的泛黄或灰色胎，少数白瓷胎洁白细腻，瓷器多素面，少数印花、刻花、三彩、点彩和堆贴文饰等。器形以碗数量最多，其次有罐、盆、三足器、杯、壶等。从瓷器的釉色和造型初步辨认窑口的有：寿州窑、肖窑、吉州窑、耀州窑、磁州窑、景德镇窑、建窑、定窑、越窑、长沙窑、均窑等隋、唐、宋时期的窑口，还有一些不明窑口的瓷器有待进一步确认。柳孜遗址出土的陶瓷器，数量之多，窑口之众，实属罕见，这为唐宋陶瓷业的生产、运销、外销等方面提供了珍贵的实物资料。\r\n', 0, 577, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\柳孜运河码头遗址.jpg'),
(6, '洛阳回洛仓遗址', '回洛仓始建于隋大业年间，是隋炀帝在洛阳周边设置的“国家粮仓”，其主要功能是为城内的皇室和百姓供应粮食。回洛仓内各个仓窖的大小基本一致，窖口内径10米，外径17米，深10米，规模巨大。每个仓窖储存的粮食在50万斤左右，整个仓城的储粮总数可达3.55亿斤。 2014年6月22日在卡塔尔多哈召开的联合国教科文组织第38届世界遗产委员会会议上，回洛仓作为中国申报的“中国大运河”的一部分成功入选世界遗产名录。\r\n整个仓城由仓窖区、管理区、道路和漕渠等几部分构成。其中，管理区位于仓城南侧，仓城内有东西、南北方向道路各一条。两条漕渠分别位于仓城西侧和仓城南侧。回洛仓遗址的发掘可为研究隋唐时期大型官仓的仓储制度及粮食储藏保管等，提供可贵的实物资料。\r\n1000多年来，中原地区历经战火和洪旱天灾，因回洛仓位于洛阳城外，不利于防守而逐渐被废弃。直到2004年，原洛阳市文物工作队为配合洛阳一拖东方红轮胎厂区的建设，钻探出71座仓窖，选择了其中的3座进行了试掘；2009年7月，又进行全面勘探，确定了仓城东、北、南三面城墙的准确位置；2012年1月至10月底，为配合大运河申遗，进行第三次勘探、发掘。如今回洛仓作为大运河中转站附近的重要遗址，被列入世界文化遗产名录，可谓实至名归。\r\n', 0, 665, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\洛阳回洛仓遗址.jpg'),
(7, '南旺枢纽考古遗址公园', '南旺枢纽考古遗址公园是济宁市和汶上县将为了对大运河南旺分水枢纽工程大遗址片区实行重点保护，设想建设的考古遗址公园。已经进行了初步挖掘，并于2010年10月被批准为第一批国家考古遗址公园。\r\n南旺分水枢纽是永乐九年工部尚书宋礼采用汶上老人白英建议修建的工程。是京杭大运河全线的关键工程，它体现出我国古代劳动人民惊人的智慧和伟大创造力，是大运河卓越工程价值和高超工程技术的杰出代表。济宁市和汶上县将对大运河南旺分水枢纽工程大遗址片区实行重点保护，建设考古遗址公园，并争取列入国家文物保护"十二五"重点项目。\r\n2008年3月至6月，经国家文物局批准，在山东省文化厅、文物局的部署和组织下，山东省文物考古研究所、中国文化遗产研究院联合对汶上京杭大运河南旺分水枢纽及龙王庙古建筑群进行了系统的调查和发掘。本次发掘总面积达4000余平方米，主要对南旺分水枢纽工程中的石石剥岸和运河河道进行了勘探和发掘，同时对该分水枢纽工程的附属建筑——分水龙王庙古建筑群基址进行了详细的清理，也对该分水枢纽工程的南旺湖、蜀山湖、马踏湖等水柜、闸坝设施进行了全面系统的调查。\r\n龙王庙分水石剥岸和运河河道是此次发掘工作的重点。共在龙王庙建筑群前布正南北向探沟4条。经解剖，石石剥岸在元代运河河岸的基础上经过明清两代多次修筑而成，其利用石石剥岸向河心凸出的弧形结构实现分水功能，分水部位较东西两侧石剥岸向河心凸出；其外侧底部用木桩做地钉，上铺条石，用浇铸有“分水石剥岸”字样的“腰铁”和白石灰连接黏合；内侧用三合土夹杂不规则石块夯筑而成。分水石剥岸地层堆积中发现有大量杉木木桩残件，出土有明清瓷器残件和铁钉等遗物。\r\n', 0, 684, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\南旺枢纽考古遗址公园.jpg'),
(8, '隋唐大运河遗址', '隋唐大运河途经豫、皖、苏三省，在安徽境内有150多公里。当年隋炀帝开凿大运河通济渠出河南后，经安徽淮北、宿州、泗县，在江苏盱眙入淮。安徽境内的隋唐大运河除个别段落被改造使用至今，其余段落已淤塞。目前，唯一“存活的”的隋唐大运河遗址处在我县境内，西起唐河交叉点，横穿泗城后向东北至濉河，全长约25公里的，遗址基本保持了隋唐大运河通济渠的原貌，功能已经发生改变，但仍可发挥着灌溉、分洪的水利作用。\r\n隋唐大运河的遗址大体分两种：一种是在地面上，如沿岸的古城、古佛；另一种是在地下，是考古挖掘出来的古桥、古码头、古仓窖和古瓷器。前者中的古城墙，如河南鹤壁市的黎明镇，古佛如鹤壁市大伾山的北魏大佛，这些凤毛麟角的地面运河遗迹之所以能保存至今，是因为所在地势较高，或者根本就在小山上。其余遗迹则全在地下，所有的重大发现都是考古的成果。其中最令人惊奇的首推古粮仓的发现。\r\n安徽省已经有两次发掘:第一次发掘是在1998年5月，发掘地是淮北的柳孜，当时经过考古人员200多天的日夜奋战，发掘的900多平方米的面积内，发现一处石构建筑遗存、一批唐代沉船，出土大量唐、宋时期的全国20多座窑口的陶瓷等文物。被国家文物局列为“99年全国十大考古新发现”之一，2001年被国务院评为全国文物保护单位。第二次发掘是在2006年6月，发掘地是宿州.目前发掘面积达500多平方米.出土文物数百件,其中的一百多件精美瓷器中,不光品种繁多,而且涉及的窑口包括安徽的寿州窑、烈山窑、东门渡窑、河南的鹤壁窑、钧窑、河北的定窑等数十个窑口,另外还出土了几尊造型精美、形象生动的佛造像。\r\n\r\n', 0, 764, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\隋唐大运河遗址.jpg'),
(9, '总督漕运公署遗址', '漕运总督部院遗址位于中国江苏省淮安市淮安区城区中心，是明、清两代统管全国漕运事务的漕运总督的官署建筑群，规模宏大，保存完好，布局严谨。\r\n明清的漕运总督部院位于淮安府城中心，与南面的北宋镇淮楼、迎熏门（南门），和北面的淮安府署在同一条中轴线上。规模宏伟，占地约2万平方米，有房213间，牌坊3座，中曰“重臣经理”，东西分别曰：“总共上国”、“专制中原”。依中轴线设大门、二门、大堂、二堂、大观楼、淮河节楼、后院等；东侧有官厅、书吏办公处、东林书屋、正值堂、水土祠、一览亭等；西侧有官厅、百录堂、师竹斋、来鹤轩等；在大门外东西两侧各有一座牌坊，大门对面有照壁，大门前还有元代从波斯国运来的白矾石狮子一对。大堂坐北朝南，东西长28.8米、南北宽22.8米，五开间五进，青砖地坪，有大柱34根。漕运总督衙门大堂遗址，现存有33个柱础1860年，漕运总督署迁往15千米外的清江浦，这里的建筑逐渐废弃。\r\n2002年8月，淮安市淮安区在旧城改造中挖掘发现了明清时期的总督漕运部院遗址，在明清遗迹下3米处还发现有宋元代文化层。遗址被列为江苏省省级文物保护单位。并在此兴建了漕运总督署遗址公园，还计划在附近建造一座中国漕运史博物馆。\r\n', 0, 682, 'C:\\xampp\\htdocs\\chinese_canal_images\\travel\\总督漕运公署遗址.jpg');

