--
-- 表的结构 `travel_spot`
--

CREATE TABLE IF NOT EXISTS `travel_spot` (
  `ID` int(11) NOT NULL AUTO_INCREMENT,
  `Title` varchar(255) NOT NULL,
  `City` varchar(255) NOT NULL,
  `Content` text NOT NULL,
  `ImgPath` varchar(1024) NOT NULL,
  `BrowseNumber` int(11) NOT NULL,
  `InsertTime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `Abstract` text,
  PRIMARY KEY (`ID`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=38 ;

--
-- 转存表中的数据 `travel_spot`
--

INSERT INTO `travel_spot` (`ID`, `Title`, `City`, `Content`, `ImgPath`, `BrowseNumber`, `InsertTime`, `Abstract`) VALUES
(1, '红旗渠', '安阳', '红旗渠风景区是修建在太行山悬崖峭壁中的人工天河，洋溢着当年战天斗地的革命气概。被世人称之为“人工天河”,位于豫、晋、冀三省交界处，是全国重点文物保护单位，在国际上被誉为“世界第八大奇迹”。\r\n红旗渠总干渠长70.6公里，渠墙高4.3米。现在的红旗渠景区由“分水苑”、“青年洞“、“络丝潭”三个景区组成。 “分水苑”是一园林式景区，也是红旗渠总干渠分为三条干渠的地方。主景“青年洞”靠断壁而凿，从大山之中穿通而过，为你展现出一幅雄壮的画卷，将太行美景“雄、险、奇、秀”凝集于此，极目远眺，尽收眼底。“络丝潭”因其潭深一络蚕丝而得名，又因浊漳河有“九峡十八断”，此处乃一较大断崭跌水，上有连接豫、冀两省的峡谷索桥，故称“天桥断”。\r\n红旗渠的修建充分体现了林县人民的优秀品质和智慧，这些都是构成红旗渠精神的基本内容。那么流传至今的红旗渠精神到底是什么呢，立足本地条件、依靠自己力量的自力更生精神，战天斗地、百折不挠的艰苦创业精神，顾全大局、齐心协力的团结协作精神，不计报酬、不怕牺牲的无私奉献精神。\r\n最佳旅游欣赏季节为3-5月、9-11月，中原地区在春季和秋季的气候适宜，适合游玩。', 'Home/Images/travel/景点/安阳/红旗渠/红旗渠.jpg', 0, '2015-09-18 01:13:09', NULL),
(2, '黄华神苑', '安阳', '黄华神苑景区位于林虑山主峰东侧，是集自然景观与历史名胜古迹为一体的山岳型自然风景旅游区。黄华山是最早见于史书记载的名山之一，集儒、释、道于一体，开发历史悠久、文化积淀丰厚。\r\n    这里山清水秀，云蒸霞蔚，风光旖丽，煞是壮观，汇古今奇观于一山。有“黄华流水颠倒颠”、“悬流千尺进珠帘”、“酷暑藏冰冰冰洞”等七十二胜景。 有战国时期的赵国长城、黄华会馆，东魏高欢避暑宫，隋代的觉仁院，宋代的王母祠、慈明院，明清时期的水帘亭、黄华中院、玉皇阁等建筑群；有金代著名书画家王庭筠在此隐居时创立的黄华书院及历代著名诗赋碑碣七十余通；有石塔群两处和古树名木多株。 山顶瀑布飞挂，山谷干湖映辉，云隐奇峰，水绕幽谷，享有“太行最秀林虑峰，林虑黄华更胜名”的赞誉。', 'Home/Images/travel/景点/安阳/黄华神苑/黄华神苑.jpg', 0, '2015-09-18 01:13:09', NULL),
(3, '夌里城遗址', '安阳', '羑里城是《周易》的发源地，位于汤阴县城北八华里羑、汤两河之间的空旷原野上，为殷纣王囚周文王处，是我国历史上自有文字记载以后第一座国家监狱。此处也是文王据伏羲八卦推演出64卦384爻，即“文王拘而演《周易》之圣地。“划地为牢”、“文王拘而演周易”历史典故均源自于此。 \r\n羑里城主要建筑有演易坊、山门、大殿、拜殿、演易台、洗心亭、玩占亭、吐儿冢，还有碑文石刻等。 遗址南北长106米，东西宽103米，总面积10918平方米，现存主要建筑有文王庙（周易博物馆）、演易坊、演易台等建筑。 其中演易坊是一座青石牌坊，位于城台的南端，上镌三个楷书大字“演易坊”，东西两侧各有一块石碑，西侧碑上刻着“周文王羑里城” 6个大字，东侧碑上刻着的则是著名的“禹碑”。演易台就是传说中的姬昌被囚之所，分上下两层，门楣上刻有“演易台”三字。羑里城遗址景区内有八卦阵迷宫，完全按照八卦阵图所建。', 'Home/Images/travel/景点/安阳/夌里城遗址/夌里城遗址.jpg', 0, '2015-09-18 01:24:46', NULL),
(4, '马氏庄园', '安阳', '马氏庄园位于安阳市西部20公里的西蒋村，紧临珠泉河，是清末头品顶戴、广西广东巡抚马丕瑶的府第，被学者称为“中州大地绝无仅有的封建官僚府第建筑标本”，“中原第一官宅”。是全国重点文物保护单位，全国红色旅游经典景区，河南省廉政教育基地。\r\n始建于清光绪十一年（公元１８８５年），总占地面积２００００多平方米，建筑面积５０００多平方米。建筑共计６组，每组分４个庭院，共建９个大门，俗称“九门相照”，有门、厅、堂、廊、室、楼，共３０８间。 整个建筑群分南、中、北三个区六路计２１处院落，南区一路，坐南朝北，五重院落。中区四路，坐北朝南。其中，西三路各建四重院落，东路建两重院落，再往东为马家园林。北区一路，亦坐北朝南，两重院落。除北区及中区东路外，每路均由四个“四合院”组成，均开九个门，自前向后依次排列在一条中轴线上，形成前门、中厅、后楼“九门相照”的格局。前半部分，用来对外接待宾客，后半部分为内宅。建筑大部分为硬山顶、悬山顶、卷棚顶式，青砖蓝瓦。庄园装饰丰富多彩，石柱底部为方形，雕兽头、花草，上部为扁鼓形，刻联珠，部分门墩雕刻对狮。门窗上、房檐下为木刻砖雕，图案繁多，富丽堂皇。庄园留有家训屏风，马丕瑶“进士第”、马吉昌“太史第”等众多匾额和铜镜，长2.87米的慈禧“寿”字中堂，光绪御笔碑文。\r\n该庄园是一处清代典型的地主官僚庄园。整座庄园布局严谨，错落有致，古朴典雅，雄浑庄重，既有典型的北京四合院宽敞明亮的建筑风格，又有晋商大院深深邃富丽的建筑艺术，还有中原地区蓝砖灰瓦五脊六兽挂走廊的建筑特色。马氏庄园不仅是一部清末民国初期的建筑巨著，一座具有跨时代意义的廉政教育基地，红色教育基地，更是一部集儒学、建筑、风水、书法、楹联等中国传统文化于一体的百科全书。', 'Home/Images/travel/景点/安阳/马氏庄园/马氏庄园.jpg', 0, '2015-09-18 01:24:46', NULL),
(5, '天宁寺', '安阳', '来到安阳天宁寺其实是为了天宁寺塔（又叫文峰塔)而来，此塔有五级，由下而登，逐渐宽敞，其巅则为平台，周可容二百人，远望太行，历历在目。文峰塔是一座非常独特的塔，建于后周文顺二年（952年），迄今已有1000多年的历史。因塔建于天宁寺内，故原名天宁寺塔。是古城安阳标志性建筑。因其独特伞状建筑形制而闻名于世。塔身八面砖雕神情逼真，造型生动，姿态自然，塔顶平台可容纳200人左右。登高望远，登塔俯瞰，安阳古城的绮丽风光可以尽收眼底。', 'Home/Images/travel/景点/安阳/天宁寺/天宁寺.jpg', 0, '2015-09-18 01:24:46', NULL),
(6, '殷墟', '安阳', '殷墟是中国奴隶社会商朝后期的都城遗址，位于河南省安阳市区西北小屯村一带，距今已有三千三百多年历史。因其出土大量的甲骨文和青铜器而驰名中外。商后期叫北蒙，又称殷，公元前14世纪盘庚迁都于此，至纣亡国，共传8代12王，前后达273年。周灭殷后，曾封纣之子武庚于此，后因武庚叛乱被杀，殷民迁走，逐渐沦为废墟，故称殷墟。\r\n殷墟是中国历史上被证实的第一个都城，它的发现和发掘被评为20世纪中国“100项重大考古发现”之首。 殷墟王陵遗址与殷墟宫殿宗庙遗址、洹北商城遗址等共同组成了规模宏大、气势恢宏的殷墟遗址，大致分为宫殿区、王陵区、一般墓葬区、手工业作坊区、平民居住区和奴隶居住区。 殷墟总面积达24平方公里，内有大量的青铜器，石器，骨器，玉器等具有珍贵史料价值的文物，其中最有名的是重达 875公斤的“司母戊大方鼎”，它是迄今世界上所发现的最大最重的青铜器。 \r\n殷墟宫殿区驰名中外，为世界文明古国中最著名的“古典城邦”之一。50余座建筑遗址分“宫殿、宗庙、祭坛（甲、乙、丙）”三组，宏伟壮观。另外还发现铸铜遗址等。宫殿区出土大量的甲骨文、青铜器、玉器、宝石器等珍贵文物。殷墟文物有的是华夏之最，有的是世界之冠，蕴藏着殷代先民们的创造、智慧和卓越的技能。考古专家评价，殷墟“发掘着一个典型的奴隶社会”。具有“都市、文字和青铜器三个要素”。“是一个灿烂的中国文明”。著名历史学家郭沫若赋诗云：“洹水安阳名不虚，三千年前是帝都。”“中原文化殷创始”“观此胜于读古书”。当代骚人墨客陈运和诗称“甲骨文乐得跳出殷墟 青铜器乐得跳出殷墟 重见天日，真是安阳小屯村的特大欢喜”。王陵遗址 殷墟王陵遗址与宫殿宗庙遗址隔河相对，是商王的陵地和祭祀场所，也是中国已知最早的完整的王陵墓葬群，面积达11.3公顷。王陵遗址共发现有12座王陵大墓和2500多座祭祀坑。', 'Home/Images/travel/景点/安阳/殷墟/殷墟.jpg', 0, '2015-09-18 01:24:46', NULL),
(7, '袁林', '安阳', '袁林又名袁家林或袁坟，是袁世凯的墓地所在，位于安阳市洹上村东北隅的太平庄。 兴建于民国五年，这座陵墓按照明、清陵的格局，采用中西合壁的构筑手法，以中国古典传统形制为体，西洋建筑风貌为用。 袁氏墓冢仿照美国第18任总统格兰特濒河庐墓的形制修成，为内包水泥，外砌石墙的独特欧式风貌。照壁、牌楼、碑亭、东西配殿、景仁堂、墓台等建筑，以神道为中轴，分布其间，布局严谨、错落有致，既承袭我国传统建筑规制，又吸取西洋建筑风貌，形成了中西合璧、风格迥异的陵园建筑特色。\r\n穿过中国传统的牌楼门之后，是林荫夹道的神路。神路两旁是对立着的华表、石马、石虎、石狮、石雕武将、石雕文臣等。按照“帝王”的规格，为袁世凯墓做仪仗，令人感到特别的是，不论石马、石虎、石狮还是文臣武将，全都是短腿。 其碑亭是传统式的。亭内袁世凯的墓碑也是和封建社会中的帝王将相一样是由巨大的背负。过了碑亭，就是大门。正面有一座大殿，两侧有陪殿，全是清代风格的建筑。再往前行，便是大墓台基。台基有三道门，中间的铁门又完全是西式建筑，貌似民初的“大帅府”。然而，铁门上挂着的墓徽却是雕刻着苍龙、猛虎等。铁门内的三层高台大墓，便是袁世凯葬身之处。它呈圆形，全用钢筋水泥浇铸，周围雕的石狮也是模仿西式的“写实”，而不是中国传统式的“写意”。 袁世凯这个古怪的墓反映了他在历史舞台上的瞬息多变的身份。', 'Home/Images/travel/景点/安阳/袁林/袁林.jpg', 0, '2015-09-18 01:24:46', NULL),
(8, '岳飞庙', '安阳', '岳飞庙原名精忠庙，后也称“宋岳忠武王庙”，是后人为纪念南宋抗金名将、我国著名民族英雄岳飞而建。位于汤阴县城内西南街，今址是明景泰元年（公元1450年）重建，以后历代屡有增建，逐渐成为一处完整的古建筑群。 \r\n现有面积4000多平方米，殿宇建筑近百间。座北朝南，外廊呈长方形。临街大门为精忠坊，面西，木结构，斗拱型制九踩四昂重翘。坊之正中阳镌明孝宗朱佑樘赐额“宋岳忠武王庙”，两侧八字墙上用青石碣分别阳刻“忠”、“孝”两个大字。字高1.8米，遒劲端庄，格外醒目，过精忠坊为山门，座北朝南，三开间式建筑，两侧扇形壁镶嵌有滚龙戏水浮雕，门前一对石狮分踞左右。山门檐下一排巨匾，上书“精忠报国”、“浩然正气”、“庙食千秋”，是当代书法家舒同、楚图南、肖劳的手迹。山门对面为施全祠，内塑施全铜像，对祠前秦桧、王氏、万俟 、张俊、王俊五奸党铁跪像呈镇压之势。在今天，我们以庙的形式来纪念我们的民族英雄，更重要的是这种精神将永远也不散去。', 'Home/Images/travel/景点/安阳/岳飞庙/岳飞庙.jpg', 0, '2015-09-18 01:36:42', NULL),
(9, '中国文字博物馆', '安阳', '中国文字博物馆位于河南省安阳市人民大道东段北侧，东邻京珠高速公路和正在建设中的高速铁路。博物馆主要建设内容包括，仓颉馆、科普馆、研究中心、文化交流中心等，是一组具有现代建筑风格和殷商宫庭风韵的后现代派建筑群。 中国文字博物馆门前屹立着高大的字坊。字坊高18.8米，宽10米，取甲骨文、金文中“字”字之形。通向展馆的主干道两旁，是中国文物学会青铜修复委员会委员、殷商青铜工艺研究所所长兼总工赵乾民监制的、由28片极具代表性的青铜甲骨片组成的碑林，隐含了殷商时期最具代表性的两种元素——甲骨文和青铜器。这批甲骨文精品大都来自《甲骨文合集》《小屯南地甲骨》和《花园庄东地甲骨》，最大尺寸高达 1.4米、宽0.9米，总重量3吨，青铜甲骨片背面则为这些甲骨不辞的释义。同时，28片向上天卜问吉凶祸福的甲骨片又代表着28星宿，象征人与自然密切相连，是我国古典哲学中“天人合一”思想的生动体现。\r\n 文字是历史文明传承的载体和见证。中国文字博物馆作为一座全面反映、专题研究中国各民族文字、文字历史、文字文明的专题博物馆，通过荟萃历代中国文字样本精华，讲解中国文字的构形特征和演化历程，反映中华文明与中国语言文字的研究成果，展示中华民族灿烂的文化和辉煌的文明，进一步提高中华民族在人类文明史上的地位，展现中华民族悠久的历史文化，弘扬民族精神，增强民族自豪感，将对我国文字、文化、文明的传承和保护、研究和发扬产生深远影响。建成后的中国文字博物馆将成为立足于甲骨文的故乡——安阳，面向国内、国际开放的知识型、信息型博物馆。', 'Home/Images/travel/景点/安阳/中国文字博物馆/中国文字博物馆.jpg', 0, '2015-09-18 01:36:42', NULL),
(10, '颛顼帝喾陵', '安阳', '颛顼帝喾陵位于商丘县城南23公里高辛集遗址西北100米处，为高大古丘，南北长233米，东西宽130米。陵前原有帝喾祠大殿、沐浴室、更衣室、山门等古建筑。帝喾是黄帝的曾孙，阏伯的父亲,是五帝之一。帝喾祠修建于汉。在元明又经多次修复。\r\n建筑宏伟，古朴典雅，碑碣林立，松柏蓊郁。陵寝结构严谨，景色优美，每年都会举办热闹的庙会，届时有很多信众来这里烧香请愿。庙堂内中央有一口古井。梁上绘有彩龙，彩龙映入井中，栩栩如生。相传大旱之年求雨多有灵验，所以被人们誉为“灵井”。 相传赵匡胤不得志时，去北方投奔郭威，路过帝喾陵，抽签问卜，当有天子命。后来他果然在商丘当了归德节度使而发迹,赵匡胤登基后，因商丘是西周时的宋国，又是后来的宋州，而定国号为“宋”。', 'Home/Images/travel/景点/安阳/颛顼帝喾陵/颛顼帝喾陵.jpg', 0, '2015-09-18 01:36:42', NULL),
(11, '扁鹊庙', '沧州', '扁鹊庙，又名扁鹊祠，位于内丘县城西26公里的鹊山脚下，是祭祀、谒拜华夏医祖扁鹊的古建筑群。原有单体建筑27座，现存有回生桥、山门、扁鹊殿、后土总司殿、玉皇殿等13座，占地面积15万平方米，是我国现存规模最大、历史最悠久的扁鹊庙群中国医药胜地、中国医胜扁鹊的故乡莫州，就在华北平原上的任丘市境内。任丘地处京、津、晋金三角地带，历代著名中医名家都留下了他们的足迹。供奉中国十大名医以及医圣扁鹊，天下最大的药王庙就坐落在任丘这块土地上。它是中国古代医学，中国中医学的发源地，中华医学就是从这里走向世界的。', 'Home/Images/travel/景点/沧州/扁鹊庙/扁鹊庙.jpg', 0, '2015-09-18 01:36:42', NULL),
(12, '东光铁佛寺', '沧州', '位于河北省沧州市东光县城内的铁佛寺是沧州的一大特色，也是一处有着众多人文景观的地方。在当地还有着“沧州狮子景州塔,东光县的铁菩萨”这样一句打油诗来描绘铁佛寺。\r\n原名“普照寺”的铁佛寺始建于北宋开宝五年。铁佛寺大门上悬挂的“铁佛寺”烫金匾额是现代中国佛教协会会长赵朴初所题写的，原有的匾额已在沧桑的历史中消失殆尽。到铁佛寺旅游，铁佛寺的山门、天王殿、大雄宝殿及东西配殿都是你不可错过的铁佛寺景点。其中的大雄宝殿是铁佛寺的主殿，大雄宝殿内供奉的是释迦牟尼佛的尊像，这尊像高8.24米，重48吨的铁铸佛像是我国最大的座式铸铁佛像。', 'Home/Images/travel/景点/沧州/东光铁佛寺/东光铁佛寺.jpg', 0, '2015-09-18 01:36:42', NULL),
(13, '海盐博物馆', '沧州', '河北海盐博物馆坐落在河北省黄骅市渤海路中段。黄骅产盐历史悠久，自古有着"鱼盐之利雄天下"的优势，是沧州长芦盐场的主产区，长芦盐场是我国海盐产量最大的盐场,产量约占全国海盐总产量的四分之一。市内拥有"国保"单位海丰镇古制盐遗址。\r\n海丰镇是宋金时期北方最大的港口，曾有沿海"万灶青烟皆煮海"的盛况，是当时长芦盐场煮盐的基地。北海盐博物馆风格为仿古式建筑，共三层，园内为苏州园林式风格，水榭楼台相映，假山绿竹点缀，回廊曲折环绕，环境静谧优雅。展厅面积2000余平方米，并建有800平方米的露天大型互动展厅一个。', 'Home/Images/travel/景点/沧州/海盐博物馆/海盐博物馆.jpg', 0, '2015-09-18 01:49:42', NULL),
(14, '河间府署', '沧州', '河间府署素有“京南第一府”美誉，昔日是与保定、大名、开封齐名的京南四大名府之一。据记载，河间府署坐北朝南，气势雄伟恢弘，衙署的主要建筑均分置于中轴线上，附属建筑分布于东西两侧附属轴线上，形成东中西三路轴线，各轴线均有院落数进，每进院落中，以主要建筑为中心，左右对称分布次要建筑，构成四合院式的布局。府衙大堂内高悬明朝严嵩所书“保釐堂”巨幅匾额，堂前戒石亭内有“尔俸尔禄，民膏民脂，下民易虐，上天难欺”的碑铭。大堂西侧悬挂一口铜铸巨钟，声闻四十里，但是该物件后被日本侵略军劫走。', 'Home/Images/travel/景点/沧州/河间府署/河间府署.jpg', 0, '2015-09-18 01:49:42', NULL),
(15, '纪晓岚文化园', '沧州', '纪晓岚文化园位于纪晓岚故里河北省沧州市沧县崔尔庄。纪晓岚是中国一代文宗。名昀，字晓岚，一字春帆，晚号石云，道号观弈道人。历雍正、乾隆、嘉庆三朝，享年八十二岁。 纪晓岚也是中国清代炫人耳目的人物，作品颇多，以总纂《四库全书》名闻天下，著有《阅微草堂笔记》。“纪晓岚文化园”，占地70余亩，建有宦海书丛馆、阅微草堂、滦如槐姑室、九十九砚亭、文漪阁等仿古建筑。该园以实物、绘画等方式，记载了纪晓岚的生平和著述，还收藏有一部由商务印书馆出版的《四库全书》。　', 'Home/Images/travel/景点/沧州/纪晓岚文化园/纪晓岚文化园.jpg', 0, '2015-09-18 01:49:42', NULL),
(16, '盘古古庙', '沧州', '在沧州有这样的民谣：“一文一武，一国宝，一人祖。”文者，是一代文宗纪晓岚，武者，是沧州乃驰名中外的武术之乡，国宝指沧州铁狮，人祖即盘古，盘古遗址就在今沧州市所属的青县境内。\r\n    青县城南6公里有村曰“大盘古”，村西有座盘古庙。 大盘古位于黑龙港河（古黄河故道）西岸，其南有小盘古村，皆因相传盘古在此定居而得名。盘古庙始建于元世祖至元十五年，明曾重修，清康熙二十七年因河流冲刷，设于秋祠西南重建。据记载，当年的盘古庙状似北京的故宫三殿，石、砖、木结构，琉璃瓦封顶，金碧辉煌，蔚为壮观。前殿高三丈六尺，象征一年360天；台阶18登，为18层地狱之说；飞檐椽子108根，按36天罡、72地煞而布；每根椽头系铜铃一枚，照工、商、角、征、羽五音而置，有所谓“风摇铃响天动乐，光照殿顶地生辉”之说。殿内有身披树叶，手托日月的铁铸涂金盘古像，高一丈零八寸。中殿塑南海观音像，股坐莲盆，金童玉女两侧。后殿设老君、孔子、龙王三塑像，殿前有合抱粗的合欢树两株，殿后铁磬悬于古槐。', 'Home/Images/travel/景点/沧州/盘古古庙/盘古古庙.jpg', 0, '2015-09-18 01:49:42', NULL),
(17, '歧口炮台', '沧州', '歧口炮台遗址位于河北黄骅市南排河镇沧浪渠入海口，是曾经的战略要地。炮台始建于明代，清咸丰七年(1857年)的时候进行了重修，建有大炮台2座(南北岸各一座)，北岸为“大将军”炮台，南岸为“二将军”炮台。 咸丰八年(1858年)抵及歧口，英法联军倚仗其坚船利炮，强行登陆。歧口炮台驻扎字兵与当地民众协同一心，顽强抵抗，奋死力战，英法联军败退。不得不转往大沽口登陆。当时两座主炮“大将军”、“二将军”之一的“二将军”炮至今完好，保存于黄骅市博物馆。现在歧口村渔港码头设抗击八国联军登陆纪念碑一座。', 'Home/Images/travel/景点/沧州/岐口炮台/岐口炮台.jpg', 0, '2015-09-18 01:49:42', NULL),
(18, '青云观', '沧州', '青云观,位于沧州市东北的青县觉道庄,距青县县城20公里。观内有殿堂15座,供奉80尊神像,是华北平原上历史悠久的道观。\r\n    相传春秋战国时期，老子李耳弃周守藏室史官而走，云游四方至此地隐居，德高望重老子传经布道，救苦救雅，民众相聚而居，逐渐形成村落，起名为道德庄。沧桑变迁，元初．老子分神化气，重降此庄，名为张核，道号觉道，相继村更名为觉道家庄，觉道大师积功德，四处化缘，在原老者君讲经道场营造起一座颇有影响的道观枣青云观，从此,觉道家庄道场营造起一座颇有影响的道观。觉道家庄就成了道教圣地，村周围先后建起了碧霞庵。五龙圣母庙等道观，历史上曾在此涌现出颇有道诣的道师和法师。', 'Home/Images/travel/景点/沧州/青云观/青云观.jpg', 0, '2015-09-18 01:57:55', NULL),
(19, '铁狮子', '沧州', '沧州铁狮子景区位于沧州市东南20公里，沧县旧州镇东关村西。沧州铁狮子铸于后周广顺三年（公元953年）。身长6米，体宽3米，通高5.5米，重约40吨。铁狮子坐北面南，身披障泥，背负莲盆，前胸及臀部饰束带，鬃作波浪状披垂项上，巨口大张，昂首怒目，四肢叉开，作行走状。其气势之阳刚雄伟，造型之生动逼真，堪称我国历史上年代最久远、体积最大、形态最精美的铸造工艺珍品。', 'Home/Images/travel/景点/沧州/铁狮子/铁狮子.jpg', 0, '2015-09-18 01:57:55', NULL),
(20, '吴桥杂技大世界', '沧州', '吴桥，作为我国杂技艺术的发祥地，以其悠久的杂技历史和精湛的杂技艺术在国内外杂技界被称作杂技艺术的摇篮，1954年吴桥被周恩来总理亲自命名为“杂技之乡”。 杂技大世界共建江湖文化城、杂技奇观宫、魔术迷幻、小泰山等八大景点、集娱乐、参与、交流、表演、餐饮等多功能于一体，展示民俗精粹，以独特的杂技文化旅游日益奠定了华北知名景点地位。\r\n杂技大世界的中心景区江湖文化城占地19200平方米，重现了当年北京天桥、天津三不管、上海大世界、南京夫子庙四个民俗文化集散地的表演特色和江湖文化景观，让人们充分领略吴桥杂技艺人的奇招绝活，漫步城中可以尽情欣赏各具特色的杂技、马戏、戏法、气功、曲艺、独台戏、拉洋片、驯白鼠等艺术表演。', 'Home/Images/travel/景点/沧州/吴桥杂技大世界/吴桥杂技大世界.jpg', 0, '2015-09-18 01:57:55', NULL),
(21, '常州古运河', '常州', '京杭大运河在常州穿城而过，千百年来，古老的运河是常州的母亲河，人文资源丰富，名胜古迹众多，是一部承载常州历史文化的宝典。当今的常州古运河，正成为一条融风景名胜和历史人文景观于一体的旅游景观走廊。\r\n\r\n常州古运河水上游西起篦箕巷，东到东坡公园，与常州最繁华的延陵路齐驾并驱，是古运河的精典游线。全程往返约10公里，行程约1个半小时。画舫船豪华而又古典，环境舒适，坐在船上品茗赏景，倾听导游吴侬软语介绍着发生在千年运河两岸的故事，宛如偷得浮生半日闲，尽享乐活人生。', 'Home/Images/travel/景点/常州/常州古运河/常州古运河.jpg', 0, '2015-09-18 01:57:55', NULL),
(22, '红梅公园', '常州', '红梅公园分三区八景。在公园的南部是文物古迹区，有红梅阁和文笔塔；在公园的西北部是娱乐活动区，有运动场、春晖茶室、青少年活动场所、游艇、听松楼和舞厅；在公园的东部是科普教育区，有动物园、盆景园、月季园和屠一道根艺藏珍馆等。在红梅公园里，根据各区景点的不同特色，分为八景：红梅春晓、古刹钟声、曲池风荷、青峦倒影、凤桥花径、翠薇秋霞、孤山雪松、文笔夕照。公园还具有水乡特色。水面约占全园面积的1/5，水路总长达2500米，桥梁众多，风格迥异，仅“文笔夕照”一处就有曲桥、拱桥、平桥、廊桥等多种造型的桥梁15座之多。园内桥名雅致，有意有境，不落俗套，如文思桥、星聚桥、伯牙桥、翠盖桥、西双桥、三吴桥等，尤以笔架山北首的云影桥历史最为悠久。\r\n\r\n公园游览项目众多，始建于20世纪60年代初的动物园，拥有国家一类、二类保护动物200余只，是华东地区金钱豹种群繁殖基地之一；位于塔园西侧的盆景园，展现了常州盆景师法自然、潇洒隽永的艺术特色，其中许多盆景在全市、全省乃至全国盆景大赛中多次获奖；位于塔园北侧的月季园，各色月季竞相开放，争奇斗艳，堪称江南之冠，是全国五大月季中心城市之一的主要观赏基地；还有公园东大门处展示天工神艺的屠一道根艺珍藏馆，名闻遐迩，素有“世界根艺在中国，根艺精品在常州”之美誉。', 'Home/Images/travel/景点/常州/红梅公园/红梅公园.jpg', 0, '2015-09-18 01:57:55', NULL),
(23, '南山竹海', '常州', '山竹海生态旅游区位于江苏溧阳市南部山区，距溧阳市区30公里，是国家AAAAA级旅游区，有“天堂南山，梦幻竹海”之美誉。 南山竹海旅游区是一个集资源利用、生态保护和旅游观光于一体的世外桃源。这里的生态环境宜人，山水相映成趣，风景如诗如画，方圆几十里无一丝污染。除了万顷竹海、千年古松、高山镜湖，这里还有古官道、古代军事遗址等人文景观。南山竹海深蕴着青山绿水的诗意和神韵，是一处风景如画的风雅之地。 南山竹海景区内分为五大功能区，分别为：静湖娱乐区、休闲娱乐区、历史文化区、寿文化区和登山游览区。主要的景点有：静湖、小鸟天堂、古官道、中国第一寿星、吴越弟一峰、撞钟祈福等。', 'Home/Images/travel/景点/常州/南山竹海/南山竹海.jpg', 0, '2015-09-18 02:20:38', NULL),
(24, '天目湖', '常州', '天目湖位于苏、浙、皖三省交界处的江南历史名城溧阳市境内，被誉为“江南明珠”，是国家首批AAAAA级景区。 山水园景区是天目湖的核心景区，诗意天成的精华所在，自然景色与人工点缀相得益彰，拥有慈孝文化园、状元文化区、奇石馆、精灵国、彩蝶谷、中国茶岛等景点。 湖四面群山枕水、碧波荡漾，湖中岛屿散落、湖岸曲折通幽，以山绕水、水映山而取胜。泛舟湖上，山重水复，松涛竞逐，禽飞兽走，带你进入梦游仙境的奇妙之旅。游船是景区的经典项目，可以满足千余人同时畅游天目湖。 状元文化为湖里山历史文化区的核心，慈孝文化为传统文化园的开篇之作。 奇石馆收尽天下奇石，龙兴岛彩蝶谷则带你进入周庄梦蝶的奇幻之旅——全新打造的蝴恋、蝴梦、蝴画三个展馆，全方面展示了与蝴蝶有关的美丽传说和自身价值。 精心打造的精灵国，有琳达的舞台、朵儿的村庄、吉米的乐园，为孩子们打造快乐的动物乐园，邂逅动物王国的音乐圣典——精灵乐队的精彩演出，与演员一起互动、逛欢。 占地4.77万平方米的水公园项目，由大型冲浪池、惊险刺激与悠闲倘洋结合的漂流河、各种刺激的滑道组合、大型水寨、儿童戏水区等项目组成，日接待量达2万人，加之自然生态的主题包装，完善、高品质的配套设施，力在打造华东地区唯一一座原生态“水上主题乐园”，将于2014年暑期全面投运。', 'Home/Images/travel/景点/常州/天目湖/天目湖.jpg', 0, '2015-09-18 02:20:38', NULL),
(25, '淹城野生动物世界', '常州', '江苏淹城野生动物世界位于常州市武进中心城区，紧邻具有“中国第一水城”美誉的国家重点文物保护单位“春秋淹城遗址”，距常州市区8公里。淹城野生动物世界是以野生动物生态散养为特色，集动物野趣观赏、休闲娱乐、科普教育和古文化展示为一体的大型综合性主题公园。园内展出来自世界各地的珍稀濒危野生动物共200多种近万头（只），是江苏省唯一一家超大规模的野生动物园。\r\n\r\n淹城野生动物世界内设有袋鼠馆、鸟类长廊、狼谷、黑豹馆、白狮馆、东北虎馆、孔雀苑、猩猩馆、熊猫山寨、非洲狮部落等20余个动物专馆，有步行游览和自驾车游玩两种方式，可自行选择。 如果是步行游览，可以从东大门入口进入，进门后向右，按逆时针方向逐个游览。东大门也是主要的出入口，游客中心和售票处都位于这里。如果是自驾车游玩，则要从南大门自驾区入口进入，顺时针方向游玩。 园区内有各种动物的表演，游园时不要错过。位于园区中心的国际大马戏表演场周一至周五13:30每天一场，双休日每天11:30、15:00每天两场精彩的马戏表演可千万不要错过。', 'Home/Images/travel/景点/常州/淹城野生动物世界/淹城野生动物世界.jpg', 0, '2015-09-18 02:20:38', NULL),
(26, '碧霞元君故居', '德州', '碧霞元君故居景区初建于北宋真宗时期，有近千年的历史文化孕育其中，历代帝王将相，文人墨客，竞相谒拜。相传，清乾隆皇帝三下江南，路径乐陵至此，曾挥笔写下“泰山奶奶之神地”的金牌匾。该故居占地100多亩，建筑面积6000平方米，组建成了由龙腾牌坊、盛世广场、碧霞祠景区、王母殿景区为主的游览景区群，形成了二主殿（碧霞祠、王母殿）、六偏殿（车神殿、观音殿、财神殿、药王殿、文昌殿）、一亭（转运亭）、一桥（明桥）、一潭（日月潭）的宏伟格局，融合儒、释、道历史文化精髓，极力打造江北道教文化圣地。', 'Home/Images/travel/景点/德州/碧霞元君故居/碧霞元君故居.jpg', 0, '2015-09-18 02:20:38', NULL),
(27, '德百温泉度假村', '德州', '德百温泉度假村位于国家AAAA级景区山东德州夏津黄河故道森林公园内，项目总投资100亿元， 德百室外温泉占地两万五千平方米，有56个泡池，整个室外温泉被纵贯东西的两条地热长廊划分为九个区，包括：四季祥瑞区、名汤养生区、汤池木屋区、冲浪区、特色风情区、美白养颜区、保健理疗区、唐风唐韵区、水上乐园。\r\n德百温泉出水温度65度，水质清澈透明，富含硼、硅、锶、锂、碘、氟等多种对人体健康有益的微量元素。热水中氟含量达到医疗价值浓度。偏硼酸、偏硅酸含量均达到了矿水浓度，具有较高的医疗价值，可作为洗浴、医疗保健用水。 德百温泉是鲁西北地区规模最大、泡池最多、功能最全的大型室外温泉。长期在德百温泉沐浴，不仅有益身体健康，并且有医疗保健、美容养颜等多重功效，是游客四季旅游选择的理想境地。', 'Home/Images/travel/景点/德州/德百温泉度假村/德百温泉度假村.jpg', 0, '2015-09-18 02:20:38', NULL),
(28, '国科玉堂温泉', '德州', '国科玉汤温泉这里好似一个四季恒温的天然氧吧，绿植青青，艳草芬芳，空气中充满着南国风味，让你远离城市的熙攘和烦躁。进入温泉可尽享犹如置身于夏无酷暑，冬无严寒的热带雨淋气候，尽情体验北国“西双版纳”风情。\r\n这里是一所天然的"保健院"。国科温泉为高热、高渗、中性水质，有远古"圣水"之誉；富含钙、镁、钾、钠、硫、铁、氯、溴等三十多种矿物质和具有重要医疗价值的放射性元素，自1700米深的地热温泉水，是难得的纯温泉，富含各种对人体有益的微量元素及矿物质，为您提供了良好的保健功效。温泉出表水温高达56摄氏度，日涌水量达1998立方米。温泉水质甘秀美、淡黄清澈，与一般颜色浑浊、带有硫磺味儿的温泉截然不同，享有"一盆玉汤"的美誉，是难得的纯温泉，对治疗皮肤病、色斑、关节炎及神经衰弱等有特殊的疗效。早在辽代，萧太后就曾留香于此，自元代起又被开辟成皇家园林，成了历代封建帝王专有的享受。明武宗曾留下"泡海隆冬也异常，小池何自暖如汤。融融一脉流股筋，不为人间洗冷肠"的诗句。后来历代皇帝都把这里作为一种享受的象征，纷纷到此"行汤"。', 'Home/Images/travel/景点/德州/国科玉堂温泉/国科玉堂温泉.jpg', 0, '2015-09-18 02:48:32', NULL),
(29, '海岛金山寺', '德州', '海岛金山寺起始于隋代。2003年，世界十大法师之一静空大法师的得意弟子、杭州东天目山齐淑萍居士筹巨资恢复重建庆云海岛金山寺这一名寺胜景。新建的海岛金山寺位于县城以北、北环路南侧，占地300多亩。海岛金山寺以山门、天王殿、大雄宝殿、藏经阁、古佛寺为中轴线，念佛堂、钟楼、鼓楼、方丈楼、贵宾楼为两翼错落展开。寺院建筑飞檐斗拱，画栋雕梁，令人叹为观止。殿内广列诸佛，乾隆版《大藏经》、 《四库全书荟要》、菩提经文、古佛、古碑等诸多历史文物为镇寺之宝。寺内，晨钟暮鼓，经声佛号，香烟袅袅，游者香客骆绎不绝；寺外，河水潺潺，碧野映衬，风景优美宜人。铜铸巨钟可声传方圆数里，令人称奇；释迦牟尼佛像高8米多，为目前全国室内最大的释迦牟尼佛像，香客游人无不啧啧称道。无论是海岛金山寺宏伟庄严的群体建筑，还是其深厚的文化底蕴，成为德州的一大人文景观，有力的吸引感染着游客。', 'Home/Images/travel/景点/德州/海岛金山寺/海岛金山寺.jpg', 0, '2015-09-18 02:48:32', NULL),
(30, '皇明太阳谷', '德州', '皇明太阳谷是国内首家以太阳能为主题的公园，拥有世界级可再生能源九大中心、十大太阳能建筑，全球唯一微排旅游湿地度假圣地，这就是皇明太阳谷与之不同的地方。皇明太阳谷集中展示了以太阳能为主的多种节能技术相结合的节能建筑，被誉为“21.5世纪绿色城市模板”，是一个极富科教意义的游览景点。', 'Home/Images/travel/景点/德州/皇明太阳谷/皇明太阳谷.jpg', 0, '2015-09-18 02:48:32', NULL),
(31, '陵县文博苑', '德州', '陵县文博苑位于陵县人民公园内。建于1985年，内有碑亭、颜公祠等建筑，保存的主要文物是东方朔画赞碑，俗称“颜子碑”，全名“汉太中大夫东方先生画赞碑”。西晋文学家夏侯湛撰文，唐代著名书法家颜真卿书。碑为四面刻，碑阳、碑阴各十五行，左右侧各三行，每行十三字，碑正文均为大安楷书，阳额篆书、阴额隶书，碑文字大六公分，碑额字大十公分，共一千零七十三字，该碑至今已有一千二百余年，属国家一级文物藏品。该碑唐天宝十三年（公元七五四）建于古厌次城（今陵县神头镇）的东方朔祠庙内，元代时迁至县城，', 'Home/Images/travel/景点/德州/陵县文博苑/陵县文博苑.jpg', 0, '2015-09-18 02:48:32', NULL),
(32, '欧乐堡梦幻世界', '德州', '欧乐堡梦幻世界位于齐河县东南，紧邻泉城极地海洋世界，是一座以欧洲经典建筑风情为主题、拥有众多尖端设备项目的大型游乐园。在这儿你可以勇敢地坐一次惊险刺激的蓝火过山车、摩托过山车，或是去5D球幕影院体验身临其境地凌空飞行，还能观赏精彩的花车巡游以及马戏、马术表演。 进入乐园记得先领一份园区导览图和游园指南，规划一下游园线路，注意各类表演的演出时间。\r\n欧乐堡梦幻世界实行一票通玩制，门票涵盖了几乎所有的娱乐设施、影院、表演。园区里有餐厅提供快餐，不过园区里面的餐饮消费比较高，游客可以自带些水和干粮点心，夏季更要注意防晒措施。泉城欧乐堡梦幻世界的娱乐项目非常多，不过最受欢迎的莫过于七大过山车和各种表演。来自德国的蓝火过山车是其中最刺激的项目，你可以在空中体验最高时速达100公里的冲刺，尽情地狂呼乱叫！如果还嫌不够过瘾，还有摩托过山车、悬挂过山车、星空过山车、白马过山车、自旋过山车等你去尝试。 乐园内还有很多常规的娱乐项目，比如大摆锤、激流勇进等，以及更适合孩子的超豪华转马、旋转飞椅、桑巴气球、海盗船、碰碰车等项目。', 'Home/Images/travel/景点/德州/欧乐堡梦幻世界/欧乐堡梦幻世界.jpg', 0, '2015-09-18 02:48:32', NULL),
(33, '齐河海洋世界', '德州', '说到海洋馆大家一定知道泉城海洋极地世界，这是目前亚洲规模最大、世界一流的海洋馆，位于德州齐河县境内，距离济南市大约20分钟路程。景区突出海洋文化科普教育特色，主要建设热带雨林、海底隧道、海洋生物、海兽表演、科普教育等功能区，海洋馆里有来自世界各地的1000多个品种的海洋生物、上万条珍稀鱼类和北极熊、大白鲸等极地海洋动物。这里一定会是喜欢动物的人们和小朋友们最喜欢的地方。', 'Home/Images/travel/景点/德州/齐河海洋世界/齐河海洋世界.jpg', 0, '2015-09-18 02:48:32', NULL),
(34, '苏禄王墓', '德州', '苏禄王墓1997年被列为全省重点文物保护单位，1998年1月被中华人民共和国国务院公布为重点文物保护单位。古苏禄国位于菲律宾苏禄群岛上，是一个信奉伊斯兰教的酋长国，国内分为东王、西王、峒王三家王侯，以东王为尊，在我国明代，古苏禄国同我国开始了密切的友好来往。明永乐十五年，苏禄群岛上的三位国王—东王巴都葛叭哈喇，西王麻哈喇葛麻丁、峒王巴都葛叭喇卜，率领家眷一行340人组成友好使团，前来中国进行友好访问，受到明永乐皇帝朱棣的隆重接待。三王在北京愉快地逗留了22天，乘船沿京杭大运河南下回国，东王在取道山东归国徒中，不幸遭疾，于1417年9月13日病故于德州以北安陵镇驿馆。 永乐皇帝闻讣，深为哀悼，遂派礼部郎中带祭文赶赴德州，以王礼厚葬。东王长子督马含率众回国继承王位，王妃葛木宁及次子温哈喇、三子安都鲁和侍从十余人留居德州守墓。永乐十六年秋，永乐帝又亲撰文，命勒石庙廷，“以垂永久”。东王妃及二位王子逝世后，分别安葬在墓院东南隅。', 'Home/Images/travel/景点/德州/苏禄王墓/苏禄王墓.jpg', 0, '2015-09-18 02:48:32', NULL),
(35, '文昌阁', '德州', '文昌阁位于平原恩城镇，建于1480年，康熙年间曾进行过两次修葺。10米见方的高台，周围用青砖扁砌，高3.5米，80厘米高的十字花砖垒花墙为正方形，座北朝南，顶为木架结构的“阁”建于高台之上，青瓦翘角，尖顶之上竖着一个亚腰葫芦形的白色塔尖，阁内奉有“文昌帝君”，西北角有朝北角门，砖筑门楼入内便是“马道式”的坡梯，踏步而上，既可凭栏赏景，又可拜谒“文昌”。', 'Home/Images/travel/景点/德州/文昌阁/文昌阁.jpg', 0, '2015-09-18 02:48:32', NULL),
(36, '希深欢乐岛游乐园', '德州', '德州希森欢乐岛游乐园，建于2002年7月，地处风景秀丽的德州新湖风景区西侧，与德州新湖风景区、德州中心广场交相辉映，溶为一体，是鲁北地区最大的旅游景区。\r\n德州游乐园占地70余亩，三面环湖，园内繁花似锦，绿草如茵，湖面波光遴遴，风景如画。游乐园建有十二项大型现代化娱乐设备，有鲁北“迪斯尼乐园”之称。 德州游乐园2004年引进了鲁北地区第一套大型滑行类游艺设施，号称“刺激游戏之王”的“过山车”。此次引进的过山车轨道全长490米，时速可达75公里，落差达23米。“心脏停跳，天旋地转，骨骼喀嚓喀嚓地扭动，更高，更快，更像失控！”，“抓紧，否则就没命了！”这就是过山车带给你的体验。过山车倍受欢迎，自开放以来每天都有游人排队乘坐这种令人情不自禁发出尖叫的机器，等待体验那灵魂出壳的惊悸感。乘坐过山车的感觉就像普通人终于驾驶了喷气式战斗机。当过山车在轨道上达到每小时75公里的速度时，人们可以真实地体验到战斗机驾驶员的感觉。“坐过过山车，你才算没白活”。让我们做好准备进入过山车创造的“疯狂世界”吧！', 'Home/Images/travel/景点/德州/希深欢乐岛游乐园/希深欢乐岛游乐园.jpg', 0, '2015-09-18 02:48:32', NULL),
(37, '禹王亭博物馆', '德州', '禹王亭博物馆位于禹城市区西北3公里处，占地136.5亩。该公园由一个座北朝南的大院落，具丘遗迹、禹王亭和人工湖组成，结构新颖、紧凑、气势恢弘，古朴典雅。大院落里有禹王大殿、东西配殿、钟鼓楼等，大院落南首是一牌坊式山门，门上方为清朝乾隆皇帝题写的“禹王亭”手迹。 拾阶进入山门，有宽6米，两旁栽种着松柏的南北甬道，甬道南端东侧矗立着一石碑，上面刻着重修禹王亭记碑文；甬道南端西侧，立有两块石碑，刻记着重修禹王公园捐款单位名称和捐款个人姓名。院落北部东西两侧，各建有11间古式配殿；甬道北端即是一座明清建筑模式的禹王大殿。大殿占地300余平方米，西阔为5开间、重檐、歇山、九脊顶，总高度为16.7米，长25.4米，进深15米。 大殿正门上方、两檐之间设“禹王殿”鎏金匾额，门两侧立柱有楹联一幅，琉璃瓦顶和玺彩画，金壁辉煌。殿内正中塑禹王坐像一尊，高约6米，背后塑玉女手持风扇侍立两旁。大院落北门外系“具丘山”，南北长45米，东西宽50米，高8米。据旧志记载：“大禹治水时曾筑此丘，以观察水势”。明、清时于土丘上建禹王亭。', 'Home/Images/travel/景点/德州/禹王亭博物馆/禹王亭博物馆.jpg', 0, '2015-09-18 02:48:32', NULL);

